// Constants
const float pi = 3.1415;
const float BPM = 60.0;

const float bd = BPM / 60.;
const float qd = bd / 4.;





// Timing
float beat(float t){
    return mod(t, 60. / BPM);
}

float beat_id(float t){
    return mod(floor(BPM*t / 60.), 4.);
}

float cBeat(float t){
   return floor(BPM*t / 60.);
}


float quarter(float t){
    return mod(BPM*t / 60., .25);
}

float quarter_id(float t){
    return mod(floor(4. * BPM*t / 60.), 4.);
}

float cQuarter(float t){
   return floor(4.*BPM*t / 60.);
}

float bar(float t){
    return mod(t, 2. * (60. / BPM));
}

float cBar(float t){
    return floor(8.*BPM*t / 60.);
}


// Note stuff
const float baseFreq = 110.0; // A1
const float d12thRootOf2 = pow(2.0, 1.0 / 12.0);


// Get the note but also change pitch of everything sometimes for variety 
float note(float k, float t){

    float h = 2., wha = 0.;

    if(mod(cBeat(.5*t+bd), 2.) > 0. && cBeat(t) >= 32.){
        h = 4.;

        wha = mod(floor(.5*BPM*(t-bd*2.) / 60.), h);
    }
    else{
        wha = mod(floor(.5*BPM*(t-bd*2.) / 60.), h);
    }
    k += wha*3.0;

    return baseFreq * pow(d12thRootOf2, k);
}



// Waveforms
float sine(float t, float hz, float k){
    return sin(hz*t*pi*2. + k);
}

float tri(float t, float hz, float k){
    return (2. / pi) * asin(sin(2.*pi*t*hz + k));
}

float sqr(float t, float hz, float k){
    return sign(sin(2.*pi*t*hz + k)) * 0.5;
}


// noise
float nse(float t){
    return fract(120.0*sin(t*1000.0));
}

vec2 hash22(vec2 p){
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}
vec2 hash21(float p)
{
	vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
	p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}


float tick(float t){
    return nse(t*999.) * exp(-t*40.);
}

// Metronome sound
float blip(float t, float hz, float k){
    return (1. - exp(-t*40.)) * sine(t, hz, 0.0) * exp(-t * k);
}



// Utility functions
float dist1(float s, float d){
 	return clamp(d * s, -1., 1.);   
}
vec2 dist2(vec2 s, float d){
 	return clamp(d * s, vec2(-1.0), vec2(1.0));   
}

