// My attempt at some drum sounds
vec2 snare(float t){
    vec2 s = exp(-t*23.) * hash22(iSampleRate * vec2(t, t*1.423)) * 2.0 - 1.0; 
    s = dist2(s, 1.3) * .85;
    s += .5*dist1(exp(-t*28.) * sine(t, 200., 0.), 4.2);
    return s * 0.5;
}

vec2 kick(float t){
    vec2 s = vec2(0);
    s += 1.1*dist2(exp(-t*25.) * vec2(sine(t, 75., 0.)), 2.1);
    s += .6 * exp(-t*15.) * vec2(sine(t, 70., 0.));
    return s;
}

vec2 hat(float t){
    vec2 s = sqrt(t*7.) * exp(-t*38.) * hash22(iSampleRate * vec2(t, t*1.423)) * 2.0 - 1.0; 
    s = dist2(s, 1.1) * .85;
    return s * 0.7;
}

// Add sound on a quarter beat
vec2 onQ(float t, float n, vec2 s){
    vec2 ms = vec2(0);

    if(quarter_id(t) == n){
        ms += s;
    }
    return ms;
}


// Make song
vec2 snd(float t){
    vec2 ms = vec2(0);

    ms += .6*onQ(t, 0., vec2(kick(beat(t))));
    ms += .6*onQ(t, 2., vec2(snare(beat(t - qd*2.))));

    if(beat_id(t*2.) > 1.){
        ms += .9 * onQ(t, 1., vec2(kick(beat(t - qd))));
    }

    if(beat_id(t) > 1. && cQuarter(t) >= 16.){
        ms += .37*onQ(t, 2., .22*vec2(sqr(beat(t - 2.*qd), note(12., t), 0.),
        sqr(beat(t - 2.*qd), note(12., t) + 4., 0.)));

        ms += .2*onQ(t, 2., .22*vec2(tri(beat(t - 2.*qd), note(0., t), 0.),
        tri(beat(t - 2.*qd), note(0., t) + 4., 0.)));
    }

    if(cQuarter(t) >= 8.){
        ms += onQ(t, 0., .4*vec2(tri(beat(t), note(-12., t), 0.)));
        ms += onQ(t, 0., .13*vec2(sine(beat(t), note(0., t), 0.)));

        if(beat_id(t*2.) > 1.)
            ms += 2.6*onQ(t, 1., .1*vec2(tri(beat(t + bd), note(10., t), 0.)));

        //if(beat_id((t+bd*2.)) > 2.)
            //ms += 2.2*onQ(t, 3., .1*vec2(tri(beat(t - qd*2.), note(12., t), 0.)));

        ms += onQ(t, 2., .3*vec2(tri(beat(t - qd*2.), note(0., t), 0.)));

        if(beat_id(t*2.) > 1.){
            ms += .36*onQ(t, 3., vec2(tri(beat(t - 3.*qd), note(-2., t), 0.),
            tri(beat(t - 3.*qd), note(-2., t) + 2., 0.)));
            ms += 1.1*onQ(t, 3., vec2(blip(beat(t - qd*3.), 400., 16.)));
        }

        ms += .43*onQ(t, 1., vec2(hat(beat(t - qd))));
        ms += .43*onQ(t, 3., vec2(hat(beat(t - qd*3.))));

        ms += 1.3*onQ(t, 3., vec2(blip(beat(t - qd*3.), 200., 16.)));
    }
    return ms;

}

vec2 mainSound( int samp, float time ){
    vec2 ms = snd(time);

    //return clamp(ms * 1.2, -0.95, 0.95) * .6;
    return ms * .7;
}

