vec3 RSph (float s)
{
  vec3 b;
  b = vec3 (nSphObjD);
  return blkGap * (floor (vec3 (mod (s, b.x), mod (s, b.x * b.y) / b.x,
      s / (b.x * b.y))) - 0.5 * (b - 1.));
