#define SCREEN_D -700.0
#define RADIUS 500.0
//#define NO_SHADOWS
//#define MSAA
//#define FAKE_NORM
#define EXPOSURE 1.0

#define MOVEMENT_SPEED 1000.0

#define LIGHT vec3(5000.0+sin(iTime*.5)*100.0,000.0+cos(iTime*.4)*100.0,1000.0-iTime*MOVEMENT_SPEED-18000.0+sin(iTime*.5)*10000.0)
//#define LIGHT vec3(0,000,-1000.0-iTime*MOVEMENT_SPEED)

float hash(float n) {
    return fract(n*fract(n*.133929));
}

vec2 rotate(vec2 p, float a) {    
    return vec2 (cos(a)*p.x - sin(a)*p.y, sin(a)*p.x + cos(a)*p.y);
}

vec2 irotate(vec2 p, float a) {
    return rotate(p, -a);
}

vec3 iproject(vec3 p)
{
    p.x -= iResolution.x/2.0;
    p.y -= iResolution.y/2.0;

    p.z -= 2000.0;

    p.x /= iResolution.x/1200.0;
    p.y /= iResolution.y/675.0;

    p.x *= (SCREEN_D+p.z);
    p.y *= (SCREEN_D+p.z);

    p.x /= (SCREEN_D);
    p.y /= (SCREEN_D);

    p.z += 2000.0;

    //p.z += 1000.0;
    //p.xy = irotate(p.xy, iTime);
    p.z += SCREEN_D;
    p.yz = irotate(vec2(p.y,p.z), 2.0*6.28*sin((-iMouse.y)/iResolution.y));
    p.xz = irotate(vec2(p.x,p.z), 2.0*6.28*sin((-iMouse.x)/iResolution.x));
    p.z -= SCREEN_D;
    //p.z -= 1000.0;
    p.z -= iTime*MOVEMENT_SPEED;
    return p; 
}

float effect_sin(float a, float cellnum) {
    return sin(a*.001+iTime*(hash(cellnum)*2.0-1.0))*200.0;
}

float effect_cos(float a, float cellnum) {
    return cos(a*.001+iTime*(hash(cellnum)*2.0-1.0))*200.0;
}

//lumpy tube crawler things
float effect_thickness(float a,float cellnum) {
    float b = sin(iTime*(hash(cellnum)-.5)*1.0+a*.0002);
    b = b*10.0-9.0;
    b = max(b,0.0);
    b = b*b*b*b;
    b = b*2.0 - 1.0;
    return b*120.0;
}

//twisty funhouse effect
vec3 effect_funhouse(vec3 p) {
    p.x += sin(p.z*.0005 + iTime*1.0)*300.0;
    p.y += cos(p.z*.0005 + iTime*1.0)*300.0;
    return p;
}

//bulge effect around our light source (sort of unnoticeable at the moment)
vec3 effect_bulge(vec3 p) {
    float d = 10000.0-distance(p,LIGHT);
    d*=.00001;
    d = clamp(d,0.0,1.0);
    d = smoothstep(0.0,1.0,d);
    p.xy -= p.xy*d*.2;
    return p;
}

//smooth union
float sdf_union(float a, float b, float amount) {
    float ab = clamp(amount - abs(a-b), 0.0, amount)/amount;
    ab*=ab;
    ab*=ab;
    return min(a,b)-ab*(amount*.125);
}

float scene(vec3 p, vec3 p_orig) {


    p = effect_bulge(p);  
    p = effect_funhouse(p);

    //find distance
    vec3 cellnum = floor(p/4000.0);

    //vertical tubes
    vec3 pa = vec3(mod(p.x,4000.0),p.y,mod(p.z,-4000.0)); //this is our relative position inside a cell                                      
    vec3 aa = vec3(2000.0+effect_sin(p.y,cellnum.z+cellnum.x*10.0),p.y,-2000.0+effect_cos(p.y,cellnum.z+cellnum.x*10.0)); //this is the target position
    float a = distance(pa,aa); //distance between us and the target
    a += -RADIUS-effect_thickness(p.y,cellnum.z+cellnum.x*.8371); //modify thickness/radius

    //horizontal
    vec3 pb = vec3(p.x,mod(p.y,4000.0),mod(p.z,-4000.0));
    vec3 bb = vec3(p.x,2000.0+effect_sin(p.x,cellnum.y+cellnum.z*10.0),-2000.0+effect_cos(p.x,cellnum.y+cellnum.z*10.0)); 
    float b = distance(pb,bb);
    b += -RADIUS-effect_thickness(p.x,cellnum.z*3.5931+cellnum.y);

    //z-axis
    vec3 pc = vec3(mod(p.x,4000.0),mod(p.y,-4000.0),p.z);
    vec3 cc = vec3(2000.0+effect_sin(p.z,cellnum.x+cellnum.y*10.0),-2000.0+effect_cos(p.z,cellnum.x+cellnum.y*10.0),p.z);
    float c = distance(pc,cc);
    c += -RADIUS-effect_thickness(p.z,cellnum.y+cellnum.x*1.037);

    //smooth union
    const float fillet = 1200.0;
    float ab = sdf_union(a,b,fillet);
    float abc = sdf_union(ab,c,fillet);
    return abc;
    //return min(min(a,b),c);

}

//march them rays
float march(inout vec3 p, vec3 p_orig, vec3 ps) {
    float dist = 1000.0;
    for (int i = 0; i < 80; i++) {
        dist = scene(p,p_orig);
        if(dist<5.1)break;
        p+=ps*max(dist*.8,5.1);
    }
    return dist;
}

//march back towards our light source
float march_light(inout vec3 p, vec3 p_orig, vec3 ps, vec3 light, inout float min_dist) {
    float dist = 1000.0;
    float dist2 = 1000.0;
    min_dist = 1000.0;
    for (int i = 0; i < 50; i++) {
        dist = scene(p,p_orig);
        min_dist = min(min_dist, dist);
        dist2 = length(p-light);
        p+=ps*min(dist*.8, dist2);
        if(dist < 15.1 || dist2 < 45.1)break;
    }
    return dist2;
}


//this function is one of my least favorite things
//its purpose is to find:
//our relative position between tubes (intersection to intersection) (0.0 - 1.0) (0.0 = at one intersection; 1.0 = at the other intersection)
vec3 distance_from_intersect(vec3 p) {

    p = effect_bulge(p);
    p = effect_funhouse(p);

    //horizontal UV (based on the location between vertical and z-axis tubes)
    vec3 pqa = p;

    pqa.x+=2000.0;
    vec3 cellnuma = floor(pqa/4000.0);

    float pa = mod(pqa.x,4000.0);                                       
    float aa = effect_sin(p.y,cellnuma.z+(cellnuma.x-1.0)*10.0);
    float aah = effect_sin(p.z,cellnuma.x-1.0+cellnuma.y*10.0);
    float a = abs(aa-pa);
    float ah = abs(aah-pa);
    a += -RADIUS-effect_thickness(p.y,cellnuma.z+(cellnuma.x-1.0)*.8371);
    ah += -RADIUS-effect_thickness(p.z,cellnuma.y+(cellnuma.x-1.0)*1.037);

    float aa2 = effect_sin(p.y,cellnuma.z+cellnuma.x*10.0)+4000.0;
    float aah2 = effect_sin(p.z,cellnuma.x+cellnuma.y*10.0)+4000.0;
    float a2 = abs(aa2-pa);
    float ah2 = abs(aah2-pa);
    a2 += -RADIUS-effect_thickness(p.y,cellnuma.z+cellnuma.x*.8371);
    ah2 += -RADIUS-effect_thickness(p.z,cellnuma.y+cellnuma.x*1.037);
    a = sdf_union(a,ah,1200.0);
    a2 = sdf_union(a2,ah2,1200.0);


    //vertical UV (based on the location between horizontal and z-axis tubes)
    vec3 pqb = p;

    pqb.y+=2000.0;
    //pqb.x+=2000.0;
    //pqb.z-=2000.0;
    vec3 cellnumb = floor(pqb/4000.0);

    float pb = mod(pqb.y,4000.0);
    float bb = effect_sin(p.x,cellnumb.y-1.0+cellnumb.z*10.0); 
    float bbh = effect_cos(p.z,cellnumb.x+(cellnumb.y-1.0)*10.0);
    float b = abs(bb-pb);
    float bh = abs(bbh-pb);
    b += -RADIUS-effect_thickness(p.x,cellnumb.z*3.5931+cellnumb.y-1.0);
    bh += -RADIUS-effect_thickness(p.z,cellnumb.y-1.0+cellnumb.x*1.037);


    float bb2 = effect_sin(p.x,cellnumb.y+cellnumb.z*10.0)+4000.0; 
    float bbh2 = effect_cos(p.z,cellnumb.x+cellnumb.y*10.0)+4000.0;
    float b2 = abs(bb2-pb);
    float bh2 = abs(bbh2-pb);
    b2 += -RADIUS-effect_thickness(p.x,cellnumb.z*3.5931+cellnumb.y);
    bh2 += -RADIUS-effect_thickness(p.z,cellnumb.y+cellnumb.x*1.037);
    b = sdf_union(b,bh,1200.0);
    b2 = sdf_union(b2,bh2,1200.0);


    //z-axis UV (based on the location between horizontal and vertical tubes)
    vec3 pqc = p;

    pqc.z-=2000.0;
    vec3 cellnumc = floor(pqc/4000.0);

    //z-axis
    float pc = mod(pqc.z,4000.0);
    float cc = effect_cos(p.x,cellnumc.y+cellnumc.z*10.0);
    float cch = effect_cos(p.y,cellnumc.z+cellnumc.x*10.0);
    float c = abs(cc-pc);
    float ch = abs(cch-pc);
    c += -RADIUS-effect_thickness(p.x,cellnumc.z*3.5931+cellnumc.y);
    ch += -RADIUS-effect_thickness(p.y,cellnumc.z+cellnumc.x*.8371);

    float cc2 = effect_cos(p.x,cellnumc.y+(cellnumc.z-1.0)*10.0)+4000.0;
    float cch2 = effect_cos(p.y,cellnumc.z+1.0+cellnumc.x*10.0)+4000.0;
    float c2 = abs(cc2-pc);
    float ch2 = abs(cch2-pc);
    c2 += -RADIUS-effect_thickness(p.x,(cellnumc.z-1.0)*3.5931+cellnumc.y);
    ch2 += -RADIUS-effect_thickness(p.y,cellnumc.z+1.0+cellnumc.x*.8371);
    c = sdf_union(c,ch,1200.0);
    c2 = sdf_union(c2,ch2,1200.0);

    return vec3(a/(a+a2),b/(b+b2),c/(c+c2));
}

vec3 cast_ray(vec2 coord) {

    vec3 p = iproject(vec3(coord, 1300));
    vec3 p_orig = p;
    vec3 ps = normalize(iproject(vec3(coord, -1000))-p);

    float dist = march(p,p_orig,ps);

    vec3 light = LIGHT;
    vec3 ps2 = normalize(light-p);
    vec3 p2 = p+ps2*100.0;

    vec3 out_color = vec3(0,0,0);

    if(dist<5.1) {

        //march a few more rays to derive surface normal
        vec3 pp;
        pp.x = scene(vec3(p.x+10.0,p.y,p.z),p_orig);
        pp.y = scene(vec3(p.x,p.y+10.0,p.z),p_orig);
        pp.z = scene(vec3(p.x,p.y,p.z+10.0),p_orig);
        vec3 norm = normalize(vec3(pp-dist));

        //the rotation angle around each axis
        float rx = atan(norm.y,norm.z) * 1.15;
        float ry = atan(norm.x,norm.z) * 1.15;
        float rz = atan(norm.x,norm.y) * 1.15;

        //our relative position between tubes
        vec3 rp = distance_from_intersect(p);
        vec3 rpp = rp*4.0;

        //convert relative position (0.0 - 1.0 - 0.0) (0.0 = at an intersection; 1.0 = smack between intersections)
        rp = abs(.5-rp)*2.0;

        vec3 col=vec3(0,0,0);
        vec2 uv;
        float thick;
        vec3 cellnum = floor(p/4000.0);

        //apply our texture based on our relative position
        if(rp.x < rp.y && rp.x < rp.z) {
            //horizontal texture
            uv = vec2(rpp.x , rx*1.0);
            //thickness light amount (translucent effect)
            thick = effect_thickness(p.x,cellnum.z*3.5931+cellnum.y);
            //add light from intersecting tubes
            thick += (1.0-(rp.y-rp.x))*max(effect_thickness(p.y,cellnum.z+cellnum.x*.8371),0.0)*2.0;
            thick += (1.0-(rp.z-rp.x))*max(effect_thickness(p.z,cellnum.y+cellnum.x*1.037),0.0)*2.0;
        } else if (rp.y < rp.z) {
            //vertical texture
            uv = vec2(rpp.y , ry*1.0);
            thick = effect_thickness(p.y,cellnum.z+cellnum.x*.8371);
            thick += (1.0-(rp.x-rp.y))*max(effect_thickness(p.x,cellnum.z*3.5931+cellnum.y),0.0)*2.0;
            thick += (1.0-(rp.z-rp.y))*max(effect_thickness(p.z,cellnum.y+cellnum.x*1.037),0.0)*2.0;
        } else  {
            //z-axis texture
            uv = vec2(rpp.z , rz*1.0);
            thick = effect_thickness(p.z,cellnum.y+cellnum.x*1.037);
            thick += (1.0-(rp.x-rp.z))*max(effect_thickness(p.x,cellnum.z*3.5931+cellnum.y),0.0)*2.0;
            thick += (1.0-(rp.y-rp.z))*max(effect_thickness(p.y,cellnum.z+cellnum.x*.8371),0.0)*2.0;
        }

        uv*=.5;
        vec3 tex = texture(iChannel0, uv, -1.0).xyz;

        //normalize our thickness lighting
        thick+=120.0;
        thick /= 240.0;
        thick = (thick < 1.0) ? thick * thick : thick;

        //find out how close we are to an intersection to "share" the thickness lighting
        float diff = abs(tex.r-tex.g) + abs(tex.r-tex.b) + abs(tex.g-tex.b);
        diff *= 4.0;

        if(diff > 1.0) col += tex*thick*0.5; //emissive/translucent glow


        vec3 l = normalize(light-p);

        #ifdef FAKE_NORM
        //fake texture normal
        norm.x -= (texture(iChannel0,uv).g - texture(iChannel0,vec2(uv.x,uv.y+.0003)).g)*80.0;
        norm.y -= (texture(iChannel0,uv).g - texture(iChannel0,vec2(uv.x+.001,uv.y)).g)*80.0;
        #endif

        float bri = clamp(dot(norm,l),0.0,1.0); //directional lighting


        float lbri = 100000.0-length(light-p); //distance to light
        lbri /= 100000.0;
        lbri = clamp(lbri,0.0,1.0);
        lbri *= lbri;

        bri += lbri*.5; //exceedingly basic global illumination
        bri *= lbri;

        bri += .3; //ambient light

        float dist_;
        #ifndef NO_SHADOWS
        float dist2 = march_light(p2,p_orig,ps2,light,dist_); //ray march back to light source
        if(dist2 > 45.1) {
            bri*=.7; //shadow hit//soft shadows
        } else {
            dist_ = clamp(dist_, 0.0, 65.0);
            dist_ /= 65.0;
            dist_ = 1.0 - dist_;
            dist_ = smoothstep(0.0,1.0,dist_);
            bri -= dist_*bri*.3;
        }
        #endif


        out_color = col+(bri*tex);



    }

    return out_color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = fragCoord-.5;
    vec3 col = cast_ray(coord);


    #ifdef MSAA
    col += cast_ray(vec2(coord.x+.5,coord.y));
    col += cast_ray(vec2(coord.x,coord.y+.5));
    col /= 3.0;
    #endif

    fragColor = vec4(col,1.0);
    fragColor *= EXPOSURE;
    fragColor.r = pow(fragColor.r,2.2);
    fragColor.g = pow(fragColor.g,2.2);
    fragColor.b = pow(fragColor.b,2.2);

