/*
    Cell(42bits -> 0x3ffffffffff) : {
        position_x (14bits -> 0x3fff),
        position_y (13bits -> 0x1fff),
        direction  ( 9bits -> 0x01ff),
        velocity   ( 6bits -> 0x003f)
    }
*/

void vec4tocell3(vec4 data, out vec4 cell0, out vec4 cell1, out vec4 cell2)
{
    uint r = floatBitsToUint(data.r);
    uint g = floatBitsToUint(data.g);
    uint b = floatBitsToUint(data.b);
    uint a = floatBitsToUint(data.a);

    float cell0_x = float(a & 0x3fffu) / float(0x3fff);
    a >>= 14;
    float cell0_y = float(a & 0x1fffu) / float(0x1fff);
    a >>= 13;
    float cell0_d = TAU * float((a & 0x001fu) + ((b & 0x000fu) << 5)) / float(0x01ff);
    b >>= 4;
    float cell0_v = float(b & 0x003fu) / float(0x003f);
    b >>= 6;
    float cell1_x = float(b & 0x3fffu) / float(0x3fff);
    b >>= 14;
    float cell1_y = float((b & 0x0fffu) + ((g & 0x0001u) << 12)) / float(0x1fff);
    g >>= 1;
    float cell1_d = TAU * float(b & 0x01ffu) / float(0x01ff);
    g >>= 9;
    float cell1_v = float(g & 0x003fu) / float(0x003f);
    g >>= 6;
    float cell2_x = float(g & 0x3fffu) / float(0x3fff);
    g >>= 14;
    float cell2_y = float((g & 0x0003u) + ((r & 0x007ffu) << 2)) / float(0x1fff);
    r >>= 11;
    float cell2_d = TAU * float(r & 0x01ffu) / float(0x01ff);
    r >>= 9;
    float cell2_v = float(r & 0x003fu) / float(0x003f);

    cell0 = vec4(cell0_x, cell0_y, cell0_d, cell0_v);
    cell1 = vec4(cell1_x, cell1_y, cell1_d, cell1_v);
    cell2 = vec4(cell2_x, cell2_y, cell2_d, cell2_v);
}


