        else if (qqq < 0.8)
        {
            hash_r = 16384u;
            hash_g = 32768u+16384u;
        }
        else
        {
            hash_r = 32768u+16384u;
            hash_g = 32768u+16384u;
        }
        uint hash_b = hash(10u * size + index + uint(iDate.w)) & 0xffffu;
        uint hash_a = hash(3u * size + index + uint(iDate.w)) & 0xffffu;
        //fragColor = vec4(uintBitsToFloat(hash_r), uintBitsToFloat(hash_g), uintBitsToFloat(hash_b), uintBitsToFloat(hash_a));
        fragColor = vec4(float(hash_r) / 65536. * iResolution.x, float(hash_g) / 65536. * iResolution.y, TAU * float(hash_b) / 65536., 0.2 + 0.8 * rand(iTime + float(index * 10000u)));
        //fragColor = vec4(fragCoord.xy, TAU * float(hash_b) / 65536., float(hash_a) / 65536. / 100.);
        //fragColor = vec4(fragCoord.xyxy);
        return;
    }    
    //vec4 data = texture(iChannel0, uv);
    vec4 data = texelFetch(iChannel0, ivec2(fragCoord), 0);

    float x = data.r / iResolution.y; // 0->256
    float y = data.g / iResolution.y; // 0->256
    float d = data.b; // 0->TAU
    float a = data.a; // 0->1
    float v = 0.00025 + a * 0.0005 + RESET;
    float lookup_distance = mod(iDate.w, 70.) < 50. ? 300. : 10.;
    lookup_distance = mod(iDate.w, 60.) < 20. ? 100. : lookup_distance;
    float vv = v * lookup_distance * (sin(iTime / 5.)*.5+.6);


    vec2 aaa = vec2(cos(d) * vv, sin(d) * vv);
    vec2 b = vec2(cos(d + PI / 6.) * vv, sin(d + PI / 6.) * vv);
    vec2 c = vec2(cos(d - PI / 6.) * vv, sin(d - PI / 6.) * vv);

    vec3 aa = vec3(0.);
    vec3 bb = vec3(0.);
    vec3 cc = vec3(0.);
    for (float i = 2.; i < 5.; i++)
    {
        ivec2 tmp = ivec2(mod((vec2(x, y) + aaa * i), vec2(ASPECT_RATIO, 1.)) * iResolution.yy);
        aa += texelFetch(iChannel1, tmp, 0).rgb;
        tmp = ivec2(mod((vec2(x, y) + b * i), vec2(ASPECT_RATIO, 1.)) * iResolution.yy);
        bb += texelFetch(iChannel1, tmp, 0).rgb;
        tmp = ivec2(mod((vec2(x, y) + c * i), vec2(ASPECT_RATIO, 1.)) * iResolution.yy);
        cc += texelFetch(iChannel1, tmp, 0).rgb;

    }

    float fact = (0.8*(sin(iTime / 3.)*.5+.5)+.2);
    float turn_speed = 0.0675 * (0.7 + 0.3 * (1.-fact));
    if (fragCoord.x < iResolution.x / 3.)
    {
        if (bb.r > cc.r)
        {
            d += PI / 1. * turn_speed;
        }
        if (cc.r > bb.r)
        {
            d -= PI / 1. * turn_speed;
        }
        d += (rand(iTime+float(index)/10.)) * 0.03;
    }
    else if (fragCoord.x < 2. * iResolution.x / 3.)
    {
        if (bb.g > cc.g)
        {
            d += PI / 1. * turn_speed;
        }
        if (cc.g > bb.g)
        {
            d -= PI / 1. * turn_speed;
        }
    }
    else
    {
        if (bb.b > cc.b)
        {
            d += PI / 1. * turn_speed;
        }
        if (cc.b > bb.b)
        {
            d -= PI / 1. * turn_speed;
        }

        d -= (rand(iTime+float(index)/10.)) * 0.03;
    }

    float fa = ((sin(iTime / 11.) + 1.));
    float fb = ((1. - sin(iTime / 11.)));

    if (abs(fa) < 0.7 || abs(fb) < 0.7)
    {
        fa = 1.;
        fb = 1.;
    }
    else
    {
        fa *= 1./0.7;
        fa -= sign(fa) * 1.;
        fb *= 1./0.7;
        fb -= sign(fa) * 1.;
    }
    fa -= 1.;
    fb -= 1.;
    d += PI * turn_speed * bb.g * 2. * fa;
    d -= PI * turn_speed * cc.g * 2. * fb;


    //d = bb;
    d += fact * 0.2 * (rand(iTime + floor(float(index) / 30000.))-0.5);
    //d += 0.05 * (smoothstep(0.95, 1., 2.*(sin(iTime * 10.)*.5+.5)*rand(float(index * 100u))) - 0.5);
    fact -= 0.2;
    x += cos(d) * (v + v * fact * 1.8);// * (aa.b + 0.5);
    y += sin(d) * (v + v * fact * 1.8);// * (aa.b + 0.5);
    //a += (rand(iTime + float(index * 10000u))-0.5) * 0.01;
    x = mod(x, ASPECT_RATIO);
    y = mod(y, 1.);
    //x+=0.4;

    fragColor = vec4(vec2(x, y) * iResolution.y, mod(d, TAU), a);
    //fragColor = vec4(data.xy, d, v);
    //fragColor = data;
}

