const int MAX_ITERATIONS = 200;

int julia(vec2 zz, vec2 c) 
{
  vec2 z = zz;
  float a = z.x;
  float b = z.y;
  int n = 0;
  while (n < MAX_ITERATIONS) {
    float aa = a*a;
    float bb = b*b;
    float twoab = 2.0 * a * b;

    a = aa - bb + c.x;
    b = twoab + c.y;

    if (a*a*a*b + b*b*b*a > 3.0) break;
    n++;
  }
  return n;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{
  int juliaValue = julia((fragCoord.xy/iResolution.xy*4.0 - vec2(2, 2)) * vec2(iResolution.x / iResolution.y, 1), 
                          vec2(cos(iTime), sin(iTime/1.2)));

  float color = float(juliaValue) / float(MAX_ITERATIONS) * 7.0 + 0.1;
  fragColor = vec4(color*0.2, color-0.3, color, 1.0);
}

#define pmod(p,a) mod(p,a) - 0.5*a
#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define pi acos(-1.)
#define iTime (iTime + 24.)
// from inigo quilez
vec3 triIntersect( in vec3 ro, in vec3 rd, in vec3 v0, in vec3 v1, in vec3 v2 )
{
    vec3 v1v0 = v1 - v0;
    vec3 v2v0 = v2 - v0;
    vec3 rov0 = ro - v0;
    vec3  n = cross( v1v0, v2v0 );
    vec3  q = cross( rov0, rd );
    float d = 1.0/dot( rd, n );
    float u = d*dot( -q, v2v0 );
    float v = d*dot(  q, v1v0 );
    float t = d*dot( -n, rov0 );
    if( u<0.0 || u>1.0 || v<0.0 || (u+v)>1.0 ) t = -1.0;
    return vec3( t, u, v );
}

