        float dif = clamp(dot(n,l), 0.0, 1.0);
        float sky_dif = clamp(0.5+0.5*dot(n,vec3(0.0, 1.0, 0.0)), 0.0, 1.0);
        float bounce_dif = clamp(0.2+0.2*dot(n,vec3(0.0, -1.0, 0.0)), 0.0, 1.0);
        float shdw = calcSoftShdw(p+n*0.05, l, 16.0); //step(rayMarch((p+n*SURF_DIST), l).x, 0.0);//hard shadows



        //specular light
        vec3 h = normalize(l + normalize((ro-p)));
        float spec = pow(max(0.0, dot(n,h)), 128.0);


        vec3 light_in = vec3(0.0);
        light_in += vec3(7.0, 5.8, 3.6)*dif*shdw;
        light_in += vec3(0.5, 0.8, 0.6)*sky_dif;
        light_in += vec3(0.6, 0.3, 0.1)*bounce_dif;

        col = mat*light_in;

        col += mat*spec*shdw;
    }
    return (col);

}

