{
    // Camera/Ray origin
    vec3 ro = vec3(-cos(0.2*iTime), 2.7, -1.2*sin(0.2*iTime));

    vec3 avrg_col = vec3(0.0);
#if AA>1 //Supersampling AA
    for(int i = 0; i < AA; i++)
    for(int j = 0; j < AA; j++){
        //Pixel coordinates
        vec2 of = vec2(float(i),float(j)) / float(AA) - 0.5;
        vec2 uv = ((fragCoord+of)- .5*iResolution.xy)/iResolution.y;
#else

        vec2 uv = (fragCoord- .5*iResolution.xy)/iResolution.y;
#endif

        //Ray direction
        vec3 rd = normalize(vec3(uv.x, uv.y, 1));

        //Render scene
        vec3 col = render(ro,rd);



        avrg_col += col;

#if AA>1      
    }
    avrg_col /= float(AA*AA);//Apply AA
#endif
    //gamma
    avrg_col = pow(avrg_col, vec3(0.4545));

    fragColor = vec4(avrg_col,1.0);
}

