void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y * 4.0;
    float unit = 8.0 / iResolution.y;
    vec3 color = vec3(0.0);

    vec2 a = vec2(anim( 1.0) * 3.0, anim( 2.0));
    vec2 b = vec2(anim( 3.0) * 3.0, anim( 4.0));
    vec2 c = vec2(anim( 5.0) * 3.0, anim( 6.0));
    vec2 d = vec2(anim( 7.0) * 3.0, anim( 8.0));
    vec2 e = vec2(anim( 9.0) * 3.0, anim(10.0));
    vec2 f = vec2(anim(11.0) * 3.0, anim(12.0));
    vec2 g = vec2(anim(13.0) * 3.0, anim(14.0));
    vec2 h = vec2(anim(15.0) * 3.0, anim(16.0));
    vec2 i = vec2(anim(17.0) * 3.0, anim(18.0));
    vec2 j = vec2(anim(19.0) * 3.0, anim(20.0));
    vec2 k = vec2(anim(21.0) * 3.0, anim(22.0));

    bool dash = true;
    vec2 prevPos = a;
    for (float t=0.01; t < 1.01; t += 0.01) {
        vec2 curPos = decicBezier(a, b, c, d, e, f, g, h, i, j, k, t);
        if (dash) color = mix(color, hue2rgb(t + iTime), smoothstep(unit, 0.0, sdLine(uv, prevPos, curPos)));
        prevPos = curPos;
        dash = !dash;
    }

    fragColor = vec4(color, 1.0);
}

#define PI 3.1415926;

float plot(vec2 uv, float f, float offset, bool flag){
   f *= 2.0;
   float y = mix(0.0, 1.0, uv.y);
   return smoothstep(flag ? 0.02 : -0.02, 0.0, y - 0.1*sin(iTime+f) - offset);
}


vec3 getcolor(vec2 uv, float y, float offset, vec3 c1, vec3 c2, bool flag){
    y *= PI;
    float f = plot(uv, y, offset, flag);

    return mix(c1, c2, f);
}

