    vec2 uv = fragCoord/iResolution.xy;

    vec3 color1 = getcolor(uv, uv.x, 0.8, vec3(1,0,0),  vec3(0,0,0), false);
    vec3 color2 = getcolor(uv, uv.x, 0.6, vec3(-1,1,0), vec3(0,0,0), false);
    vec3 color3 = getcolor(uv, uv.x, 0.4, vec3(0,-1,1), vec3(0,0,0), false);
    vec3 color4 = getcolor(uv, uv.x, 0.2, vec3(0,0,-1), vec3(0,0,0), false);

