vec2 PlayNotes(const in float time)
{
    vec2 ret = vec2(0.0);
    Note note;
    Instrument instrument;
    float sustain_time;
    float duration;
    float play_at_time;
    float relative_time;
    float scaled_relative_time;
    float volume;
    vec2 note_volume;
    float beats_per_second;
    float normalized_wave_time;
    for (int note_index = 0; note_index < notes.length(); note_index++)
    {
        note = notes[note_index];
        instrument = instruments[note.instrumentIndex];
        sustain_time = max(note.duration - instrument.attackTime - instrument.decayTime, 0.0);
        duration = (instrument.attackTime + instrument.decayTime + sustain_time + instrument.releaseTime) / playbackSpeed;
        play_at_time = note.playAtTime / playbackSpeed;
        relative_time = time - play_at_time;
        scaled_relative_time = relative_time * playbackSpeed;
        if ((time >= play_at_time) && ((time - duration) < play_at_time))
        {
            switch (instrument.waveType)
            {
            case squareWaveType:
                beats_per_second = 1.0 / note.frequency;
                normalized_wave_time = mod(relative_time * pitch, beats_per_second) / beats_per_second;
                volume = (normalized_wave_time > instrument.waveRatio) ? -1.0 : 1.0;
                break;
            case triangleWaveType:
                volume = 1.0;
                beats_per_second = 1.0 / note.frequency;
                normalized_wave_time = mod(relative_time * pitch, beats_per_second) / beats_per_second;
                if (normalized_wave_time < instrument.waveRatio)
                {
                    volume = (clamp(normalized_wave_time / instrument.waveRatio, 0.0, 1.0) * 2.0) - 1.0;
                }
                else if (normalized_wave_time > instrument.waveRatio)
                {
                    volume = 1.0 - (clamp((normalized_wave_time - instrument.waveRatio) / (1.0 - instrument.waveRatio), 0.0, 1.0) * 2.0);
                }
                break;
            case sineWaveType:
                volume = sin(relative_time * pitch * doublePi * note.frequency);
                break;
            case whiteNoiseWaveType:
                volume = (fract(sin(relative_time * pitch * 12865.9898) * 43758.5453) * 2.0) - 1.0;
                break;
            }
            note_volume = vec2((instrument.leftRight > 0.5) ? cos((instrument.leftRight - 0.5) * pi) : 1.0, (instrument.leftRight < 0.5) ? cos((0.5 - instrument.leftRight) * pi) : 1.0) * volume * note.velocity;
            if (scaled_relative_time < instrument.attackTime)
            {
                note_volume *= (scaled_relative_time / instrument.attackTime) * instrument.amplitude;
            }
            else if (scaled_relative_time < (instrument.attackTime + instrument.decayTime))
            {
                note_volume *= mix(instrument.amplitude, instrument.sustainAmplitude, (scaled_relative_time - instrument.attackTime) / instrument.decayTime);
            }
            else if (scaled_relative_time < (instrument.attackTime + instrument.decayTime + sustain_time))
            {
                note_volume *= instrument.sustainAmplitude;
            }
            else
            {
                note_volume *= mix(0.0, instrument.sustainAmplitude, exp((-(scaled_relative_time - instrument.attackTime - instrument.decayTime - sustain_time) * doublePi) / instrument.releaseTime));
            }
            ret += note_volume;
        }
    }
    return ret;
}

vec2 mainSound(in int samp, float time)
{
    if (time >= musicPlayTime)
    {
        return vec2(0.0);
    }
    vec2 current_volume = vec2(0.0);
    for (uint anti_aliasing_index = 0U; anti_aliasing_index != antiAliasing; anti_aliasing_index++)
    {
        float current_time = time - float(anti_aliasing_index) / (iSampleRate * float(antiAliasing));
        current_volume += (current_time < 0.0) ? vec2(0.0, 0.0) : PlayNotes(mod(current_time, repeatMusicTime / playbackSpeed));
    }
    return current_volume * volume / float(antiAliasing);
}

