vec3 getColor(float height){
    for(int i = 0; i < heights.length(); i++){
        if(height <= heights[i])
            return mix(colors[heightColorRelation[i-1]], colors[heightColorRelation[i]], (height - heights[i - 1]) / (heights[i] - heights[i -1]));
    }
    return vec3(colors[heightColorRelation[heights.length()-1]]);
}
