struct Line
{
    vec2 position;
    vec2 tangent;
    float a, b;
};

Line line(vec2 p1, vec2 p2);
vec2 normal(vec2 tangent);
vec2 normal(Line);
float map(vec2 x, Line);
vec4 blend(vec4 C, vec4 c, float f);

void mainImage(out vec4 C, in vec2 O)
{
    O = (O - iResolution.xy / 2.);
    C = vec4(0);

    vec2 c1 = vec2(-100., -50.); // lower left corner
    vec2 c2 = vec2(100., 50.); // upper right corner
    ivec2 n = ivec2(9, 9); // cells per dimension
    vec2 grid = vec2(n) / (c2 - c1); // world-position to cell-id

    {
        vec2 f = vec2(10.);

        Line ray = line(
            mix(
                vec2(-250., -100.),
                vec2(250., -100.),
                .5+.5*cos(iTime*.1)),
            vec2(50., 55.));
        if (iMouse.z > 0.)
            ray = line(iMouse.xy - iResolution.xy / 2., abs(iMouse.zw) - iResolution.xy / 2.);
        // special cases
        //ray = line(vec2(0, -100), vec2(0, 100)); // up
        //ray = line(vec2(0, 100), vec2(0, -100)); // down
        //ray = line(vec2(-150, 0), vec2(150, 0)); // right
        //ray = line(vec2(150, 0), vec2(-150, 0)); // left

        vec2 i = min(
            sign(ray.tangent) * (c1 - ray.position) * grid,
            sign(ray.tangent) * (c2 - ray.position) * grid
        );
        vec2 len = 1. / abs(ray.tangent) / grid;
        vec2 vx = ray.tangent * len.x;
        vec2 vy = ray.tangent * len.y;

        if (true)
        {
            // interleave x-hits and y-hits
            for (ivec2 j=ivec2(0,0) ; any(lessThan(j, n)) ; )
            {
                vec2 t = (i + vec2(j)) * len;
                int k;
                if (j.y == n.y || j.x < n.x && t.x < t.y)
                    k = 0;
                else
                    k = 1;
                vec2 x = ray.position + t[k] * ray.tangent;
                j[k] += 1;
                vec2 offset = vec2(0);
                offset[k] = .5 * sign(ray.tangent)[k];
                vec2 cell = floor((x - c1) * grid + offset);
                vec2 center = c1 + (cell + .5) / grid;
                vec2 connect = center;
                connect[k] = x[k];
                vec2 bar = vec2(.5) / grid;
                bar[k] = 0.;
                f[k] = min(f[k], length(O - center) - float(k + 1));
                f[k] = min(f[k], map(O, line(connect, center)));
                f[k] = min(f[k], map(O, line(x - bar, x + bar)));
            }
        } else {
            // find x-hits, then y-hits
            for (int j=0 ; j<n.x ; ++j)
            {
                vec2 x = ray.position + i.x * vx;
                i.x += 1.;
                vec2 cell = floor((x - c1) * grid + vec2(.5 * sign(ray.tangent).x, 0));
                vec2 center = c1 + (cell + .5) / grid;
                f.x = min(f.x, length(O - center) - 1.);
                f.x = min(f.x, map(O, line(vec2(x.x, center.y), center)));
                f.x = min(f.x, map(O, line(x - vec2(0,.5) / grid, x + vec2(0,.5) / grid)));
            }

            for (int j=0 ; j<n.y ; ++j)
            {
                vec2 x = ray.position + i.y * vy;
                i.y += 1.;
                vec2 cell = floor((x - c1) * grid + vec2(0, .5 * sign(ray.tangent).y));
                vec2 center = c1 + (cell + .5) / grid;
                f.y = min(f.y, length(O - center) - 2.);
                f.y = min(f.y, map(O, line(vec2(center.x, x.y), center)));
                f.y = min(f.y, map(O, line(x - vec2(.5,0) / grid, x + vec2(.5,0) / grid)));
            }
        }

        C = blend(C, vec4(1.,.2,0,1), f.x);
        C = blend(C, vec4(0,.2,1,1), f.y);
        C = blend(C, vec4(0,.5,0,1), map(O, ray));
    }

    // grid
    {
        float f = 10.;
        for (int i=0 ; i<n.x+1 ; ++i)
        {
            float a = float(i) / float(n.x);
            f = min(f, map(O, line(
                vec2(mix(c1, c2, a).x, c1.y),
                vec2(mix(c1, c2, a).x, c2.y) )));
        }
        for (int i=0 ; i<n.y+1 ; ++i)
        {
            float a = float(i) / float(n.y);
            f = min(f, map(O, line(
                vec2(c1.x, mix(c1, c2, a).y),
                vec2(c2.x, mix(c1, c2, a).y) )));
        }
        C = blend(C, vec4(vec3(0),1), f);
    }

    C = blend(C, vec4(1), 0.);
    C.xyz = pow(C.xyz, vec3(1./2.2));
}

Line line(vec2 p1, vec2 p2)
{
    return Line(p1, normalize(p2 - p1), 0., length(p2 - p1));
}

vec2 normal(vec2 tangent)
{
    return vec2(-tangent.y, tangent.x);
}

vec2 normal(Line l)
{
    return normal(l.tangent);
}

float map(vec2 x, Line l)
{
    x -= l.position + l.tangent * (l.a + l.b) / 2.;
    float u = dot(x, l.tangent);
    float v = dot(x, normal(l));
    return length(vec2(max(0., abs(u) - (l.b - l.a) / 2.), v));
}

vec4 blend(vec4 C, vec4 c, float f)
{
    return C + c * smoothstep(1.5, 0., f) * (1. - C.w);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 offset = vec2(-0.649033, 0.372347);
    float scale = pow(0.5, 20.0);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.y)*2.0 - 1.0 - vec2(1.5, 0.0);
    vec2 m  = (iMouse.xy/iResolution.y)*2.0 - 1.0 - vec2(1.5, 0.0);

    // m = round(uv*10.0) / 10.0;

    vec2 c = vec2((uv-m)*scale);
    vec2 C = vec2(m*scale + offset);
    vec2 z = vec2(0.0);
    vec2 Z = vec2(0.0);

    float id = 0.0;
    for(int i = 0; i < 900; ++i) {
        // x+iy = 2*z_n*Z_n
        float x = 2.0*(z.x*Z.x - z.y*Z.y);
        float y = 2.0*(z.x*Z.y + z.y*Z.x);

        // z_n+1 = z_n^2 + c + 2*Z_n*z_n
        float a = z.x*z.x - z.y*z.y + c.x + x;
        float b = 2.0*z.x*z.y       + c.y + y;
        z.x = a;
        z.y = b;

        // Z_n+1 = Z_n^2 + C
        {
            float a = Z.x*Z.x - Z.y*Z.y + C.x;
            float b = 2.0*Z.x*Z.y + C.y;
            Z.x = a;
            Z.y = b;
        }

        // Actual coordinate is: z+Z
        // The idea is to calculate only a few 'Z' in high precision
        // and the 'z' in low precision.
        float d = dot(z, z);
        if (d > 256.0) {
            // https://iquilezles.org/articles/msetsmooth
            id = float(i) - log2(log2(d)) + 4.0;
            break;
        }
    }

    // Sinebow coloring
    float pi = 3.14159265358979;
    float t = (0.5 - id*0.02)*pi;

    float r = cos(t + pi*0.0/3.0);
    float g = cos(t + pi*1.0/3.0);
    float b = cos(t + pi*2.0/3.0);

    vec3 col = vec3(r*r, g*g, b*b) * min(id, 1.0);

    fragColor = vec4(col,1.0);
}

