vec4  fC ( in vec2 fragCoord ) // Change the void mainImage of your shader to this.

{
    vec2 st = fragCoord.xy/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;
    st *= 0.072;
    st -= vec2(0.200,-0.990);

    vec3 color = vec3(0.0, 0.0, 0.0);

    vec2 z = st;

    for (int i=0; i<109; i++) {
    	vec2 z2;
        z2.x = z.x*z.x - z.y*z.y * 1.01 + 0.011 * cos(iTime*2.123);
        z2.y = z.x*z.y * 2.0 + 0.02 * sin(iTime);
        z = z2;
        z += st;
        float d = z.x*z.x + z.y*z.y * (0.15*z.y+0.85);
        if (d > 4.0) {
            float step = 1.0 / 30.0;
		    color = vec3(float(i) * step);
            vec3 cc = vec3(
                1.0-(sqrt(cos(0.3*d))-2.0),
                1.0-(sqrt(cos(0.6*d))-2.0),
                1.0-(sqrt(cos(0.3*d))-2.0)
            );
            color += 0.4 * (0.5 + 0.5 * sin(-3.0 * iTime + 150.0 * step * cc ));
            break;
        }
    }


   return vec4(color, 1); // At the end of your shader, replace the fragColor with this return. 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0);
    float A = 4.,  // Change A to define the level of anti-aliasing (1 to 16) ... higher numbers are REALLY slow!
          s = 1./A, x, y;

    for (x=-.5; x<.5; x+=s) for (y=-.5; y<.5; y+=s) fragColor += min ( fC(vec2(x,y)+fragCoord), 1.0);

	fragColor /= A*A;
}
