// PERLIN NOISE

int whichVec[8] = int[8](0, 3, 8, 8, 6, 3, 0, 2); // shhhhh... don't worry about it.

float grad(int i, vec3 n) {
    switch(whichVec[i]) {
    	case 0:  return  n.x + n.y;
        case 1:  return -n.x + n.y;
    	case 2:  return  n.x - n.y;
    	case 3:  return -n.x - n.y;
    	case 4:  return  n.x + n.z;
    	case 5:  return -n.x + n.z;
    	case 6:  return  n.x - n.z;
    	case 7:  return -n.x - n.z;
    	case 8:  return  n.y + n.z;
    	case 9:  return -n.y + n.z;
    	case 10: return  n.y - n.z;
    	case 11: return -n.y - n.z;
    	case 12: return  n.y + n.x;
    	case 13: return -n.y + n.z;
    	case 14: return  n.y - n.x;
    	case 15: return -n.y - n.z;
        default: return 0.0;
    }
}

float lerp(float p1, float p2, float v) {
    return v * p1 + (1.0 - v) * p2;
}


//still not sure this works...
float noiseSphere(vec3 p) {
    p = 0.5 * p + 0.5; // shift domain from [-1.0, 1.0] to [0.0, 1.0]
    float x1, x2, y1, y2;
    x1 = lerp(grad(0, p), grad(1, p), p.x);
    x2 = lerp(grad(2, p), grad(3, p), p.x);
    y1 = lerp(x1, x2, p.y);
    x1 = lerp(grad(4, p), grad(5, p), p.x);
    x2 = lerp(grad(6, p), grad(7, p), p.x);
    y2 = lerp(x1, x2, p.y);
    return 0.5 * lerp(y1, y2, p.z) + 0.5; // shift range from [-1.0, 1.0] to [0.0, 1.0]
}

