// Rotates?
mat2 rot(float a) {
    return mat2(cos(a), sin(a), 
               -sin(a), cos(a));
}

// Quaternion multiplication
vec4 qMult(vec4 q1, vec4 q2) {
    vec4 qr;
	qr.x = (q1.w * q2.x) + (q1.x * q2.w) + (q1.y * q2.z) - (q1.z * q2.y);
	qr.y = (q1.w * q2.y) - (q1.x * q2.z) + (q1.y * q2.w) + (q1.z * q2.x);
	qr.z = (q1.w * q2.z) + (q1.x * q2.y) - (q1.y * q2.x) + (q1.z * q2.w);
	qr.w = (q1.w * q2.w) - (q1.x * q2.x) - (q1.y * q2.y) - (q1.z * q2.z);
	return qr;
}

// Rotate using quaternions
vec3 rotate(vec3 point, vec3 axis, float angle) {
    vec4 p, q1, q2;
    p.xyz = point;
    p.w = 0.0;
    q1.xyz = sin(0.5 * angle) * axis;
    q1.w = cos(0.5 * angle);
    q2.xyz = -q1.xyz;
    q2.w = q1.w;
    return qMult(qMult(q1, p), q2).xyz;
}

// Draws some spinning lights
vec3 light(vec3 n) {
    float rSpeed = 0.03;
    float gSpeed = 0.05;
    float bSpeed = 0.07;
    vec3 rStart = Z;
    vec3 gStart = Z;
    vec3 bStart = Z;
    vec3 rAxis = rotate(X, Z, 0.0 * TWOPI / 3.0);
    vec3 gAxis = rotate(X, Z, 1.0 * TWOPI / 3.0);
    vec3 bAxis = rotate(X, Z, 2.0 * TWOPI / 3.0);
    vec3 rDir = rotate(rStart, rAxis, TWOPI * iTime * rSpeed);
    vec3 gDir = rotate(gStart, gAxis, TWOPI * iTime * gSpeed);
    vec3 bDir = rotate(bStart, bAxis, TWOPI * iTime * bSpeed);
    return vec3(dot(n, rDir), dot(n, gDir), dot(n, bDir));
}

// Spins a sphere and lights it
vec3 render(vec3 p) {
    float speed = 0.1;
    vec3 axis = vec3(0.0, 1.0, 0.0);
    //return light(p);
    //return vec3(noiseSphere(rotate(p, axis, TWOPI * iTime * speed)));
    return light(p) * noiseSphere(rotate(p, axis, TWOPI * iTime * speed * 3.));
}

