vec2 near(vec2 new, vec2 old) {
    if (new.x < old.x) return new;
    return old;
}

vec2 map(vec3 rp) {

    vec2 msd = vec2(FAR, 0);
    for (int i = 0; i < 10; i++) {  
        //this is so sensitive
       float c = 0.3;// + sin(T * 0.2) * 0.1; //clamp(0.3 + sin(T * 0.4) * 0.06, 0.24, 0.32); //0.1 - 0.3
       float q = 1.0 + sin(T) * 0.1;  //1.6 + sin((T - PI)  * 0.1) * 0.4; // 3.6 - 1.0
       rp = abs(rp) / dot(rp, rp) - c;
       rp.xy *= rot(float(i));
       msd = near(msd, vec2(sdBox(rp, q - rp, vec3(1.0)), i)); 
    }

    return msd;
}

//from shane
vec3 normal(vec3 rp, out float edge) {

    vec2 e = vec2(EPS, 0.0);
	float d1 = map(rp + e.xyy).x;
    float d2 = map(rp - e.xyy).x;
	float d3 = map(rp + e.yxy).x;
    float d4 = map(rp - e.yxy).x;
	float d5 = map(rp + e.yyx).x;
    float d6 = map(rp - e.yyx).x;
	float d = map(rp).x * 2.0;

    // Edges - Take a geometry measurement from either side of the hit point. Average them, then see how
    // much the value differs from the hit point itself. Do this for X, Y and Z directions. Here, the sum
    // is used for the overall difference, but there are other ways. Note that it's mainly sharp surface 
    // curves that register a discernible difference.
    edge = abs(d1 + d2 - d) + abs(d3 + d4 - d) + abs(d5 + d6 - d);

    //edge = max(max(abs(d1 + d2 - d), abs(d3 + d4 - d)), abs(d5 + d6 - d)); // Etc.

    // Once you have an edge value, it needs to normalized, and smoothed if possible. How you 
    // do that is up to you. This is what I came up with for now, but I might tweak it later.
    edge = smoothstep(0.0, 1.0, sqrt(edge / e.x * 2.0));

    return normalize(vec3(d1 - d2, d3 - d4, d5 - d6));
}

//spheretrack Logeps VolumeGlow
vec3 marchScene(vec3 ro, vec3 rd
){vec2 m=1.-iMouse.xy/iResolution.xy;//m could be identical to ScreenSpace.xy
 //[l] lazily coutneracts overstepping for higher precision in its gradient descent.
 //     by intentional understepping, assuming lipschits constant >1,
 float l=m.x;//set by iMouse.x
 //dynamic number of iterations lacks backwards compatibility.
 float iterMax=450.*m.y;//set by Mouse.y
 //float EPS=0.001; ish.
 //loop accumulators:
 float t=.0,     // t=distanceToCamera (without epsilon)
       g=.0;     // VolumeMarched smoothstep glow: +=lerp((exp(-SquaredDistanceToGlowCenter)))
 vec2  r=vec2(0);// .x=distanceToSurface .y=MaterialID
 for (float i = 0.; i < iterMax; i++){
     vec3 p=ro + rd * t;//pointOnRay
     vec2 r = map(p);   //shortest euclideanPointDistance to distanceField of pointOnRay.
 #ifdef DynamicEps
     if (t>FAR|| log(t*t*EPS/r.x)>0.) break;//zFar || logEps        exits
     //above is very basic logeps, IFF (scene is scaled properly) it relpaces the line below.
 #else
     if (t>FAR||         EPS>r.x    ) break;//zFar || zNearSurface  exits
 #endif
     g += smoothstep(0.,1.,1.2*exp(-dot(p,p)));//increment glow
     t += r.x*l ;}//march along ray
 return vec3(t,g,r.y);}//vec3(distanceToCamera,GlowIntensity,MaterialID)

/**/
void setupCamera(out vec3 ro, out vec3 rd, vec2 uv) {
    vec3 lookAt = vec3(0);
    vec3 camera = vec3(0.0, 0.5, -3.6);
    camera.xz *= rot(1.0);
    camera.xy *= rot(T * 0.5);
    // Using the above to produce the unit ray-direction vector.
    float FOV = PI / 3.; // FOV - Field of view.
    vec3 forward = normalize(lookAt.xyz - camera.xyz);
    vec3 right = normalize(vec3(forward.z, 0., -forward.x )); 
    vec3 up = cross(forward, right);    
    rd = normalize(forward + FOV * uv.x * right + FOV * uv.y * up);
    ro = camera;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec3 pc = vec3(0.0); //pixel colour
    vec3 lp = vec3(4.0, 5.0, -2.0);
    lc = palette(T * 0.05);
    float pulse = (sin(T * 16.0) + 1.0) * 0.5;

    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec3 ro, rd;
    setupCamera(ro, rd, uv);	

    vec3 t = marchScene(ro, rd);

    if (t.x > 0.0 && t.x < FAR) {
        vec3 rp = ro + rd * t.x;
        float edge = 0.0;
        vec3 n = normal(rp, edge);
        vec3 ld = normalize(lp - rp);
        float diff = clamp(dot(n, ld), 0.0, 1.0);
        float fres = pow(clamp(1.0 + dot(n, rd), 0.0, 1.0), 64.0);;
        vec3 refl = reflect(n, rd);    
        float spec = pow(clamp(dot(refl, rd), 0.0, 1.0), 16.0);

        float rt = length(rp);   

        pc += edge * palette(t.z * 0.1 + T * 0.1) * 2.0 * exp(rt * -rt * 0.8);;
        pc += spec * lc * exp(rt * -rt) * clamp(pulse, 0.1, 1.0);    

        pc += vec3(1.0) * diff / (rt * rt * rt);
        pc += lc * exp(rt * -rt * 0.6) * diff * clamp(pulse, 0.1, 1.0);
        pc += lc * t.y * clamp(pulse, 0.1, 1.0);

        pc += vec3(0.8, 0.0, 0.3) * max(n.y, 0.0) * 0.02;
    }

    float gfog = 1.0 - exp(-t.x * t.x * 0.1 / FAR);
    pc = mix(pc, vec3(0.0), gfog);

	fragColor = vec4(sqrt(clamp(pc, 0., 1.)),1.0);
}

