}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 st = fragCoord.xy / iResolution.y;
    st.x += -(iResolution.x / iResolution.y - 1.) / 2.;
    vec3 color = vec3(0.0);

    vec2 mouseR = iMouse.xy / iResolution.xy;

    float x = smoothstep(.2, .8, mouseR.x);
    st = scale(st, mix(.7, .1, x));
    st += mix(0., .4, x);

//    if (mouseR.x < 1./3.) {
//	    st = scale(st, .8);
//    } else if (mouseR.x < 2./3.) {
//        st *= .5;
//        st += .5;
//    } else {
//        st = scale(st, .2);
//        st += .35;
//    }

    // bottom-left
    vec2 margin = vec2(-0.090, -0.090);
    float s = 1.964;
    vec2 bl = smoothstep(margin - s / 2., margin + s / 2., st); 
    vec2 tr = smoothstep(margin - s / 2., margin + s / 2., 1.0 - st);

    vec3 pct = vec3(min4(bl.x, bl.y, tr.x, tr.y));
    float p = bl.x * bl.y * tr.x * tr.y;
    pct = mix(pct, vec3(p), vec3(8., 7., 9.));

    pct = gain(pct, 4.);

    float t = 5.880;
    t += iTime;
    t += PI2 * mouseR.y;
    color = sin(pct * PI + t * 1.);

    color = color.rgb + color.bbb - color.ggg * 3.;
    //color = sin(color + vec3(1,2,3));
    fragColor = vec4(color,1.0);
}

