                       float closest=gndDistance;mtl=   GROUND_MTL;
    if (bldDistance<closest){closest=bldDistance;mtl=BUILDINGS_MTL;}
    if (redDistance<closest){closest=redDistance;mtl=      RED_MTL;}
    return closest;}
