// Source https://www.shadertoy.com/view/4djSRW
float hash13(vec3 p3)
{
	p3  = fract(p3 * 0.1031f);
    p3 += dot(p3, p3.yzx + 19.19f);
    return fract((p3.x + p3.y) * p3.z);
}

float func(float f)
{
	return (f > 0.5f ? (0.5f - (f - 0.5f)) : f) * 2.0f;
}
vec3 road(float f)
{
	return f > 0.8 ? vec3(mix(vec3(0.3f), vec3(0.8f), (f - 0.8f) * 5.0f)) : vec3(mix(vec3(0.4f, 0.5f, 0.1f), vec3(0.3f), clamp01(f * 5.0f)));
}


const vec2 coords0[] = vec2[]( vec2(-1, -1), vec2(-1, 1) );
const vec2 coords1[] = vec2[]( vec2(1, 1), vec2(1, -1) );

MAIN()
{
	vec2 uv = (fragCoord) / SCALE;
	const vec2 coord = floor(uv);
	uv = mod(uv, 1.0f) * 2.0f - 1.0f;

	const float t = iTime * 0.5f;
	const int val0 = int(floor(hash13(vec3(coord, floor(t))) * 1.99999f));
	const int val1 = int(floor(hash13(vec3(coord, ceil(t))) * 1.99999f));
	const float lerp = clamp01((t - floor(t)));

	float f = 1.0f;
	vec2 r[] = vec2[]( coords0[val0], coords1[val1] );
	int idx = 0;
	if(val0 != val1)
	{
		f = lerp;
		if(f < 0.5f)
		{
			f *= 2.0f;
			idx = val0;
		}
		else
		{
			f = (0.5f - (f - 0.5f)) * 2.0f;
			idx = val1;
		}

		f = pow(f, 10.0f) * 1000.0f;
		r[0] = coords0[idx] + vec2(0, f * coords0[idx].y);
		r[1] = coords1[idx] + vec2(f * coords1[idx].x, 0);
		f += 1.0f;
	}

	float result = 0.0f;
	const float rad_min = (f - DIAMETER * 0.5f);
	const float rad_max = (f + DIAMETER * 0.5f);
	const float tile_mask = clamp01((1.0f - length(uv) * 0.95f) * 20.0f);
	for(int i = 0; i < 2; i++)
	{
		const float dist = distance(r[i], uv);
		if(dist >= rad_min && dist <= rad_max)
		{
			const float r = (dist - rad_min) / (rad_max - rad_min);
			result = max(func(r), result);
		}
	}

	fragColor = vec4(road(clamp01(result) * tile_mask), 1.0f);
}

