float df_segment(vec2 uv, vec2 vec_a, vec2 vec_b, float thickness){
    vec2 vec_ap = uv    - vec_a;
    vec2 vec_ab = vec_b - vec_a;
    return distance(vec_ap, vproj01(vec_ap, vec_ab)) - thickness;
}

