    float axis_x = df_line(uv, vec2(0, 0), vec2(1, 0), .01);
    float axis_y = df_line(uv, vec2(0, 0), vec2(0, 1), .01);
    float axes = smoothstep(FSAA, .0, op_join(axis_x, axis_y));

    // Grid!
    float half_repeat = .5 * GRID_REPEAT;
    vec2 grid_uv = mod(uv - half_repeat, GRID_REPEAT) - half_repeat;
    float grid_x = df_line(grid_uv, vec2(0, 0), vec2(1, 0), .001);
    float grid_y = df_line(grid_uv, vec2(0, 0), vec2(0, 1), .001);
    float grid = smoothstep(FSAA, .0, op_join(grid_x, grid_y));

    Arrow arrow0 = ARROW_DEFAULT;
    Arrow arrow1 = ARROW_DEFAULT;
    Arrow arrow2 = ARROW_DEFAULT;

    arrow0.angle = .5 * iTime;
    arrow1.angle = .1 * PI;
    arrow2.angle = arrow1.angle;
    arrow0.norm = 1.1;
    arrow1.norm = 2.2;
    arrow2.norm = arrow_vproj_scalar(arrow0, arrow1) * arrow1.norm;

    float dot_product = df_segment(uv, vec2(0, 0), vec2(arrow_dot(arrow0, arrow1), 0), .01);
    dot_product = smoothstep(FSAA, .0, dot_product);

    vec3 scene = RGB_DARK;
    scene = mix(scene, RGB_GRAY, grid);
    scene = mix(scene, RGB_LIGHT, axes);
    scene = mix(scene, RGB_RED,   arrow_draw(uv, arrow0));
    scene = mix(scene, RGB_BLUE,  arrow_draw(uv, arrow1));
    scene = mix(scene, RGB_LIGHT, arrow_draw(uv, arrow2));
    scene = mix(scene, RGB_PURP,  dot_product);
	fragColor.rgb = scene;
}

