vec2 rotate(vec2 p, float ang){
    float c = cos(ang), s = sin(ang);
    return vec2(p.y * c - p.x * s, p.y * s + p.x * c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 R = (4.0*( fragCoord.xy / iResolution.xy ) - 2.0);


  	vec2 m = iMouse.xy / iResolution.xy;
	vec2 F=rotate(gl_FragCoord.xy,iTime/10.0);

  	vec3 p = vec3(F / iResolution.y, 0.5-m.x+0.02*sin(iTime));
	float ki=16.0+112.0*(iMouse.x/iResolution.x);
  	for (float i = 1.0; i < 128.0; i++)
	{
		if(i>ki)
			break;
    		p.xzy = vec3(1.3, 0.999, 0.777) * abs(p/dot(p,p)-vec3(1.0,1.0,m.y*0.3));
	}

	float cm=0.0;

	float cre=R.x;
	float cim=R.y;
	float zre=p.x/5.0;
	float zim=p.y/5.0;
	float max=32.0;
	float j=0.0;

	for(float i=0.0;i<256.0;i++)
	{
		if(i>max)
		{
			cm=0.0;
			break;
		}
		float dam=zre*zre-zim*zim+cre;
		zim=2.0*zre*zim+cim;
		zre=dam;
		if( (zre*zre+zim*zim)>4.0)
		{
			cm=i/max;
			break;
		}
	}

	vec3 cc=vec3(cm,cm,cm)*p;

	if(cm<0.3)
		cc = vec3(cm,cm,cm)/p;


    fragColor = vec4(cc,1.0);
}

// Parallel Transport Study
// Copyright Max Sills 2016, licensed under the MIT license.
/*
This is an attempt to write down what I'm learning about geometry to keep
myself honest. Feel free to correct anything I'm saying in the comments.

The sphere is a two dimensional topological manifold.
Meaning it's a set of points M, together with a collection of subsets O,
called the "open" sets. This can be denoted (M, O).

When you embed the sphere in R3, it inherits the induced topology 
from R3 (base of open balls). The manifold structure is obtained 
by adding invertible charts from the sphere to R2. A manifold is a
set M, its open sets O, and a collection of charts A, (M, O, A).

Consider two charts which map the northern and southern 
hemispheres of the sphere into a unit disk on the plane.

The northern chart maps points (X,Y,Z) on the sphere
to the points (x, y) on the unit disc. Points (x, y)
on the disc are mapped back to (x, y, sqrt(-x^2 + y^2 + 1)).

Every point in the northern hemisphere has a corresponding 
point in the pictured unit disc. It also has a tangent plane
which touches the sphere only at that point. Actually, there
are an infinite amount of such planes, because you can rotate 
a tangent plane by any amount around its normal vector
and still get another tangent plane.

An immediate question is, what orientation should an arbitrary tangent plane
have? The answer is given by something called an "affine connection".
Riemannian manifolds, which are pairs (M, O, A, metric), where a metric
is a way to define the distance between two points, have a special "default" 
orientation map, called the "Levi-Civita connection". That's pictured
on the left.

Parallel transport is a technique to determine per-unit surface area
curvature. You take a loop around an area on a manifold, and see how 
tangent plane orientations have changed with respect to the Levi-Civita connection.

You can interact with this study, and imagine different curves on the northern
hemisphere of a sphere being projected down to the unit disc. The two arrows
represent the orientation of a tangent plane on the sphere.
*/
#define PI 3.14159
mat3 Rot (float angle)
{
    float c = cos(angle/360.*2.*PI);
    float s = sin(angle/360.*2.*PI);

return  mat3(
        vec3(c, s, 0),
        vec3(-s, c, 0),
        vec3(0, 0, 1)
); 
}

mat3 Disp (vec2 displacement)
{
return  mat3(
        vec3(1, 0, 0),
        vec3(0, 1, 0),
        vec3(displacement, 1)
); 
}

vec2 Apply(mat3 m, vec2 p)
{
    return (m*vec3(p,1.)).xy;
}

// Nead a repetition operator that respects
// negatives and is centered at zero or
// mirror symmetries flake out.
float RepeatInterval(float x, float size)
{
    return mod(x-size/2., size) - size/2.;
}

// Smooth Clamp for distance functions
// Return val until x exceeds cap, then
// smoothly, quickly, go to infinitiy.
float SmoothClamp(float x, float cap, float val)
{
    return val + exp(10000.*(x-cap));
}

float sdCappedCylinder( vec2 p, vec2 h )
{
  p -= vec2(0.,h.y);
  vec2 d = abs(vec2(length(p.x),p.y)) - h;
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float arrow(vec2 p, float len){
    float body = sdCappedCylinder(p, vec2(.001, len));

    p.x = abs(p.x); // Mirror cap.
    vec2 p_cap = Apply(Rot(145.) * Disp(vec2(0.,-2.*len)), p );
    float cap = sdCappedCylinder(p_cap, vec2(.001, len/2.));
    return min(body,cap);
}

float radii(vec2 p)
{
    float l = length(p);
    float theta = atan(p.y,p.x);
    theta = RepeatInterval(theta, 45./360.*2.*PI );

    p.x = l*cos(theta);
    p.y = l*sin(theta);

    //Gridlines
    vec2 pp=Apply(Rot(90.), p);
    float grids = arrow(pp,15.);

    // Arrows

    p.x -= iTime/5.;
    p.x = RepeatInterval(p.x,1.);
    p = Apply(Rot(90.), p);
    float a = arrow(p,l/4.*.2);

    float d = min(a,grids);
    return SmoothClamp(l,5.,d);
}

float circles(vec2 p)
{
    float l = length(p);
    float theta = atan(p.y,p.x);
    theta = RepeatInterval(theta - iTime/10., 15./360.*2.*PI );

    p.x = l*cos(theta);
    p.y = l*sin(theta);

    p.x = RepeatInterval(p.x,1.) ;

    float a = arrow(p, l/5.*.2);

    // Coordinate circles
    float x = RepeatInterval(l,1.);
    float b = 50.*(x)*(x);
    a = min(a, b);

    return SmoothClamp(l,5.5,a);
}

float mouse(vec2 uv)
{
    vec2 mo = iMouse.xy/iResolution.xy;		
    mo = mo *2.0 - 1.0; 
    mo.x *= iResolution.x / iResolution.y;
    mo *= 5.;

    float theta = atan(mo.y,mo.x);
    vec2 p = uv - mo;
    float l = length(mo);
    p = Apply(Rot((theta+PI/2.)*-180./PI + 180.), p);
    float radial_arrow = arrow(p, .5);
    p = Apply(Rot(-90.), p);
    float circ_arrow = arrow(p, .5);

    float d = min(circ_arrow,radial_arrow);
    return SmoothClamp(l,5.1,d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;		
    uv.x *= iResolution.x / iResolution.y;
    uv *= 5.;

    fragColor = vec4(0);
    float d = min(mouse(uv), min(radii(uv), circles(uv)));

    float t = clamp(d, 0.0, .04) * 2.*12.5;
    vec4 bg = vec4(0);
    vec4 fg = vec4(.8);
    fragColor = mix(bg, fg, 1.-t);  
}

const int MAX_RAY_NUM = 255;
const float  MIN_DIST = 0.0;
const float MAX_DIST = 100.0;
const float SCALE = 0.0001;


float sphereSDF(vec3 p){

   return length(p)-1.0;


}



float ShortestDistanceToObjectSurface(vec3 dir,vec3 eye,float start,float end){
        float depth = start;

    for(int i = 1;i<MAX_RAY_NUM;i++){
       float dist = sphereSDF(eye+depth*dir);

        if(dist<SCALE){

        return depth;

        }

        depth+=dist;
        if(depth>=end){

        return end;
        }


    }

    return end;




}


vec3 RayDir(vec2 size,vec2 fragcood){
    vec2 xy = fragcood-size/2.0;
    float z = size.y;
    return normalize(vec3(xy,-z));


}

vec3 createNormal(vec3 p){

  return normalize(vec3(
   sphereSDF(vec3(p.x+SCALE,p.y,p.z))-sphereSDF(vec3(p.x-SCALE,p.y,p.z)),

   sphereSDF(vec3(p.x,p.y+SCALE,p.z))-sphereSDF(vec3(p.x,p.y-SCALE,p.z)), 

   sphereSDF(vec3(p.x,p.y,p.z+SCALE))-sphereSDF(vec3(p.x,p.y,p.z-SCALE))

   ));



}


vec3 PhongLight(vec3 k_d,vec3 k_s,vec3 eye,vec3 lightpos,vec3 LightScale,vec3 p,float alpha){

    vec3 N = createNormal(p);
    vec3 L = normalize(lightpos-p);
    vec3 V = normalize(eye-p);
    vec3 R = normalize(reflect(-L,N));


    float LdotN = dot(L,N)*2.0;

    float RdotN = dot(R,V);

    if(LdotN<0.0){


        return vec3(0.0,0.0,0.0);


    }


    if(RdotN<0.0){



      return k_d*LightScale*LdotN;


    }


    return LightScale*(k_d*LdotN+pow(RdotN,alpha)*k_s*2.0);



}


vec3 PhongLightPosSuf(vec3 k_a,vec3 k_d,vec3 k_s,vec3 eye,vec3 p,float alpha){

    vec3 Ambient = vec3(1.0,1.0,1.0)*0.5;

    vec3 color = Ambient*k_a;


    vec3 LightPos1 = vec3(4.0 * sin(iTime),
                          2.0,
                          4.0 * cos(iTime));

    vec3 LightScale = vec3(0.4,0.6,0.1);

    color+=PhongLight(k_d,k_s,eye,LightPos1,LightScale,p,alpha);



    vec3 LightPos2 = vec3(2.0 * sin(0.37 * iTime),
                          2.0 * cos(0.37 * iTime),
                          2.0);

    vec3 LightScale2 = vec3(0.8,0.2,0.2);

     color+=PhongLight(k_d,k_s,eye,LightPos2,LightScale2,p,alpha);

    return color;








}


