// Primality test, created by inigo quilez
// https://www.shadertoy.com/view/4slGRH
bool isPrime(int x)
{
	if( x==1 ) return false;
	if( x==2 ) return true;
	if( x==3 ) return true;
	if( x==5 ) return true;
	if( x==7 ) return true;
	if( (x%2)==0 ) return false;
	if( (x%3)==0 ) return false;
	if( (x%5)==0 ) return false;

    int xm = 1 + int(sqrt(float(x)));

	int y = 7;
	for( int i=0; i<100000; i++ ) // count up to 3000000
	{
		         if( (x%y)==0 ) return false;
		y += 4;  if( y>=xm    ) return true;
		         if( (x%y)==0 ) return false;
		y += 2;  if( y>=xm )    return true;
		         if( (x%y)==0 ) return false;
		y += 4;  if( y>=xm )    return true;
		         if( (x%y)==0 ) return false;
		y += 2;  if( y>=xm )    return true;
		         if( (x%y)==0 ) return false;
		y += 4;  if( y>=xm )    return true;
		         if( (x%y)==0 ) return false;
		y += 6;  if( y>=xm )    return true;
		         if( (x%y)==0 ) return false;
		y += 2;  if( y>=xm )    return true;
		         if( (x%y)==0 ) return false;
		y += 6;  if( y>=xm )    return true;
	}

	return true;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	float zoom = 1.0;
    float speed = 0.3;
    float shiftX = 0.0;
    float shiftY = 0.0;
    if (iMouse.z > 0.0) {
        vec2 normMouse = iMouse.xy/iResolution.xy-0.5;
        vec2 deflection = normMouse*normMouse*normMouse;
    	shiftX = (exp(deflection.x*50.0)-exp(-deflection.x*50.0))*5000.0;
    	shiftY = (exp(deflection.y*24.0)-exp(-deflection.y*24.0))*200.0;
        // magnify when mouse vertically centered
        zoom = floor(mix(iResolution.y/80.0, 1.0, smoothstep(0.0, 0.1, normMouse.y*normMouse.y*4.0)));
    }        

    float shear = mod(iTime*speed, 2.0);
    float offsetY = -0.5*shear + shiftY;  // scroll one row up per turn
    vec2 cy = vec2(0.0);
    for (int i = 0; i <= 1; i++) {
        float y = floor((fragCoord.y-0.5+float(i) - floor(iResolution.y*0.5))/zoom + offsetY);
        float offsetX = y*shear + 0.25*shear*shear + shiftX; 
	    vec2 cx = vec2(0.0);
    	for (int j = 0; j <= 1; j++) {
            float x = floor((fragCoord.x-0.5+float(j) - floor(iResolution.x*0.5))/zoom + offsetX);
            int n = abs(int(y * y + x));  // consider negative primes too
            cx[j] = isPrime(n) ? 1.0 : 0.0;
        }
        // ultra smooth anti-alias for large magnification, horizontally
        cy[i] = mix((cx[0]+cx[1])*0.5, mix(cx[0], cx[1], fract(offsetX*zoom)), smoothstep(1.5, 3.5, zoom));
    }
    // ultra smooth anti-alias for large magnification, vertically
    float c = mix((cy[0]+cy[1])*0.5, mix(cy[0], cy[1], fract(offsetY*zoom)), smoothstep(0.5, 1.5, zoom));

    fragColor = vec4(0.0, c, c, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy * 2. - 1.;
