    for(float i = -1.; i < +1. ; i += .1){
    	float line = smoothstep(0.,0.005,abs(uv.x - i));
        color = mix(vec4(0.5),color,line);

        line = smoothstep(0.,0.005,abs(uv.y - i));
        color = mix(vec4(.5),color,line);
    }

    float axis = smoothstep(0.,0.01,abs(uv.x));
    color = mix(vec4(.3),color,axis);
    axis = smoothstep(0.,0.01,abs(uv.y));
    color = mix(vec4(.3),color,axis);

    //float sinsin = smoothstep(0.,0.01,abs(.5 * sin(2. * 3.14 * uv.x + iTime) - uv.y));
    float sinsin = smoothstep(0.,0.003,abs(.5 * sin(2. * 3.14 * uv.x + iTime) - uv.y) / sqrt(1.0 + .5*.5*6.2831*6.2831*cos(2. * 3.14 * uv.x + iTime)*cos(2. * 3.14 * uv.x + iTime)));
    color = mix(vec4(.1),color,sinsin);

    fragColor = color;
}

