vec3 hsv2rgb(vec3 c) {
  vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

float distFunc( vec3 pos )
{
    pos.xz = mod(pos.xz,1.)-0.5*1.;
    pos /= 0.5;
    vec2 t = vec2(0.6,0.10);
    float amp = 0.6*sin(iTime)+0.3;
    pos.y -= amp;
    vec2 q = vec2(length(pos.xz)-t.x,pos.y);
	float res = min( length(vec3(pos.x, pos.y, pos.z))-.2, pos.y+amp );
    res = min( res, length(q)-t.y );
    //res = min( res, pos.x-3. );

    return res*0.5;
}

vec3 getNormal( vec3 pos )
{
    float eps = 0.0001;

    return normalize( vec3( distFunc(pos)-distFunc(vec3( pos.x-eps, pos.y, pos.z )),
                                              distFunc(pos)-distFunc(vec3( pos.x, pos.y-eps, pos.z )),
                          					  distFunc(pos)-distFunc(vec3( pos.x, pos.y, pos.z-eps ))) );
}

float softshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
	float res = 1.0;
    float t = mint;
    for( int i=0; i<16; i++ )
    {
		float h = distFunc( ro + rd*t );
        res = min( res, 8.0*h/t );
        t += clamp( h, 0.02, 0.10 );
        if( h<0.001 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy*2.-iResolution.xy) / min(iResolution.x, iResolution.y);

    vec3 col = vec3( 0. );

    vec3 camPos = vec3( 1., 6., 10. );
    vec3 ray = normalize( vec3( uv, 0. ) - camPos );
    vec3 cur = camPos, curM;
    vec3 normal = vec3(0.);
    float diff = 0.0;
    bool hit = false;
    float ite;

    for( int j = 0; j < 2; ++j )
    {
    	for( int i = 0; i < 200; ++i )
    	{
            float d = distFunc( cur );
            if( d < 0.0001 )
            {
                normal = getNormal( cur );
                diff += 0.9*dot( normal, lightDir );
                hit = true;
                ite = float(i);
                break;
            }

            cur += ray * d;
        }

        if( j == 0 )
            curM = cur;

        if( !hit || curM.y <= 0.01 )
            break;

        cur += normal*0.01;
        ray = reflect( ray, normal );
    }

    if( curM.y <= 0.01 )
    {
        float f = mod( floor(5.0*cur.z) + floor(5.0*cur.x), 2.0);
        col = 0.1 + 0.1*f*vec3(1.0);
    }

    vec3 ref = reflect( ray, normal );
    float spe = pow(clamp( dot( ref, lightDir ), 0.0, 1.0 ),16.0);

    col += 1.2*vec3( 0.7, 0.8, 0.9 )*1.3*vec3( diff )*hsv2rgb(vec3(curM.z/4.,0.8,1.0))*softshadow( cur, lightDir, 0.02, 2.5 )+vec3(0.1);
    col += spe*vec3( diff );
    col += 0.1*softshadow( cur, ref, 0.02, 2.5 );
    fragColor = vec4( col, 1.0 );
}

// circles
vec2 redCircle = vec2(0.45, 0.25);
vec2 greenCircle = vec2(0.5, 0.25);
vec2 blueCircle = vec2(0.55, 0.25);

float radius = 0.1;

// colors
vec3 Red = vec3(1.0, 0.0, 0.0);
vec3 Green = vec3(0.0, 1.0, 0.0);
vec3 Blue = vec3(0.0, 0.0, 1.0);

float frequency = 1.0;
float amplitude = 0.5;

// creates a transparent layer
vec4 Transparent = vec4(0.0, 0.0, 0.0, 0.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

 	vec2 uv = fragCoord.xy / iResolution.xy;

 	float aspectRatio = iResolution.y / iResolution.x;

    uv.y *= aspectRatio;

    // animates the green circle to move up and down, while keeping the circle visible
    greenCircle.y = (abs(sin(iTime * frequency) + 1.0) / 2.0) * amplitude;

    vec4 Layer1 = vec4(1.0, 1.0, 1.0, 1.0);
	vec4 Layer2;
	vec4 Layer3;
	vec4 Layer4;


    // draws red circle layer
    if (length(redCircle - uv) < radius)
        Layer2 = vec4(Red, 1.0);
    else
        Layer2 = Transparent;

    // draws green circle layer
    if (length(greenCircle - uv) < radius)
        Layer3 = vec4(Green, 1.0);
    else
        Layer3 = Transparent;

    // draws blue circle layer
    if (length(blueCircle - uv) < radius)
        Layer4 = vec4(Blue, 1.0);
    else
       	Layer4 = Transparent;

    // blends layers over each other
    vec4 Composite = mix(Layer1, Layer2, Layer2.a);
    Composite = mix(Composite, Layer3, Layer3.a);
    Composite = mix(Composite, Layer4, Layer4.a);

    fragColor = Composite;

}

#define PI 3.1415926535897932384626433832795
#define PI2 (2.0*PI)
#define WAVE_FREQ 75.0
#define COMPRESSION_FACTOR 1.0
#define WAVE_SPEED 30.0
#define NOISE_SCALE 5.0
#define NOISE_SPEED 100.0
#define NOISE_INTENSITY 0.7
#define WAVE_FADE_FACTOR 7.0
#define CHROMATIC_OFFSET 0.04
#define CONE_HALF_ANGLE atan(1.0,1.0)
#define CONE_CENTER vec2(0.0, 1.0)
#define CONE_DIRECTION vec2(0.0, -1.0)
#define CONE_FADE_FACTOR 2.0
#define LIGHT_INTENSITY 1.5

//all rand and noise functions from https://gist.github.com/patriciogonzalezvivo/670c22f3966e662d2f83
float rand(float n){return fract(sin(n) * 43758.5453123);}

float rand(vec2 n) { 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noise(vec2 p){
	vec2 ip = floor(p);
	vec2 u = fract(p);
	u = u*u*(3.0-2.0*u);

	float res = mix(
		mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
		mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
	return res*res;
}

float limit_angle(float angle){
    return PI2 * fract(angle / PI2);
}

float eval(vec2 delta){
	float dist = length(delta);
    float x = WAVE_FREQ*dist;
    x *= 1.0+(COMPRESSION_FACTOR*dist);
    x -= WAVE_SPEED*iTime;
    float wave = 0.5 + (0.5*sin(limit_angle(x)));
    //1e5 added below to abuse the problem of sin function 
    //when using high floating points to make a glitch effect
    //NOTE: this is probably not a reliable way to make an effect since it may not work on other devices
    vec2 noise_kernel = NOISE_SCALE*delta + NOISE_SPEED*fract(iTime) + 1e5; 
    float noise_val = NOISE_INTENSITY*rand(noise(noise_kernel));
    float fade = 1.0+WAVE_FADE_FACTOR*dist*dist;
	return (wave + noise_val)/fade;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const vec2 center = CONE_CENTER;
    const vec2 direction = normalize(CONE_DIRECTION);
    const vec2 orthogonal = vec2(-direction.y, direction.x);
	vec2 p = (2.0*fragCoord.xy - iResolution.xy)/iResolution.yy;
    vec2 delta = p - center;
    vec2 offset = CHROMATIC_OFFSET*orthogonal;
	float r = eval( delta + offset );
	float gb = eval( delta - offset );
    float ang = atan(dot(delta, orthogonal), dot(delta, direction)) / CONE_HALF_ANGLE;
	float intensity = max(0.0, 1.0/(1.0+CONE_FADE_FACTOR*ang*ang) - 1.0/(1.0 + CONE_FADE_FACTOR));
	fragColor = vec4(vec3(r, gb, gb) * intensity * LIGHT_INTENSITY, 1.0);


}

