Split4 sd_Split_b_xxx( vec2 p, vec2 mi, vec2 ma, vec3 s )
{
//  -------------------------
//  |     |	    |     |     |
//  | b00 | b01 | b10 | b11 |
//  |     |	    |     |     |
//  -------------------------
//     x10     x0    x11

	float x0 = mix( mi.x, ma.x, s.x );
