float hash( uvec2 x )
{
    uvec2 q = 1103515245U * ( (x>>1U) ^ (x.yx   ) );
    uint  n = 1103515245U * ( (q.x  ) ^ (q.y>>3U) );
    return float(n) * (1.0/float(0xffffffffU));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    uvec2 p = uvec2(fragCoord) + 1920U*1080U*uint(iFrame);

    float f = hash(p);

    fragColor = vec4( f, f, f, 1.0 );
}

float drop(vec2 p, vec2 c, float period)
{
    float d = distance(p, c) / period;

    // todo period modulation
    return abs(cos(3.14 * d)) * exp(-0.3 * d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);

    float t = 0.5+0.5*sin(iTime);
    vec2 p = fragCoord.xy;
    vec2 c = iResolution.xy * 0.5;
    float d = drop(p, c, 4.0 + 60.0 * t);

    fragColor = mix(vec4(0.0, 0.0, 0.0, 1.0), vec4(1.0, 0.0, 0.0, 1.0), d);
}

