//#define GRAY

vec2 Hash(vec2 p )
{
    mat2 mat = mat2(vec2(131.31, 313.13), vec2(13.13, 31.31));
    p = mat  * p;
    return fract(sin(p)*18.5453);
}

vec2 GetCenter(vec2 vpo1, vec2 vpo2, vec2 vpo3)
{

    float d1 = dot(vpo3 - vpo1, vpo2 - vpo1);
    float d2 = dot(vpo3 - vpo2, vpo1 - vpo2);
    float d3 = dot(vpo1 - vpo3, vpo2 - vpo3);

    float c1 = d2 * d3;
    float c2 = d1 * d3;
    float c3 = d2 * d1;

    float c = c1 + c2 + c3;

    vec2 center = ((c2 + c3)*vpo1 + (c3 + c1)*vpo2 + (c1 + c2)*vpo3) / 2.0 / c;

    return center;
}

//=============
//return vec2( diatance, id)
vec2 Veronoi(vec2 po, out vec2 vpo, out vec2 vpo1, out vec2 vpo2, out vec2 vpo3)
{
    po = 4.0 * po;
    vec2 flo = floor(po);
    vpo = fract(po);
    vec3 mi1 = vec3(10.0);
    float dist2, dist3;

    for(int i = -1; i <= 1; i++)
    	for(int j = -1; j <= 1; j++)
        {
            vec2 po1 = vec2(float(i), float(j));
            vec2 o = Hash(flo + po1);
            vec2 v = po1 - vpo + (0.5 + 0.5 * sin(6.2831 * o + iTime));
            float d = length(v);

            //use one closest point to ensure part;
            //use two closest point to ensure one border;
            //use three closest point to ensure two border and fix corner;
            if(d < mi1.x)
            {
                dist3 = dist2;
                vpo3 = vpo2;

              	dist2 = mi1.x;
                vpo2 = vpo1;

                mi1 = vec3(d, o);
                vpo1 = v;
            }
            else if(d < dist2)
            {
                dist3 = dist2;
                vpo3 = vpo2;

                dist2 = d;
                vpo2 = v;
            }
            else if(d < dist3)
            {
                dist3 = d;
                vpo3 = v;
            }
        }
    return vec2(mi1.x, mi1.y + mi1.z);
}

