    vec3 col = fract(vec3(sin(cell.y + iTime)) + vec3(0.8, 0.5, 0.1));

    float wi = 0.06;

    col *= smoothstep(0.0, 0.15, cell.x);		//black center

    //border
    float len1 = dot(0.5 * (vpo1 + vpo2), normalize(vpo2 - vpo1));
    float len2 = dot(0.5 * (vpo1 + vpo3), normalize(vpo3 - vpo1));
    float factor1 = smoothstep(0., wi, len1);
