vec3 evaluateLight(in vec3 pos)
{
    vec3	lightCol;
    lightCol =
        (mod(ceil(pos.y),3.)==1.?
                light_blue
               :
                (mod(ceil(pos.y),3.)==2.?light_red:light_green)
               )*.5;

    lightCol += 
        (mod(ceil(pos.x),3.)==1.?
                light_blue
               :
                (mod(ceil(pos.x),3.)==2.?light_red:light_green)
               )*.5;

    float distanceToL = 
        length(fract(pos.xy+light_pos.xy)-.5)-.025;

    float	dl = length(fract(pos.xy+light_pos.xy)-.5)-.025;;
    vec3	o = fract(pos.xyz*15.+light_pos.xyz*15.)-.5;
    float	dl2 = max(max(max(abs(o.x), abs(o.y)), abs(o.z)) + .03, .0);
	vec3	ret = 
        max
        (
        lightCol * 5./(distanceToL*distanceToL)
        ,
    	lightCol * 300.0/sqrt( (dl2-1.5*dl/(dl2)) )
    )*.015;
    dl = length(pos.xyz-LPOS);
    uglob += .015*vec3(.2,.2,.9)*1./(dl*dl);
    return ret;
}

float	scene(vec3 p)
{
    vec3	op = p;
    float	mind = 1e5;
    op = p;

    // "star" lattice
    p.xy += .5;
    p.xyz = fract(p.xyz*.5)-.5;
    vec3 oo = abs(p);
    vec3 o = p*p;
    float s = sqrt(o.x+o.y);
    s = min(s, sqrt(o.x+o.z));
    s = min(s, sqrt(o.y+o.z));
    mind = max(-(length(oo -(oo.x+oo.y+oo.z)/3. )-.3503125),s-0.45);

    // cubic cylinder lattice
    p = op;
    p = fract(p)-.5;
    o = p*p*p*p; // length4 square it a bit
    s = pow(o.x+o.y,1./4.);
    s = min(s, pow(o.x+o.z,1./4.));
    s = min(s, pow(o.y+o.z,1./4.));
    mind = min(mind, s-.025);

    // cylinders for blacklight
    p = op;
    p.z += .52*t;
    light_pos.xy = vec2(cos(p.z*4. ),sin(p.z*4. ))*.15;
    p.xy = fract(p.xy)-.5;
    p.z += .52*t;
    p.xy+=light_pos.xy;
    light_pos.z = p.z;

    mind = min(mind, 
              length(p.xy)-.02501
              );

    // positive light sphere
	mind = min(mind,
               length(op-LPOS)-.0001
               );

    return(mind);
}


vec4	march(vec3 pos, vec3 dir)
{
    vec2	dist = vec2(0.0, 0.0);
    vec3	p = vec3(0.0, 0.0, 0.0);
    vec4	s = vec4(0.0, 0.0, 0.0, 0.0);

    for (int i = -1; i < I_MAX; ++i)
    {
    	p = pos + dir * dist.y;
        dist.x = scene(p);
        dist.y += dist.x;
        h += evaluateLight(p)*.5;
        if (dist.x < E )
        {
            s.y = 1.;
