    return vec4(vec3(factor), 1.);
}

vec3 ComputeLighting(vec3 point, vec2 fragCoord, vec3 lightDir, vec3 lightColor)
{
    vec3 color = vec3(AMBIENT);
    float diffuse = dot(EstimateNormal(point), normalize(lightDir));
    float colorIntensity = ceil(diffuse * SHADES) / SHADES;
    colorIntensity = max(colorIntensity, AMBIENT);    

    if (diffuse >= LIGHT_SHADE_CUTOFF)        
    	color = lightColor * colorIntensity;
    else if (diffuse >= DARK_SHADE_CUTOFF) 
        color = colorIntensity * (lightColor * lightColorB2(fragCoord).xyz);
    else
        color = colorIntensity * (lightColor * darkColorB(fragCoord).xyz);
    return color;
}

