const float stepWidth = 25.0; 

struct HeightColorPair {
    vec3 color;
    float height; 
};

HeightColorPair sawtoothGradient(vec2 uv, vec2 offset, vec3 color) {
    float len = length(uv - offset); 
    float value = len * stepWidth ;
    value = smoothstep(0.0, 1.0, abs(mod(value, 1.0))) * 0.5; 
    value += 0.5;
    value *= 1.0 - pow(len, 0.4);

    float height = 1.0 - floor(len * stepWidth) / stepWidth;

    return HeightColorPair(value * color, height); 
}

HeightColorPair topHeightColor(HeightColorPair a, HeightColorPair b) {
    HeightColorPair result = a;

    if (b.height > a.height) {
     	result = b;   
    }

    return result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 uv = (fragCoord.xy / iResolution.xy) - 0.5;

    float aRadius = (sin(iTime * 0.5) * 0.25) + 0.25; 
	HeightColorPair a = sawtoothGradient(uv, 
                                         vec2(cos(iTime) * aRadius, 
                                              sin(iTime) * aRadius),
                                         vec3(1.0, 1.0, 0.0));

    float bRadius = (sin(iTime * 0.75) * 0.1) + 0.4; 
    HeightColorPair b = sawtoothGradient(uv, 
                                         vec2(sin(iTime * 0.3) * bRadius, 
                                              cos(iTime * 0.3) * bRadius),
                                         vec3(0.0, 1.0, 1.0));

    float cRadius = (cos(iTime * 0.25) * 0.3) + 0.1; 
    HeightColorPair c = sawtoothGradient(uv, 
                                         vec2(sin(iTime * 0.7) * cRadius, 
                                              cos(iTime * 0.7) * cRadius),
                                         vec3(1.0, 0.0, 1.0));

    vec3 color = topHeightColor(topHeightColor(a, b), c).color;  

    fragColor = vec4(color ,1.0);
}

