float cross2d(vec2 a, vec2 b) { return a.x*b.y-b.x*a.y; }

vec2 solve_quadratic0(vec3 fa) {
    float a = fa[0];
    float b = fa[1];
    float c = fa[2];

    // the quadratic solve doesn't work for a=0
    // so we need a branch here.
    if (a == 0.0) {
        return vec2(-c / b);
    } else { 
        // (-b +- sqrt(b*b - 4.0*a*c)) / 2.0*a
        float k = -0.5*b/a;
        float q = sqrt(k*k - c/a);
        return k + vec2(-q,q);
    }
}

vec2 iUnitCircle(in vec2 ro, in vec2 rd) {
	float b = 2.0*dot(ro, rd);
	float c = dot(ro, ro) - 1.0;
    return solve_quadratic0(vec3(1.0, b, c));
}



void draw_screen_line(float d) {
    move_to(d, -2.0);
    line_to(d, 2.0);
}

void paint() {
    float t = iTime;

    // clear screen with a subtle gradient
    set_source_linear_gradient(
        vec3(0.0,0.0,0.3),
        vec3(0.0,0.0,0.6),
        vec2(0.0,-1.0),
        vec2(0.0,1.0));
    clear();

    scale(0.5);

    set_source_rgb(vec3(1.0));
    set_line_width_px(1.3);

    circle(0.0, 0.0, 1.0);
    stroke();


    float apt = mix(0.5, 4.0, sin(t*0.12)*0.5+0.5);
    float hw = 2.0;
	vec2 eye = vec2(-2.0, 0.0) + vec2(sin(t),cos(t))*1.2;
    vec2 c = vec2(3.0, sin(t*0.1)*hw);
    vec2 c0 = vec2(c.x, c.y + apt);
    vec2 c1 = vec2(c.x, c.y - apt);

    vec2 v0 = normalize(c0 - eye);
    vec2 v1 = normalize(c1 - eye);
    vec2 t0 = iUnitCircle(eye, v0);
    vec2 t1 = iUnitCircle(eye, v1);

    bool i0 = (t0.x == t0.x);
    bool i1 = (t1.x == t1.x);

    move_to(eye);
    line_to(c0);
    set_source_rgb(mix(vec3(1.0,0.0,0.0),vec3(1.0),vec3(i0)));
    stroke();
    move_to(eye);
    line_to(c1);
    set_source_rgb(mix(vec3(1.0,0.0,0.0),vec3(1.0),vec3(i1)));
    stroke();

    vec2 q0 = eye.xx + v0.xx * t0;
    vec2 q1 = eye.xx + v1.xx * t1;

    vec2 b0 = vec2(-1.0, 0.0);
	vec2 b1 = vec2(1.0, 0.0);

    bool w00 = cross2d(v0, b0 - eye) > 0.0;
    bool w01 = cross2d(v1, b0 - eye) > 0.0;
    bool w10 = cross2d(v0, b1 - eye) > 0.0;
    bool w11 = cross2d(v1, b1 - eye) > 0.0;

    float outer_near = max(eye.x, (w00 != w01)?b0.x:min(q0.x, q1.x));
    float outer_far = max(eye.x, (w10 != w11)?b1.x:max(q0.y, q1.y));
    float inner_near = max(eye.x, max(q0.x, q1.x));
    float inner_far = max(eye.x, min(q0.y, q1.y));

    set_source_rgb(vec3(0.0,1.0,0.5));
    if ((w00 != w01) && (b0.x > eye.x)) {
    	circle(b0, 0.05);
    }
    if (i0 && (q0.x > eye.x)) {
    	circle(eye + v0 * t0.x, 0.05);
    }   
    if (i1 && (q1.x > eye.x)) {
    	circle(eye + v1 * t1.x, 0.05);
    }
    fill();   
    set_source_rgb(vec3(0.0,0.5,1.0));
    if (w10 != w11) {
    	circle(b1, 0.05);
    }
    if (i0) {
	    circle(eye + v0 * t0.y, 0.05);
    }
    if (i1) {
	    circle(eye + v1 * t1.y, 0.05);
    }
    fill();

    if (i0 || i1 || (w00 != w01) || (w10 != w11)) {
        set_source_rgba(0.0,1.0,0.5,0.5);
        draw_screen_line(outer_near);
        stroke();
        set_source_rgba(0.0,0.5,1.0,0.5);
        draw_screen_line(outer_far);
        stroke();    
        if (i0 && i1) {    
            set_source_rgba(0.0,1.0,0.5,1.0);
            draw_screen_line(inner_near);
            stroke();    
            set_source_rgba(0.0,0.5,1.0,1.0);
            draw_screen_line(inner_far);
            stroke();      
        }    
    }
}

