void mainImage(out vec4 o, in vec2 f )
{
    h *= 0.;
    o.xyz *= 0.;
    t = iTime*.5;
    vec2 R = iResolution.xy,
          uv  = vec2(f-R/2.) / R.y;
	vec3	dir = camera(uv);
    vec3	pos = vec3(.0, .0, 1.0);
    vec2	inter = (march(pos, dir));
    if (inter.y < 20.)
        o.xyz += vec3( abs(sin(t*1.+ii*.1+m+1.04)), abs(sin(t*1.+ii*.1+m+2.09)), abs(sin(t*1.+ii*.1+m+3.14)))*(1.-inter.x*.05);

    o.xyz += h;
}

float	scene(vec3 p)
{
    distanceToL = 1e3;
    float r2,k=1.;
    ii=0.;
    m = r2 = 1e5;
    aa = t*.025;
    p.z+=6.;
    rotate(p.zx, t+1.57+0.*iTime);
    rotate(p.zy, t+1.57+0.*iTime);

    for(float	i = -1.; i < I; ++i)
    {
        ++ii;
		//r2 = min(r2, sdTorus(p, vec2(.521,.12) )); // torus based variant, comment 2 next lines if using 
		r2= min(r2, sdHexPrism(p, vec2(.3,.3)) );
		distanceToL = sdHexPrism(p, vec2(.3, .0))*32.;
		aa=aa+.5/(i+2.);
        if (mod(i, 3.) == 0.)
        {
            ma = mat2(cos(aa+1.*ii*.25),sin(aa+1.*ii*.25), -sin(aa+1.*ii*.25), cos(aa+1.*ii*.25) );
	        p.xy*=ma;
	        p.xy = abs(p.xy)-.125;
			p.z -= .2;
        }
        else if (mod(i, 3.) == 1.)
        {
            ma = mat2(cos(aa*3.+1.04+1.*ii*.1),sin(aa*3.+1.04+1.*ii*.1), -sin(aa*3.+1.04+1.*ii*.1), cos(aa*3.+1.04+1.*ii*.1) );
	        p.yz*=ma;
	        p.zy = abs(p.zy)-.125;
            p.x -= .2;
        }
        else if (mod(i, 3.) == 2.)
        {
            ma = mat2(cos(aa*2.+2.08+1.*ii*.5),sin(aa*2.+2.08+1.*ii*.5), -sin(aa*2.+2.08+1.*ii*.5), cos(aa*2.+2.08+1.*ii*.5) );
            p.zx*=ma;
	        p.xz = abs(p.xz)-.125;
	       	p.y -= .2;
        }
	m = min(m, log(sdBox(p,vec3(.0612510))/(k*k) ) );
    k *= 1.125;
    }
    return r2;
}

vec3 evaluateLight(in vec3 pos)
{
    vec3	lightCol;
    lightCol = vec3(1.,.7,.2);
    return (
        	lightCol * 1.0/(distanceToL*distanceToL)
        	)*(.25);
}

vec2	march(vec3 pos, vec3 dir)
{
    vec2	dist = vec2(0.0);
    vec3	p = vec3(0.0);
    vec2	s = vec2(0.0);

    for (int i = 0; i < I_MAX; ++i)
    {
    	p = pos + dir * dist.y;
        dist.x = scene(p);
        dist.y += dist.x;
        h += evaluateLight(p);
        if (dist.x < E || dist.y > 20.)
        {
           break;
        }
        s.x++;
    }
    s.y = dist.y;
    return (s);
}

// Utilities

float	mylength(vec2 p)
{
    float	ret = 0.;
 	p = p*p*p*p;
    ret = pow(p.x+p.y, 1./4.);

    return (ret);
}

float sdTorus( vec3 p, vec2 t )
{
	vec2 q = vec2(length(p.zy)-t.x,p.x);

    return length(q)-t.y;
}

float sdHexPrism( vec3 p, vec2 h )
{
    vec3 q = abs(p);
    return max(q.z-h.y,max((q.x*0.866025+q.y*0.5),q.y)-h.x);
}

float sdBox( vec3 p, vec3 b )
{
  return length(max(abs(p)-b,0.0));
}


vec3	camera(vec2 uv)
{
    float   fov = 1.;
	vec3    forw  = vec3(0.0, 0.0, -1.0);
	vec3    right = vec3(1.0, 0.0, 0.0);
	vec3    up    = vec3(0.0, 1.0, 0.0);

    return (normalize((uv.x) * right + (uv.y) * up + fov * forw));
}

void rotate(inout vec2 v, float angle)
{
	v = vec2(cos(angle)*v.x+sin(angle)*v.y,-sin(angle)*v.x+cos(angle)*v.y);
}

#define MAX_STEPS 100
#define EPSILON 0.005
#define N_EPSILON 0.05
#define PI 3.14159265358979323846

#define ez vec3(0.0, 0.0, 1.0)

mat2 rot(float a) {
    float c = cos(a); float s = sin(a);
    return mat2(c, s, -s, c);
}


float blade(vec3 p, float a){

    p.xy *= rot(a); 
    p.xz *= rot(-clamp(0.3 + 0.03*p.y*p.y, 0.3, 0.6));

    float psy = p.y - 3.0;
    float ppy = p.y - 1.0;


    float xs = clamp(0.4 + -0.04*psy*psy, -1.5, 1.0);
    float xp = clamp( 2.0 + 0.02*ppy*ppy,  1.0, 2.5);


    return length(p - vec3(clamp(p.x, -2.0*xs, xp),
                           clamp(p.y, -3.0, 3.0 - 0.1*p.x*p.x),
                           0.0
                           )) - 0.1;
}

float centre(vec3 p){
    float rad = 2.5;
    float len = 0.7;
    vec2 d = abs(vec2(length(p.xy/rad), p.z/len) ) - 1.0 ;
  	return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}


float concav(vec3 p){
    float rad = 2.3;
    float len = 0.7;
    vec2 d = abs(vec2(length(p.xy/rad), p.z/len) ) - 1.0 ;
  	return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}


float d_sphere(vec3 p, vec3 pos, float r){
    return length(p - pos) - r;
}

float sceneSDF(vec3 p){

    p.z += 10.0;

    p.xz *= rot(iTime);
    p.xy *= rot(2.6*iTime);

    float a = 2.0*PI/7.0;
    mat2 R = rot(a);

    vec2 O1 = vec2(0.0, -3.5);
    vec2 O2 = R*O1;
    vec2 O3 = R*O2;
    vec2 O4 = R*O3;
    vec2 O5 = R*O4;
    vec2 O6 = R*O5;
    vec2 O7 = R*O6;


    float z_pos = 0.0;

    float d = min( 
        		   min( blade(p + vec3(O1, z_pos), 0.0*a),
                        blade(p + vec3(O2, z_pos), 1.0*a)) , 
                   min( blade(p + vec3(O3, z_pos), 2.0*a),
                        blade(p + vec3(O4, z_pos), 3.0*a)));

    	  d = min( min( d,
                       	blade(p + vec3(O5, z_pos), 4.0*a)),
                   min( blade(p + vec3(O6, z_pos), 5.0*a),
           				blade(p + vec3(O7, z_pos), 6.0*a)));

    d =  min(d,centre(p + vec3(0.0, 0.0, 0.7 + z_pos)));

    d = max(d, -concav(p + vec3(0.0, 0.0, 1.0 + z_pos)));

    vec2 p1 = vec2(0.2, 0.2);
    vec2 p2 = rot(2.0*PI/3.0)*p1;
    vec2 p3 = rot(2.0*PI/3.0)*p2;

    float d_logo = min(
        				min( length(p + vec3(p1, 0.0 + z_pos)) - 0.2,
                             length(p + vec3(p2, 0.0 + z_pos)) - 0.2),
                             length(p + vec3(p3, 0.0 + z_pos)) - 0.2);

    d = max(d, - d_logo);

    return d;

}




vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        sceneSDF(vec3(p.x + N_EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - N_EPSILON, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + N_EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - N_EPSILON, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z  + N_EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - N_EPSILON))
    ));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

	vec2 uv = fragCoord.xy - 0.5*iResolution.xy;
    uv *= 2.0/iResolution.y;

    uv *= 4.0;

    float fov = PI/20.0;
    vec3 cam_pos = vec3(0.0, 0.0, 1.0*(1.0 + 1.0/tan(fov)));

    vec3 ray = cam_pos;
    vec3 dir = normalize(vec3(uv.xy, 0.0) - cam_pos);

    vec3 light = normalize(vec3(0.5, 0.5, 1.0));

    float shade = 0.0;
    float shade_1 = 0.0;
    for (int i = 0; i < MAX_STEPS; i++){

        float dist = sceneSDF(ray);

        if ( dist < EPSILON ) {

            vec3 n = estimateNormal(ray);
            float soft = dot(light,n);
            float soft_s = soft - 1.0;
            shade += 0.8*soft;
            shade += clamp(exp(-soft*soft*10.0), 0.0, 1.0);
            shade += clamp(exp(-soft_s*soft_s*10000.0), 0.0, 1.0);

            break;
        }
        else if ( length(ray) > 30.0 )
            break;
        ray += 0.71*dir*dist;
    }



	fragColor = vec4(1.0, 0.9, 0.0, 0.0)*shade;

}

vec2 uv2ri( highp vec2 uv, float radius, highp vec2 offset )
{
    float r = uv.x * 2.0 * radius - radius + offset.x;
    float i = uv.y * 2.0 * radius - radius + offset.y;

    return vec2( r, i );
}



int mandelbrot( highp vec2 c )
{
    vec2 z = vec2( 0, 0 );

    for( int depth = 0; depth < 4001; depth++ )
    {
        z = vec2( z.x * z.x - z.y * z.y + c.x,
                  2.0 * z.x * z.y + c.y );
        if( length( z ) >= 2.0 )
        {
            return depth;
        }
    }
    return -1;
}

vec3 hsv2rgb(vec3 c) {
  vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 ir = uv2ri( uv, 2.0 / ( 1.0 + pow( 1.5, iTime ) / 2.0 ), vec2( -0.745428, 0.113009 ) );

    float c = float( mandelbrot( ir ) )/1000.0;
    if( c < 0.0 )
    {
        fragColor = vec4(0,0,0,1);
    }
    else
    {
        fragColor.rgb = hsv2rgb( vec3( c, 1, pow( c, 0.5) ) );
        fragColor.a = 1.0;
    }
}

float box(vec3 p,vec3 o,vec3 s)
{
    p=abs(p-o)-s;
    return max(p.x,max(p.y,p.z));
}

float hash(float n)
{
    return fract(sin(n)*43758.5453);
}

float noise(vec3 p)
{
    return hash(p.x + p.y*57.0 + p.z*117.);
}

float noise(vec2 p)
{
    return hash(p.x + p.y*57.0);
}

float valnoise(vec2 p)
{
    vec2 c=floor(p);
    vec2 f=smoothstep(0.,1.,fract(p));
    return mix (mix(noise(c+vec2(0,0)), noise(c+vec2(1,0)), f.x),
                mix(noise(c+vec2(0,1)), noise(c+vec2(1,1)), f.x), f.y);
}

float dist(vec3 p)
{
    float d=1e3;

    vec3 p2=p;
    float s=1.;
    for(int i=0;i<4;++i)
    {
        p2.xz=abs(p2.xz);   
        float cl=max(max(p2.x,p2.z),dot(p2.xz,normalize(vec2(1))));
        float r=20.;
        cl=min(cl-r+1.,max(box(vec3(p2.x,mod(p2.y,10.)-5.,p2.z),vec3(0),vec3(r,6.,r)),cl-r));
        cl=max(cl,p2.y-50.+float(i)*80.);
        if(i==3)cl=max(cl,p.y+25.);
        cl/=s;
        if(i==2)cl=max(cl,dot(p.yz-vec2(0,25),normalize(vec2(1))));
        d=min(d,cl);
        p2*=2.;
        p2.xz=mod(p2.xz,100.)-50.;
        s*=2.;
    }

    p2=abs(p-vec3(74,0,-35));
    float cl=max(max(p2.x,p2.z),dot(p2.xz,normalize(vec2(1))))-6.;
    d=min(d,cl);

    p2=p;

    d=min(d,distance(p,vec3(0,50,0))-14.);

    {
        vec3 p2=p;
        p2.xz=mod(p2.xz,10.)-5.;
        p2.xz=abs(p2.xz);
        d=min(d,p.y+52.+max(p2.x,p2.z));
    }

    {
        vec3 p2=p;
        p2.xz=mod(p2.xz,80.)-40.;
        p2.xz=abs(p2.xz);
        d=min(d,p.y+41.+max(p2.x,p2.z));
    }

    d=min(d,max(p.y+15.,p.z+60.));

    d=min(d,max(box(p,vec3(0,-70,0),vec3(31)), -box(p,vec3(0,-25,0),vec3(20))));

    d=min(d,box(p,vec3(0,30,19),vec3(15,9,2)));

    d=max(d,box(p,vec3(0),vec3(85,64,67)));


    p2=p;
    for(int i=0;i<4;++i)
    {
        vec3 p3=p2;
        p2.y-=cos(float(i)*9.)*23.;
        p3.xz=min(p3.xz,1.);
        float rr=distance(p3.xz,normalize(p3.xz)*27.)-5.;
        d=min(d,max(rr,abs(p3.y)-2.));
        p2.xz=p2.zx*vec2(1,-1);
    }

    d=max(d,box(p,vec3(0),vec3(85,64,67)+40.));


    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 t = fragCoord.xy / iResolution.xy-.5;
	t.x*=iResolution.x/iResolution.y;
    t*=1.4;

    vec3 fog=vec3(.5,.5,1);

    vec2 p=t.xy;
    vec2 ot=t;

    vec3 backg=vec3(0.75,1,.75)/3.*exp(-length(ot.xy)/2.);

    vec3 ro=vec3(0,-20,380.);
    vec3 rd=vec3(p,-3.);


    {
        float ang=0.3-iMouse.y/iResolution.y*.5;
        ro.yz*=mat2(cos(ang),sin(ang),-sin(ang),cos(ang));
        rd.yz*=mat2(cos(ang),sin(ang),-sin(ang),cos(ang));
    }


    {
        float ang=0.6-iMouse.x/iResolution.x*.5;
        ro.xz*=mat2(cos(ang),sin(ang),-sin(ang),cos(ang));
        rd.xz*=mat2(cos(ang),sin(ang),-sin(ang),cos(ang));
    }

    ro+=rd*90.;

    vec3 co=ro;
    rd=normalize(rd);
    vec3 ord=rd;

    vec3 f=vec3(1.),a=vec3(0.);
    float s=1.;
    vec3 n;
    for(int j=0;j<6;++j)
    {
        vec3 oro=ro;

        float t=0.;
        for(int i=0;i<20;++i)
        {
            ro=oro+rd*t;
            float d=dist(ro);
            if(d<1.-4.)
            {
                break;
            }
            t+=d;
        }
        if(t>200.)
        {
            fragColor.rgb=backg;
            fragColor.rgb+=noise(gl_FragCoord.xy)/200.;

            return;
        }



        oro=ro;
        vec3 c=floor(ro);
        vec3 ts=(c+max(vec3(0.),sign(rd))-ro)/rd;

        for(int i=0;i<25;++i)
        {
            n=step(ts,ts.yzx)*step(ts,ts.zxy);
            c+=sign(rd)*n;
            float d=dist(c);
            vec3 dd=sign(rd)/rd*n;
            if(d<-1.7)
            {
                ro=oro+rd*min(ts.x,min(ts.y,ts.z));
                break;
            }
            ts+=dd;
        }
        ro=oro+rd*min(ts.x,min(ts.y,ts.z));
    }

    float fogstrength=.0001;
    vec3 ld=normalize(vec3(6,-2,1.4));
    float sh=1.;
    {
        ro-=rd*1e-5;
        vec3 oro=ro;
        vec3 rd=ld;
        vec3 c=floor(ro);
        vec3 ts=(c+max(vec3(0.),sign(rd))-ro)/rd,n;

        for(int i=0;i<4;++i)
        {
            n=step(ts,ts.yzx)*step(ts,ts.zxy);
            c+=sign(rd)*n;
            float d=dist(c);
            vec3 dd=sign(rd)/rd*n;
            if(d<-1.7)
            {
                sh=0.;
                break;
            }
            ts+=dd;
        }
    }

    vec3 rp=floor(ro);
    fragColor.rgb=exp(-(rp.y+68.)/8.)*vec3(.5,1.,1.)*2.*mix(.8,1.,.5+.5*cos(iTime*17.));

    fragColor.rgb+=((.75+.25*dot(n*-sign(rd),ld)))*mix(.5,1.,sh)*.01;

    fragColor.rgb+=smoothstep(.96,.97,abs(cos(rp.x)*cos(rp.y+5.)*cos(rp.z)));
    fragColor.rgb+=.1*smoothstep(.96,.97,abs(cos(rp.x*8.)*cos(rp.y+5.)*cos(rp.z*9.)));

    float g=mod(rp.x+floor(rp.y/20.)*4.,40.);
    fragColor.rgb+=.01*step(abs(mod(rp.y,20.)-clamp(min(g,40.-g),10.,15.)),.5);

    if(length(rp.xz)<18.5)
        fragColor.rgb+=.003;

    fragColor.rgb+=(1.-smoothstep(0.,10.,distance(rp,vec3(0.,50.,0.))-14.))*vec3(.5,1,1)*.03;
    fragColor.rgb+=(1.-smoothstep(0.,1.,distance(rp,vec3(0.,50.,0.))-14.))*vec3(.5,1,1)*.5*clamp((rp.y-50.)/50.,0.,1.);

    if(rp.y>60.)
        fragColor.rgb+=.2;

    if(rp.y<-45.)
        fragColor.rgb+=step(.9,noise(rp))*.1;

    {
        float sd=box(rp,vec3(0,30,19),vec3(15,8,2));
        if(sd<0.)
            fragColor.rgb*=.1;
    }

    vec3 screencolor=vec3(1,1,.7);
    {
        float sd=box(rp,vec3(0,30,19),vec3(12,6,2));
        vec3 rp2=rp-vec3(0,30,19);
        if(sd<0.)
            fragColor.rgb+=.5*step(1.,abs(mod(rp2.y-cos(rp2.x/1.5+iTime)*2.,5.)))*screencolor;
    }

    fragColor.rgb+=mix(.8,1.,.5+.5*cos(iTime*7.))*screencolor*10./pow(distance(rp,vec3(0,30,15)),2.)*smoothstep(0.,16.,rp.z-17.);


    float fogamount=exp(-distance(ro,co)*fogstrength/3.);
    fragColor.rgb=mix(vec3(.5,1,.5),fragColor.rgb,fogamount);

    fragColor.rgb=sqrt(fragColor.rgb)*1.5;

    fragColor.rgb=mix(fragColor.rgb,backg,smoothstep(105.,125.,length(rp.xz)));

    fragColor.rgb+=noise(gl_FragCoord.xy)/200.;

}

