mat3 RotZ(float a){return mat3(cos(a),-sin(a),0.,sin(a),cos(a),0.,0.,0.,1.);}

vec2 path(float t)
{
	return vec2(cos(t*0.2), sin(t*0.2)) * 2.;
}

float coefFromRGB(vec3 rgb)
{
	vec3 wl = vec3(564.,533.,437.);
	return length(wl * rgb) / length(wl);
}

vec2 df(vec3 p)
{
	float plane = p.y + 2.;
	p *= RotZ(p.z*0.3);
	p += sin(p.zxy*0.8)*2. + sin(p.xzy * 0.8)*2.;
    p *= RotZ(p.z*-0.8);
    float d = length(p)-3.;
	vec2 res = vec2(d, 2.);
	return res;
}

vec3 nor( in vec3 pos, float prec )
{
	vec3 eps = vec3( prec, 0., 0. );
	vec3 nor = vec3(
	    df(pos+eps.xyy).x - df(pos-eps.xyy).x,
	    df(pos+eps.yxy).x - df(pos-eps.yxy).x,
	    df(pos+eps.yyx).x - df(pos-eps.yyx).x );
	return normalize(nor);
}

// return color from temperature 
//http://www.physics.sfasu.edu/astro/color/blackbody.html
//http://www.vendian.org/mncharity/dir3/blackbody/
//http://www.vendian.org/mncharity/dir3/blackbody/UnstableURLs/bbr_color.html
vec3 blackbody(float Temp)
{
	vec3 col = vec3(255.);
    col.x = 56100000. * pow(Temp,(-3. / 2.)) + 148.;
   	col.y = 100.04 * log(Temp) - 623.6;
   	if (Temp > 6500.) col.y = 35200000. * pow(Temp,(-3. / 2.)) + 184.;
   	col.z = 194.18 * log(Temp) - 1448.6;
   	col = clamp(col, 0., 255.)/255.;
    if (Temp < 1000.) col *= Temp/1000.;
   	return col;
}

// get density of the df at surfPoint
// ratio between constant step and df value
float SubDensity(vec3 surfPoint, float prec, float ms) 
{
	vec3 n;
	float s = 0.;
    const int iter = 10;
	for (int i=0;i<iter;i++)
	{
		n = nor(surfPoint,prec); 
		surfPoint = surfPoint - n * ms; 
		s += df(surfPoint).x;
	}

	return 1.-s/(ms*float(iter)); // s < 0. => inside df
}

float SubDensity(vec3 p, float s) 
{
	vec3 n = nor(p,s); 							// precise normale at surf point
	return df(p - n * s).x;						// ratio between df step and constant step
}

// from shane sahders
// Tri-Planar blending function. Based on an old Nvidia writeup:
// GPU Gems 3 - Ryan Geiss: http://http.developer.nvidia.com/GPUGems3/gpugems3_ch01.html
vec3 tex3D( sampler2D tex, in vec3 p, in vec3 n )
{
    n = max((abs(n) - .2)*7., .001);
    n /= (n.x + n.y + n.z );  
	p = (texture(tex, p.yz)*n.x + texture(tex, p.zx)*n.y + texture(tex, p.xy)*n.z).xyz;
    return p*p;
}

vec3 shade(vec3 ro, vec3 rd, float d, vec3 lp, float li)
{
	vec3 p = ro + rd * d;											// surface point
	float sb = SubDensity(p, 0.01, 0.076);							// deep subdensity (10 iterations)
	vec3 bb = blackbody(100.*sb+100.);								// bb
	vec3 ld = normalize(lp-p); 										// light dir
	vec3 n = nor(p, 0.1);	// normal at surface point

    // derived from bumpmap func from shane
    const vec2 e = vec2(0.1, 0);
    mat3 m = mat3( tex3D(iChannel0, e.xyy, n), tex3D(iChannel0, e.yxy, n), tex3D(iChannel0, e.yyx, n));
   	vec3 g = vec3(1) * m * 20.;
    g -= n * dot(n, g);
    n =  normalize( n + g );

	vec3 refl = reflect(rd,n);										// reflected ray dir at surf point 
	float amb = 0.1242; 												// ambiance factor
	float diff = clamp( dot( n, ld ), 0.0, 1.0 ); 					// diffuse
	float fre = pow( clamp( 1. + dot(n,rd),0.0,1.0), 4. ); 			// fresnel
	float spe = pow(clamp( dot( refl, ld ), 0.0, 1.0 ),16.);		// specular
	float sss = 1. - SubDensity(p, 7.8); 							// one step sub density of df
	vec3 col = (diff + fre + bb * sb * 0.608 + sss * 0.352) * amb * li + spe * 0.612;

	return mix(col, vec3(1), vec3(coefFromRGB(bb*0.8+0.1*diff + 0.1 *spe)));
}

vec3 cam(vec2 uv, vec3 ro, vec3 cv, float t)
{
	vec3 cu = normalize(vec3(0,1,0));
  	vec3 z = normalize(cv-ro);
    vec3 x = normalize(cross(cu,z));
  	vec3 y= cross(z,x);
  	return normalize(z + uv.x*x + uv.y*y);
}

void mainImage( out vec4 fragColor, vec2 fragCoord )
{
    float t = -iTime*2.;

    vec2 si = iResolution.xy;
    vec2 uv = (2.*fragCoord.xy-si)/si.y;

    vec3 col = vec3(1);

	float ca = iTime;
	float ce = 0.5;
	float cd = 10.;

	vec3 ro = vec3(cos(ca), sin(ce), sin(ca)) * cd;
  	vec3 cv = vec3(0);
	vec3 rd = cam(uv, ro, cv, t);

	float md = 20.;
    float s = 1.;
    float d = 1.;

	const float iter = 250.;
    for(float i=0.;i<iter;i++)
    {      
        if (0.<log(d*d/s/1e5)||d>md) break;
        s = df(ro+rd*d).x;
		d += s *0.2;
    }

	if (d<md)
	{
		vec3 p = ro+rd*d;

		col = shade(ro, rd, d, ro, 1.2);
	}
	else
    {
        col = vec3(0);
    }   

	col = mix(col, vec3(0), 1.-exp(-0.005*d*d));

	fragColor.rgb = col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = (fragCoord.xy - .5*iResolution.xy) / iResolution.y;
    float t = 1.7 * iTime;
    float v = 0.0;
    for (float i = 0.05; i < 1.0; i += 0.1) {
        vec2 p = uv - i*vec2(cos(i*t), sin(i*t));
    	v += 0.005 / max(1e-6, dot(p, p));
    }
    v = 0.98 * sqrt(min(v, 1.0));
	fragColor = vec4(v);
}

float sdfCircle(vec2 center, float radius, vec2 coord){
    vec2 offset = coord - center;
    return length(offset) - radius;
}

float sdfLine(vec2 p0, vec2 p1, float width, vec2 coord) {
    vec2 dir0 = p1 - p0;
    vec2 dir1 = coord - p0;
    float h = clamp(dot(dir0, dir1)/dot(dir0, dir0), 0.0, 1.0);
    return (length(dir1 - dir0 * h) - width * 0.5);
}

float sdfUnion( const float a, const float b ) {
    return min(a, b);
}

float sdfDifference( const float a, const float b) {
    return max(a, -b);
}

float sdfIntersection( const float a, const float b ) {
    return max(a, b);
}

vec4 render(float d, vec4 color){
    vec4 target;
    float anti = fwidth(d);
    float s = smoothstep(-anti, anti, d);
    target = vec4(color.rgb, 1.0-s);
    return target;
}

float drawRing(vec4 fragColor, vec2 fragCoord, vec2 center){
    vec2 uv = fragCoord / iResolution.xy;
    float maxRadius = min(iResolution.x, iResolution.y*0.8) / 2.0;
    float ringRadius =  min(iResolution.x, iResolution.y*0.7) / 2.0;
	float d1 = sdfCircle(center, maxRadius, fragCoord);
    float d2 = sdfCircle(center, ringRadius, fragCoord);
    return sdfDifference(d1, d2);
}

float drawSupport(vec2 fragCoord, vec2 center){
    float d1 = sdfLine(center, vec2(iResolution.x*0.3, 0.0), 5.0, fragCoord);
    float d2 = sdfLine(center, vec2(iResolution.x*0.7, 0.0), 5.0, fragCoord);
    return sdfUnion(d1, d2);
}

float drawSroll(vec2 fragCoord, vec2 center){
    float pi = 3.1415926;
    float maxRadius = min(iResolution.x, iResolution.y*0.8) / 2.0;
    float radian = iTime * (pi/1.0);
    vec2 offset = vec2(maxRadius*cos(radian), maxRadius*sin(radian));
    float d1 = sdfLine(center-offset, center+offset, 0.1, fragCoord);

    vec2 offset2 = vec2(maxRadius*cos(radian+pi/3.0), maxRadius*sin(radian+pi/3.0));
    float d2 = sdfLine(center-offset2, center+offset2, 0.1, fragCoord);

    vec2 offset3 = vec2(maxRadius*cos(radian+pi/1.5), maxRadius*sin(radian+pi/1.5));
    float d3 = sdfLine(center-offset3, center+offset3, 0.1, fragCoord);

    float u1 = sdfUnion(d1, d2);
    return sdfUnion(d3, u1);

}

float drawMouse(vec2 coord){
	float bodyUp = sdfCircle(vec2(iResolution.x/2.0-iResolution.y*0.025, iResolution.y*0.26), iResolution.y*0.035, coord);
    float bodyDown = sdfCircle(vec2(iResolution.x/2.0, iResolution.y*0.2), iResolution.y*0.05, coord);

    float move = mod(iTime * 30.0, 8.0) * iResolution.x*0.002;
    vec2 pointA = vec2(iResolution.x/2.0, iResolution.y*0.2);
    vec2 pointB =  pointA + vec2(move, -iResolution.y*0.08);
    vec2 pointC = pointA + vec2(-move, -iResolution.y*0.08);
    float w = iResolution.y*0.01;

    float lLeg = sdfLine(pointA, pointB, w, coord);
    float rLeg = sdfLine(pointA, pointC, w, coord);

    float target = sdfUnion(bodyUp, bodyDown);
    target = sdfUnion(target, lLeg);
    target = sdfUnion(target, rLeg);
    return target;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 center = iResolution.xy * .5;

	float d = drawRing(fragColor, fragCoord, center);    
    vec4 layer0 = render(d, vec4(0, 1, 0, 1));

    d = drawSupport(fragCoord, center);
    vec4 layer1 = render(d, vec4(1, 0, 0, 1));

    d = drawSroll(fragCoord, center);
    vec4 layer2 = render(d, vec4(1, 1, 0, 1));

