void initCamera( 	in vec3 pos,
                	in vec3 target,
                	in vec3 upDir,
                	in float fovV
               ) {
	vec3 back = normalize( pos-target );
	vec3 right = normalize( cross( upDir, back ) );
	vec3 up = cross( back, right );
    camera.rotate[0] = right;
    camera.rotate[1] = up;
    camera.rotate[2] = back;
    camera.fovV = fovV;
    camera.pos = pos;
}

void updateCamera( int strata ) {
    float strataSize = 1.0/float(PIXEL_SAMPLES);
    float r1 = strataSize*(float(strata)+rnd());
    //update camera pos
    float cameraZ = 4.0;
    vec3 upDir = vec3( 0.0, 1.0, 0.0 );
    vec3 pos1, pos2;
    pos1 = vec3( sin(iTime*0.154)*2.0, 2.0 + sin(iTime*0.3)*2.0, cameraZ + sin(iTime*0.8) );

    camera.pos = pos1;

    vec3 target = vec3( sin(iTime*0.4)*0.3, 1.0, -5.0 );

	vec3 back = normalize( camera.pos-target );
	vec3 right = normalize( cross( upDir, back ) );
	vec3 up = cross( back, right );
    camera.rotate[0] = right;
    camera.rotate[1] = up;
    camera.rotate[2] = back;
}

Ray genRay( in vec2 pixel, in float Xi1, in float Xi2 ) {
    Ray ray;
	vec2 iPlaneSize=2.*tan(0.5*camera.fovV)*vec2(iResolution.x/iResolution.y,1.);
	vec2 ixy=(pixel/iResolution.xy - 0.5)*iPlaneSize;
    ray.origin = camera.pos;
    ray.dir = camera.rotate*normalize(vec3(ixy.x,ixy.y,-1.0));
	return ray;
}

bool intersectPlane(vec3 plane_n, vec3 plane_p, vec3 ray_o, vec3 ray_d, out float t) { 
    // assuming vectors are all normalized
    float denom = dot(plane_n, ray_d); 
    if (abs(denom) > 1e-7) { 
        vec3 vec = plane_p - ray_o; 
        t = dot(vec, plane_n) / denom; 
        return (t >= 0.0); 
    } 

    return false; 
}

vec2 angular_to_cartesian(float phi) {
    return vec2(cos(phi), sin(phi));
}

float cartesian_to_angular(vec2 w) {
	return atan(float(-w.y), float(-w.x)) + PI;
}

struct SphQuad {
    vec3 o, x, y, z; // local reference system R
    float z0, z0sq; //
    float x0, y0, y0sq; // rectangle coords in R
    float x1, y1, y1sq; //
    float b0, b1, b0sq, k; // misc precomputed constants
    float S; // solid angle of Q
};

void SphQuadInit(vec3 s,vec3 ex,vec3 ey,vec3 o, out SphQuad squad) {
    squad.o = o;
    float exl = length(ex), eyl = length(ey);
    // compute local reference system R
    squad.x = ex / exl;
    squad.y = ey / eyl;
    squad.z = cross(squad.x, squad.y);
    // compute rectangle coords in local reference system
    vec3 d = s - o;
    squad.z0 = dot(d, squad.z);
    // flip z to make it point against Q
    if (squad.z0 > 0.) {
    squad.z *= -1.;
    squad.z0 *= -1.;
    }
    squad.z0sq = squad.z0 * squad.z0;
    squad.x0 = dot(d, squad.x);
    squad.y0 = dot(d, squad.y);
    squad.x1 = squad.x0 + exl;
    squad.y1 = squad.y0 + eyl;
    squad.y0sq = squad.y0 * squad.y0;
    squad.y1sq = squad.y1 * squad.y1;
    // create vectors to four vertices
    vec3 v00 = vec3(squad.x0, squad.y0, squad.z0);
    vec3 v01 = vec3(squad.x0, squad.y1, squad.z0);
    vec3 v10 = vec3(squad.x1, squad.y0, squad.z0);
    vec3 v11 = vec3(squad.x1, squad.y1, squad.z0);
    // compute normals to edges
    vec3 n0 = normalize(cross(v00, v10));
    vec3 n1 = normalize(cross(v10, v11));
    vec3 n2 = normalize(cross(v11, v01));
    vec3 n3 = normalize(cross(v01, v00));
    // compute internal angles (gamma_i)
    float g0 = acos(-dot(n0,n1));
    float g1 = acos(-dot(n1,n2));
    float g2 = acos(-dot(n2,n3));
    float g3 = acos(-dot(n3,n0));
    // compute predefined constants
    squad.b0 = n0.z;
    squad.b1 = n2.z;
    squad.b0sq = squad.b0 * squad.b0;
    squad.k = 2.*PI - g2 - g3;
    // compute solid angle from internal angles
    squad.S = g0 + g1 - squad.k;
}

void SphQuadSample(in vec3 x, SphQuad squad, float u, float v, out vec3 w, out float pdf) {
    // 1. compute cu
    float au = u * squad.S + squad.k;
    float fu = (cos(au) * squad.b0 - squad.b1) / sin(au);
    float cu = 1./sqrt(fu*fu + squad.b0sq) * (fu>0. ? +1. : -1.);
    cu = clamp(cu, -1., 1.); // avoid NaNs
    // 2. compute xu
    float xu = -(cu * squad.z0) / sqrt(1. - cu*cu);
    xu = clamp(xu, squad.x0, squad.x1); // avoid Infs
    // 3. compute yv
    float d = sqrt(xu*xu + squad.z0sq);
    float h0 = squad.y0 / sqrt(d*d + squad.y0sq);
    float h1 = squad.y1 / sqrt(d*d + squad.y1sq);
    float hv = h0 + v * (h1-h0), hv2 = hv*hv;
    float yv = (hv2 < 1.-EPSILON) ? (hv*d)/sqrt(1.-hv2) : squad.y1;
    // 4. transform (xu,yv,z0) to world coords

    vec3 p = (squad.o + xu*squad.x + yv*squad.y + squad.z0*squad.z);
    w = normalize(p - x);
    pdf = 1. / squad.S;
}

void update_x_bounds(vec3 pos, vec3 n, inout vec2 pmin, inout vec2 pmax) {
    vec2 p1 = pmin;
    vec2 p2 = vec2(pmin.x, pmax.y);
    if(n.x != 0.0) {
        float tmin = (pos.z*n.z - (p1.y - pos.y)*n.y)/n.x - p1.x + pos.x;
        float tmax = (pos.z*n.z - (p2.y - pos.y)*n.y)/n.x - p2.x + pos.x;

        float minx = clamp(pmin.x + tmin, pmin.x, pmax.x);
        float maxx = clamp(pmin.x + tmax, pmin.x, pmax.x);

        if(n.x > 0.0) {
            pmin.x = min(minx, maxx);
        } else {
            pmax.x = max(minx, maxx);
        }
    }
}

void update_y_bounds(vec3 pos, vec3 n, inout vec2 pmin, inout vec2 pmax) {
    vec2 p1 = pmin;
    vec2 p2 = vec2(pmax.x, pmin.y);
    //pos = pos.xzy;
    if(n.y != 0.0){
        float tmin = (pos.z*n.z - (p1.x - pos.x)*n.x)/n.y - p1.y + pos.y;
        float tmax = (pos.z*n.z - (p2.x - pos.x)*n.x)/n.y - p2.y + pos.y;

        float miny = clamp(pmin.y + tmin, pmin.y, pmax.y);
        float maxy = clamp(pmin.y + tmax, pmin.y, pmax.y);

        if(n.y > 0.0) {
            pmin.y = min(miny, maxy);
        } else {
            pmax.y = max(miny, maxy);
        }
    }
}

void sampleArea(in vec3 pos, vec2 pmin, vec2 pmax, out vec3 w, out float pdfW) {
    vec2 size = pmax - pmin;
    float pdfA = 1.0 / (size.x*size.y);
    vec3 p = vec3(pmin + size*vec2(rnd(), rnd()), 0.0);
    w = p - pos;
    float d2 = dot(w,w);
    w /= sqrt(d2);
    float aCosThere = max(0.0,-w.z);
    pdfW = PdfAtoW( pdfA, d2, aCosThere );
}

void sampleDirectLight( vec3 pos,
                       	vec3 normal,
                        float Xi1,
                        float Xi2, 
                       	out vec3 dir,
                       	out float pdf ) {
    float height = objects[0].params_[2] - objects[0].params_[1];
    float r = objects[0].params_[0];
    float pdfA;
    float d2;
    float aCosThere;
    float theta;
    float thetaPdf;
    float h;
    float hPdf;

    //convert position to object space
    pos = toVec3( objects[0].transform_inv_*vec4(pos, 1.0) );
    normal = toVec3( objects[0].transform_inv_*vec4(normal, 0.0) );

    if(pos.z > 0.0) { 
        vec2 pmin = vec2(objects[0].params_[0], objects[0].params_[1]);
        vec2 pmax = vec2(objects[0].params_[2], objects[0].params_[3]);

#ifdef LIGHT_CLIPPING
        update_x_bounds(pos, normal, pmin, pmax);
        update_y_bounds(pos, normal, pmin, pmax);
#endif
        vec3 w[CDF_SIZE];
        float ww[CDF_SIZE];
        if(samplingTechnique == SAMPLE_TOTAL_AREA){
            for(int i=0; i<CDF_SIZE; i++) {
            	sampleArea(pos, pmin, pmax, w[i], pdf);
                ww[i] = max(0.0, dot(w[i], normal)) / pdf;
            }
        } else {
            vec3 s = vec3(pmin, 0.0);
            vec3 ex = vec3(pmax.x - pmin.x, 0., 0.);
            vec3 ey = vec3(0., pmax.y - pmin.y, 0.);
            SphQuad squad;
            SphQuadInit(s, ex, ey, pos, squad);

            for(int i=0; i<CDF_SIZE; i++) {
                SphQuadSample(pos, squad, rnd(), rnd(), w[i], pdf);
                ww[i] = max(0.0, dot(w[i], normal)) / pdf;
            }
        }

        for(int i=1; i<CDF_SIZE; i++) {
            ww[i] += ww[i-1];
        }

        float a = Xi1 * ww[CDF_SIZE-1];
        for(int i=0; i<CDF_SIZE; i++) {
            if(ww[i] > a) {
                pdf *= (ww[i] - ((i == 0)? 0.0 : ww[i-1])) / ww[CDF_SIZE-1];
                pdf *= float(CDF_SIZE);
                dir = w[i];
                break;
            }
        }
    } else {
        pdf = 0.0;
    }

    //convert dir to world space
    dir = toVec3( objects[0].transform_*vec4(dir,0.0) );
}

bool isLightVisible( Ray shadowRay ) {
    float distToHit;
    SurfaceHitInfo tmpHit;

    raySceneIntersection( shadowRay, EPSILON, true, tmpHit, distToHit );

    return ( tmpHit.material_id_ == MTL_LIGHT );
}


vec3 Radiance( in Ray ray, float Xi ) {
    vec3 L = vec3(4.0);
    vec3 Lo = vec3( 0.0 );

    vec3 Wo = ray.dir*(-1.0);
    SurfaceHitInfo hit;
    float dist = 1000.0;

    if( raySceneIntersection( ray, 0.0, false, hit, dist ) ) {
        if( hit.material_id_ == MTL_LIGHT ) {
            Lo = (dot(hit.normal_, Wo) > 0.0)? L : vec3(0.0, 0.0, 0.0);
        } else {
            for(int i=0; i<LIGHT_SAMPLES; i++){
                vec3 Wi;
                float pdfWi;
                vec3 n = hit.normal_ * ((dot(hit.normal_, Wo) > 0.0 )? 1.0 : -1.0);

            	float Xi1 = rnd();
            	float Xi2 = rnd();
#ifdef STRATIFIED_SAMPLING
                float strataSize = 1.0 / float(LIGHT_SAMPLES);
                Xi2 = strataSize * (float(i) + Xi2);
#endif

                sampleDirectLight( hit.position_, n, Xi1, Xi2, Wi, pdfWi );
                float dotNWi = dot( Wi, n );

                if ( (pdfWi > EPSILON) && (dotNWi > 0.0) ) {
                    bool visible = true;
#ifdef SHADOWS
                    Ray shadowRay = Ray( hit.position_ + n*EPSILON, Wi );
                    if ( !isLightVisible( shadowRay ) ) {
                        visible = false;
                    }
#endif
                    if(visible) {
                        float brdf_pdf;

                        vec3 brdf = vec3(1.0/PI);//max(0.0, dot(Wi, hit.normal_))*vec3(1.0);//Material_Evaluate( hit, Wo, Wi );
						vec3 Li = L/pdfWi;
                        Lo += (brdf*Li*abs(dotNWi));
                    }
                }
            }
            Lo *= 1.0/float(LIGHT_SAMPLES);

        }
    }

    return Lo;
}

void initSamplingTechnique(float p) {
    float split = iMouse.x;
    split = (split == 0.0)? iResolution.x * 0.5 : split;
    float k = iMouse.x/iResolution.x;
    float split1 = iMouse.x*k;
    float split2 = iMouse.x + (iResolution.x-iMouse.x)*k;

    if(p < split-1.0) {
        samplingTechnique = SAMPLE_TOTAL_AREA;
    } else if(p > split+1.0){
        samplingTechnique = SAMPLE_SPHERICAL_RECTANGLE;
    } else {
        samplingTechnique = SAMPLE_NONE;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    seed = /*iTime +*/ iResolution.y * fragCoord.x / iResolution.x + fragCoord.y / iResolution.y;
    initSamplingTechnique(fragCoord.x);

    if(samplingTechnique == SAMPLE_NONE) {
        fragColor = vec4( 1.0 );
    } else {
        float fov = radians(45.0);
        initCamera( vec3( 0.0, 0.0, 0.0 ),
                    vec3( 0.0, 0.0, 0.0 ),
                    vec3( 0.0, 1.0, 0.0 ),
                    fov
                    );

        initScene();

        vec3 accumulatedColor = vec3( 0.0 );
        float oneOverSPP = 1.0/float(PIXEL_SAMPLES);
        float strataSize = oneOverSPP;
        Ray ray;

        for( int si=0; si<PIXEL_SAMPLES; ++si ){
            updateCamera( si );

            vec2 screenCoord = fragCoord.xy + vec2( strataSize*( float(si) + rnd() ), rnd() );
            ray = genRay( screenCoord, rnd(), rnd() );

            if( length( ray.dir ) < 0.2 ) {
                accumulatedColor = vec3( 0.0 );
            } else {
                accumulatedColor += Radiance( ray, strataSize*( float(si) + rnd() ) );
            }
        }

        //devide to sample count
        accumulatedColor = accumulatedColor*oneOverSPP;

        //gamma correction
        accumulatedColor = pow( accumulatedColor, vec3( 1.0 / GAMMA ) );


        fragColor = vec4( accumulatedColor,1.0 );
    }
}

const float g_fade_time = 256. * 64.;
const float g_pixel_clock = 512.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scale = 1. / max(1., floor(min(iResolution.x / (192. + 256.), iResolution.y / 192.)));
	vec2 pixel = floor(scale * vec2(fragCoord.x, iResolution.y - fragCoord.y));

    float counter = iTime * g_pixel_clock;

    fragColor = vec4(0., 0., 0., 1.);

    float dram_row = pixel.y;
    float dram_column = pixel.x;
    if (dram_row < 128. && dram_column < 128.)
    {
        float dram_cell_counter = -1.;
        if (dram_column < 48.)
        {
            float display_byte_index
                = mod(dram_row, 32.)
                + 32. * mod(floor(dram_column / 2.), 8.)
                + 256. * mod(floor(dram_row / 32.), 4.)
                + 1024. * mod(dram_column, 2.)
                + 2048. * mod(floor(dram_column / 16.), 4.);
            dram_cell_counter = 4. * mod(display_byte_index, 2.) + 16. * floor(display_byte_index / 2.);
            dram_cell_counter += 256. * 192. * floor((counter - dram_cell_counter) / (256. * 192.));
        }
        else if (dram_column < 54.)
        {
            float attribute_byte_index
                = mod(dram_row, 32.)
                + 256. * mod(floor(dram_row / 32.), 4.)
                + 1024. * mod(dram_column, 8.);
            dram_cell_counter = 4. * mod(attribute_byte_index, 2.) + 16. * floor(attribute_byte_index / 2.) + 2.;
            dram_cell_counter += 256. * 192. * floor((counter - dram_cell_counter) / (256. * 192.));
            dram_cell_counter += 256. * min(7., floor((counter - dram_cell_counter) / 256.));
        }
        fragColor = vec4(0., 0., 0.5, 1.);
        if (dram_cell_counter != -1.)
        {
            float time_since_access = counter - dram_cell_counter;
            float value = max(0., 1. - time_since_access / g_fade_time);
            fragColor = mix(vec4(0., 0., 0.5, 1.), vec4(1., 1., 0., 1.), value);
        }
    }

    float screen_row = pixel.y;
    float screen_column = pixel.x - 192.;
    if (0. <= screen_row && screen_row < 192. && 0. <= screen_column && screen_column < 256.)
    {
        float screen_pixel_counter = screen_row * 256. + screen_column;
        screen_pixel_counter += 256. * 192. * floor((counter - screen_pixel_counter) / (256. * 192.));
        float time_since_update = counter - screen_pixel_counter;
        float value = max(0., 1. - time_since_update / g_fade_time);
        fragColor = mix(vec4(0., 0., 0.5, 1.), vec4(1., 1., 0., 1.), value);
    }
}

float PI = 3.1415926535;
vec3 col = vec3(1.0, 1.0, 1.0);
int stop = 0;
vec3 stoppos = vec3(0.0);
float map(vec3 p){
    vec3 p1 = p;
    p1.y -= clamp(p.y, 0.0, 2.0);
    float d = max(length(p1 - vec3(-2.0, 0.0, 0.0)) - 1.0, length(max(abs(p - vec3(-2.0, 1.0, 0.0)) - vec3(1.0, 1.0, 1.0),0.0)));
    d = min(d, dot(p,vec3(0.0, 1.0, 0.0)) + 1.0);
    return d;
}
vec3 grad(vec3 p) {
    vec2 d = vec2(0.0, 0.001);
	float dFx = map(p + d.yxx) - map(p - d.yxx);
	float dFy = map(p + d.xyx) - map(p - d.xyx);
	float dFz = map(p + d.xxy) - map(p - d.xxy);
	return normalize(vec3(dFx, dFy, dFz));
}
float trace(vec3 o, vec3 r){
	float t = 0.0;
    vec3 p;
    for(int i = 0; i < 128; ++i){
    	p = o + r * t;
        float d = map(p);
        if(d < 0.001){
            stop = 1;
        	break;
        }
        t += d * 0.9;
    }
    stoppos = p;
    return t;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    vec3 r = normalize(vec3(uv - vec2(0.0, PI / 3.0), 1.0));

    float the = iTime;
    vec3 o = vec3(0.0,10.0, -10.0);
    r.xz *= mat2(cos(the), -sin(the), sin(the), cos(the));
    o.xz *= mat2(cos(the), -sin(the), sin(the), cos(the));

    float t = trace(o, r);
    float light = 0.0;
    if(stop == 1){
    	vec3 nor = grad(stoppos);
        light = 0.7 * max(dot(nor, vec3(1.0)), 0.0) + 0.1;
        stop = 0;
        trace(stoppos + nor * 0.002, normalize(vec3(1.0,0.7,1.0)));
        if(stop == 1) light = 0.1;
    }

    vec3 fc = vec3(light) * col;
    fragColor = vec4(fc,1.0);
}

