// ----- math constants -----
const float PI = 3.14159265359;
const float TAU = 6.28318530718;

float rand(float seed)
{
	// Very simple pseudo-random number generator
    // Returns a value between 0 and 1
    // Preferentially returns the midrange
    return mod(fract(sin(seed * 6789.54321)) + 0.5, 1.0);
}

float coherentNoise(float seed)
{
	float a = floor(seed);
    float b = ceil(seed);
    float rA = rand(a);
    float rB = rand(b);
    if (rA == rB) {
        return rA;
    } else {
        float nT = (1.0 - (b - seed)) / (b - a);
        float cT = nT * nT * (3.0 - 2.0 * nT);
        return rA + (cT * (rB - rA));
    }
}

float multiOctaveNoise(float seed, int numOcts, float lacunarity, float persistence)
{
    float sum = 0.0;
    for (int i = 0; i < numOcts; ++i)
    {
        float oct = coherentNoise(seed * pow(lacunarity, float(i)));
        sum += oct * pow(persistence, float(i));  
    }
    return sum;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 m = vec2(0.5,0.5);
    // Uncomment the if clause below to move the flame around with the mouse
    /*
    if (iMouse.xy != vec2(0.0,0.0))
    {
    	m = iMouse.xy / iResolution.xy;
    }
	*/

    uv -= m;
    uv -= vec2(0.0,-0.45);
    float distortSpeed = 1.0;
    float distortVar = multiOctaveNoise(iTime * distortSpeed + (0.25 * uv.y), 3, 2.0, 0.5);
    uv -= vec2(0.15 * uv.y * (distortVar - 0.9),0.0);

    uv.x *= iResolution.x/iResolution.y;
    float d = 1.0 * length(uv);

    float angleSpeed = 1.0;
    float angle = atan(uv.x, uv.y);
    float angleVar = multiOctaveNoise(iTime * angleSpeed, 3, 2.0, 0.5);
    angle += 0.25 * ((angleVar * 0.125 * PI) - (0.125 * PI));

    float sizeSpeed = 1.0;
    float sizeVar = multiOctaveNoise(iTime * sizeSpeed, 3, 3.0, 0.75);

    float redAngle = 1.0 - abs(angle);
    redAngle = redAngle * (0.915 + 0.085 * sizeVar);
    float redComp = smoothstep(redAngle, redAngle-0.4, d);

    float yellowAngle = 0.95 - abs(angle);
    yellowAngle = 0.85 * yellowAngle * (0.915 + 0.085 * sizeVar);
    float greenComp = smoothstep(yellowAngle, yellowAngle-0.35, d);

	fragColor = vec4(redComp, greenComp, 0.0, 1.0);
}

#define M_PI 3.1415926535897932384626433832795
#define rot(a)  mat2( cos(a), sin(a),  -sin(a), cos(a) )

void mainImage( out vec4 fragColor, in vec2 u )
{
    // set my uv
    vec2 uv = iResolution.xy;
    uv = (u+u - vec2(uv.x, 0) ) / uv.y;

    float T = iTime;

    // rotate the uv
    float l = length(uv);
    uv *= rot( l * 0.5);
    uv *= uv/l * 4.0;		// detail resolution

    // set some colors
    vec3 color;
    for( int i = 0; i < 3; i++){
        float v = length( mod(uv, 0.5) - uv.x * 0.25 );
        uv.y *= ((sin( v * 5.0) ) * 0.5) * ((sin(T - length(uv) * 0.5 ) + M_PI) * 0.4);
        color[i] = cos(T + pow(length(uv), float(i) * 0.25));
    }

    vec3 color2 = vec3(pow(uv.y, 1.0) * 0.5);
    fragColor = vec4( mix(color, color2, length(uv) * 0.5), 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float z = iTime;

    // set centered uv
    vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0;
    uv -= 1.0;
    uv.x *= iResolution.x / iResolution.y;

    // multiple and twist uv
    uv *= sin(length(uv) * 2.0) * 7.0;

    // calculate morphing central blobs
    float val1 = length(uv);
    val1 = 1.0 - fract(length(uv + sin(iTime + uv.x) * cos(iTime + uv.y) ));

    // calculate rotating radial wave pattern
    float val2 = length(uv * 0.1) * 
        cos( pow(sin(iTime + atan(uv.y, uv.x) * 10.0) + uv.x * length(uv * 0.1), 2.0 ));

	// combine values
    float val = mix(val1, val2, sin(atan(uv.y, uv.x) * 10.0) );

    // colors
    float r = pow(val, 5.0) * 3.0;
    float g = val;
    float b = val + 0.05;
    fragColor = vec4(r, g, b, 1);
}

