#define AA 4-3

vec2 opU( vec2 d1, vec2 d2 )
{
	return (d1.x<d2.x) ? d1 : d2;
}

float opS( float d1, float d2 )
{
    return max(-d1,d2);
}

float opI( float d1, float d2 )
{
    return max(d1,d2);
}

float opBlend(float d1, float d2, float a)
{
    if (a > 1.0)
        a = 1.0;
    if (a < 0.0)
        a = 0.0;
    return a * d1 + (1.0 - a) * d2;
}

// distance to sphere function (p is world position of the ray, s is sphere radius)
// from http://iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdSphere(vec3 p, float s)
{
	return length(p) - s;
}

float sdPlane(vec3 p)
{
    return p.y;
}

float udBox( vec3 p, vec3 b )
{
    return length(max(abs(p)-b,0.0));
}

float fmod(float a, float b)
{
    if(a<0.0)
    {
        return b - mod(abs(a), b);
    }
    return mod(a, b);
}

vec2 map(vec3 pos)
{
    vec3 cp = vec3(0.0,0.0,0.0);

    vec2 res = opU(vec2(sdPlane(pos - vec3(0.0,0.0,0.0) + cp),1.0),
                   vec2(sdSphere(pos - vec3(0.0,0.5,0.0) + cp,0.5),46.9));

    float b = opBlend(udBox(pos - vec3(1.0,0.5,0.0) + cp,vec3(0.5,0.5,0.5)),
                      sdSphere(pos - vec3(1.0,0.5,0.0) + cp,0.5),(sin(iTime)+1.0)/2.0);
    res = opU(res, vec2(b,78.5));

    b = opI(udBox(pos - vec3(-1.0,0.5 * (sin(iTime)+1.0)/2.0,0.0) + cp,vec3(0.5,0.5,0.5)),
            sdSphere(pos - vec3(-1.0,0.5,0.0) + cp,0.5));
    res = opU(res, vec2(b,129.8));

    b = opS(sdSphere(pos - vec3(-1.0,0.5,-1.0) + cp,0.5),
            udBox(pos - vec3(-1.0,0.5 * (sin(iTime))/1.0,-1.0) + cp,vec3(0.5,0.5,0.5)));
    res = opU(res, vec2(b,22.4));

    return res;
}

vec2 scene(vec3 position)
{
    vec3 translate = vec3(0.0, 0.5, 1.0);

    vec2 res = map(position);

    float distance = res.x;
    float materialID = res.y;

    return vec2(distance, materialID);
}

vec2 castRay(vec3 pos, vec3 dir)
{    
    float tmin = 1.0;
    float tmax = 20.0;

#if 1
    float tp1 = (0.0 - pos.y)/dir.y; if (tp1 > 0.0) tmax = min(tmax, tp1);
    float tp2 = (1.6 - pos.y)/dir.y; if (tp2 > 0.0) { if (pos.y > 1.6) tmin = max(tmin, tp2);
                                                     else tmax = min(tmax, tp2); }
#endif

    float t = tmin;
    float m = -1.0;
    for (int i=0; i<64; i++)
    {
        float precis = 0.0005*t;
        vec2 res = map(pos + dir*t);
        if (res.x<precis || t>tmax) break;
        t += res.x;
        m = res.y;
    }

    if (t>tmax) m=-1.0;
    return vec2(t, m);
}

float softshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
	float res = 1.0;
    float t = mint;
    for( int i=0; i<16; i++ )
    {
		float h = map( ro + rd*t ).x;
        res = min( res, 8.0*h/t );
        t += clamp( h, 0.02, 0.10 );
        if( h<0.001 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773*0.0005;
    return normalize( e.xyy*map( pos + e.xyy ).x + 
					  e.yyx*map( pos + e.yyx ).x + 
					  e.yxy*map( pos + e.yxy ).x + 
					  e.xxx*map( pos + e.xxx ).x );
    /*
