float PI = 3.1415926535;
vec3 col = vec3(1.0, 1.0, 1.0);
int stop = 0;
vec3 stoppos = vec3(0.0);
float map(vec3 p){
    vec3 p1 = p;
    p1.y -= clamp(p.y, 0.0, 2.0);
    float d = length(p1 - vec3(-2.0, 0.0, 0.0)) - 1.0;
    d = min(d, dot(p,vec3(0.0, 1.0, 0.0)) + 1.0);
    return d;
}
vec3 grad(vec3 p) {
    vec2 d = vec2(0.0, 0.001);
	float dFx = map(p + d.yxx) - map(p - d.yxx);
	float dFy = map(p + d.xyx) - map(p - d.xyx);
	float dFz = map(p + d.xxy) - map(p - d.xxy);
	return normalize(vec3(dFx, dFy, dFz));
}
float trace(vec3 o, vec3 r){
	float t = 0.0;
    vec3 p;
    for(int i = 0; i < 128; ++i){
    	p = o + r * t;
        float d = map(p);
        if(d < 0.001){
            stop = 1;
        	break;
        }
        t += d * 0.9;
    }
    stoppos = p;
    return t;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    vec3 r = normalize(vec3(uv - vec2(0.0, PI / 3.0), 1.0));

    float the = iTime;
    vec3 o = vec3(0.0,10.0, -10.0);
    r.xz *= mat2(cos(the), -sin(the), sin(the), cos(the));
    o.xz *= mat2(cos(the), -sin(the), sin(the), cos(the));

    float t = trace(o, r);
    float light = 0.0;
    if(stop == 1){
    	vec3 nor = grad(stoppos);
        light = 0.7 * max(dot(nor, vec3(1.0)), 0.0) + 0.1;
        stop = 0;
        trace(stoppos + nor * 0.002, normalize(vec3(1.0)));
        if(stop == 1) light = 0.1;
    }

    vec3 fc = vec3(light) * col;
    fragColor = vec4(fc,1.0);
}

