const float stripe_color_speed = 0.01;
const float stripe_color_saturation = 0.45;

const float stripe_scale = 32.0;
const float inv_stripe_scale = 1.0 / stripe_scale;

const vec2 stripe_movement = vec2(2.0, 0.0);

vec3 hsv2rgb(vec3 c) {
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

float wrap(float v, float _min, float _max) {
	return mod(_min + (v - _min), _max - _min);
}

void mainImage(out vec4 col_out, vec2 coords) {
    vec2 ps = vec2(1.0) / iResolution.xy;
    vec2 uv = coords * ps;

    //move stripes
    coords += vec2(iFrame) * stripe_movement;

    //get "blurred" music frequency, so that it's not "pointy"
    float music_wave = 0.0;    
    const int samples = 6;
    for (int i = 0; i < samples; ++i) {
        music_wave += texture(iChannel0, vec2(uv.x * 0.16 + ps.x * float(i))).x;
    }
    music_wave /= float(samples);
    music_wave *= 100.0;

    float distortion = 0.0;
    distortion += sin(coords.x * 10.0) * 4.0;  //small noise
    distortion += sin(coords.x * 0.03) * 8.0;  //medium noise
    distortion += sin(coords.x * 0.01) * -50.0; //big noise
    distortion += sin(coords.x * 0.01) * music_wave;
    coords.y += distortion * 0.25;

    bool a_or_b = mod(coords.y * inv_stripe_scale, 2.0) < 1.0;

    float sock_hue = 0.0;

    //if the left mouse button is pressed
    if (iMouse.z > 0.0)             //get a color hue from the mouse X position
        sock_hue = iMouse.x * ps.x;
    else                            //get a color hue from 0.0 to 1.0 based on time
		sock_hue = wrap(iTime * stripe_color_speed, 0.0, 1.0);

    //generate a RedGreenBlue color from a HueSaturationValue color
    vec3 col_sock = hsv2rgb(vec3(sock_hue, stripe_color_saturation, 1.0));

    col_out.rgb = a_or_b ? col_sock : vec3(1.0);
    col_out.a = 0.0;
}

// Set this to nonzero to enable SDF-based space skipping.
#define SKIP_SPACE 1

// In this example:
//   288 SDF samples when space skipping is enabled.
//  1600 SDF samples when space skipping is disabled.

// Signed distance field.
float dist(vec3 p)
{
    // Just a field of repeating spheres.
    p=mod(p,40.)-20.;
    return length(p)-10.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);

    vec2 t=uv-.5;
    t.x*=iResolution.x/iResolution.y;

    vec2 p=t.xy;

    // Set up primary ray.
    vec3 ro=vec3(0,30,-iTime*4.);
    vec3 rd=normalize(vec3(p,-1.4));

    vec3 co=ro;
    vec3 ord=rd;

    vec3 n;

#if SKIP_SPACE
    for(int j=0;j<4;++j)
#endif
    {
        vec3 oro=ro;

#if SKIP_SPACE
		// Use signed distance field raymarching to skip space quickly.
        float t=0.;
        for(int i=0;i<40;++i)
        {
            ro=oro+rd*t;
            float d=dist(ro);
            if(d<-3.)
            {
                break;
            }
            t+=d;
        }
#endif

        // Prepare for uniform grid traversal.
        oro=ro;
        vec3 c=floor(ro);
        vec3 ts=(c+max(vec3(0.),sign(rd))-ro)/rd;

        // Perform uniform grid traversal.
#if SKIP_SPACE
        for(int i=0;i<32;++i)
#else
        // Use (many) more iterations to reach the same distances as would be reached with space skipping.
        for(int i=0;i<1600;++i)
#endif
        {
            // n holds 1.0 in the axis for which ts's component in that axis is
            // the smallest, and 0.0 elsewhere. This indicates the axis plane with the closest intersection.
            n=step(ts,ts.yzx)*step(ts,ts.zxy);
            c+=sign(rd)*n;
            float d=dist(c);
            if(d<-1.7)
            {
		        // Advance the ray march position to the next grid cell.
                ro=oro+rd*min(ts.x,min(ts.y,ts.z));
                break;
            }
            // Increment the axis plane inersections.
            vec3 dd=sign(rd)/rd*n;
            ts+=dd;
        }
        // Advance the ray march position to the next grid cell.
        ro=oro+rd*min(ts.x,min(ts.y,ts.z));
    }


    // Checkerboard texture.
    ro+=1e-3;
    float tex=.8+.2*step(.5,fract(ro.x+.5*step(.5,fract(ro.y+.5*step(.5,fract(ro.z))))));

    // Basic lighting.
    vec3 ld=normalize(vec3(3,4,1));
    fragColor.rgb=tex*vec3(.5+.5*dot(n*-sign(rd),ld));

    // Distance fading.
    float fogstrength=.001;
    float fogamount=exp(-distance(ro,co)*fogstrength);
    fragColor.rgb=mix(vec3(0),fragColor.rgb,fogamount);

    // Gamma.
    fragColor.rgb=sqrt(fragColor.rgb);
}

