// Box Frustum Tracing
// using 2d vector graphics library (https://www.shadertoy.com/view/lslXW8)

// for a description of the algorithm applied to ellipsoids see
// https://gist.github.com/paniq/6c7a465b841dc2c87294c61108370389

// note that this version is very verbose and purposefully lacks 
// any form of optimization for the sake of education.

const vec3 cube_init_center = vec3(0.0, 0.0, 3.0);
const vec2 min_frustum_half_size = vec2(1.77778,1.0)/30.0;
const vec2 max_frustum_half_size = vec2(1.77778,1.0)/6.0;

const int bound_none = 0;
const int bound_ray = 1;
const int bound_volume = 2;
const int bound_plane = 3;

vec2 frustum_center;
vec2 frustum_half_size;
vec3 cube_center;
vec3 cube_scale;
vec4 cube_rotation;
vec2 frustum_corners[4];
vec3 frustum_ray_dirs[4];
vec3 frustum_planes[4];
// these are all in model space
vec3 ms_frustum_apex;
vec3 ms_frustum_ray_dirs[4];
vec4 ms_frustum_planes[4];
vec3 cube_corners[8];
vec2 cube_ray_hits[4]; // these are scalars along the ray direction
vec3 cube_edge_near[4];
vec3 cube_edge_far[4];
bvec2 cube_has_edge[4];
bool cube_has_corner[8];
vec2 outer_bound;
vec2 inner_bound;
ivec2 outer_bound_type;
ivec2 inner_bound_type;

// check only the horizontal frustum planes
float dFrustumH(vec3 p) {
    return max(
        dot(p, frustum_planes[0]),
        dot(p, frustum_planes[2]));    
}

// check only the vertical frustum planes
float dFrustumV(vec3 p) {
    return max(
        dot(p, frustum_planes[1]),
        dot(p, frustum_planes[3]));    
}

float dFrustum(vec3 p) {
    return max(
        dFrustumH(p),
        dFrustumV(p));
}

// versor (unit quaternion) from axis and angle
vec4 versor(vec3 axis, float angle) {
    float a = angle * 0.5;
    return vec4(axis * sin(a), cos(a));
}

// invert rotation
vec4 conjugate(vec4 q) {    
    return vec4(-q.xyz, q.w);
}

// rotate point by versor
// q (t) * V * q (t) ^-1
vec3 rotate(vec4 q, vec3 p) {
    vec3 t = cross(q.xyz,p) * 2.0;
    return p + q.w * t + cross(q.xyz, t);    
}

vec4 normalize_plane(vec4 pl) {
    return pl / length(pl.xyz);
}

// transform vector in view space to model space
vec3 modelspace_dir(vec3 v) {
    return rotate(conjugate(cube_rotation), v) / cube_scale;
}
// transform vector in model space to view space
vec3 viewspace_dir(vec3 v) {
    return rotate(cube_rotation, v * cube_scale);
}
// transform point in view space to model space
vec3 modelspace(vec3 p) {
    return modelspace_dir(p - cube_center);
}
// transform point in model space to view space
vec3 viewspace(vec3 p) {    
    return viewspace_dir(p) + cube_center;
}

// from https://www.shadertoy.com/view/4s23DR
// cube() by Simon Green
vec2 intersect_unit_cube(vec3 org, vec3 dir)
{
	// compute intersection of ray with all six bbox planes
	vec3 invR = -1.0/dir;
	vec3 tbot = invR * (org + 1.0);
	vec3 ttop = invR * (org - 1.0);

	// re-order intersections to find smallest and largest on each axis
	vec3 tmin = min (ttop, tbot);
	vec3 tmax = max (ttop, tbot);

	// find the largest tmin and the smallest tmax
	vec2 t0 = max(tmin.xx, tmin.yz);
	float near = max(t0.x, t0.y);
	t0 = min(tmax.xx, tmax.yz);
	float far = min(t0.x, t0.y);

	// check for hit
    if (near < far && far > 0.0) {
        return vec2(near, far);
    } else {
        return vec2(-1.0/0.0);
    }
}
vec2 intersect_unit_cube(vec3 org, vec3 dir, out vec3 near_normal, out vec3 far_normal)
{
	// compute intersection of ray with all six bbox planes
	vec3 invR = -1.0/dir;
	vec3 tbot = invR * (org + 1.0);
	vec3 ttop = invR * (org - 1.0);

	// re-order intersections to find smallest and largest on each axis
	vec3 tmin = min (ttop, tbot);
	vec3 tmax = max (ttop, tbot);

	// find the largest tmin and the smallest tmax
	vec2 t0 = max(tmin.xx, tmin.yz);
	float near = max(t0.x, t0.y);
	t0 = min(tmax.xx, tmax.yz);
	float far = min(t0.x, t0.y);

	// check for hit
    if (near < far && far > 0.0) {
        near_normal = -sign(dir) * step(tmin.yzx, tmin.xyz) * step(tmin.zxy, tmin.xyz);
        far_normal = -sign(dir) * step(tmax.xyz, tmax.yzx) * step(tmax.xyz, tmax.zxy);        
        return vec2(near, far);
    } else {
        return vec2(-1.0/0.0);
    }
}


float intersect_plane_line(vec4 plane, vec3 ro, vec3 rd) {    
    vec3 p0 = -dot(vec4(ro,1.0), plane)*plane.xyz;
    return dot(p0, plane.xyz) / dot(rd, plane.xyz);
}

void intersect_plane_edge(int mc, vec4 plane, vec3 ro, vec3 rd, inout vec3 edge_near, inout vec3 edge_far, inout bvec2 has_edge) {
    float d = intersect_plane_line(plane, ro, rd);
    if (abs(d) < 1.0) {
        vec3 p = viewspace(ro + rd * d);
        float d;
        if (mc == 0) {
            d = dFrustumV(p);
        } else {
            d = dFrustumH(p);
        }
        if (d <= 0.0) {
            if (!has_edge[0] || (p.z < edge_near.z)) {
                edge_near = p;
                has_edge[0] = true;
            }
            if (!has_edge[1] || (p.z > edge_far.z)) {
                edge_far = p;
                has_edge[1] = true;
            }
        }
    }
}

vec2 lissajous(float t, float a, float b) {
    return vec2(sin(a*t), sin(b*t));
}

void compute_bounds() {
    outer_bound_type = ivec2(bound_none);
    inner_bound_type = ivec2(bound_none);
    outer_bound = vec2(1.0/0.0,-1.0/0.0);
    inner_bound = vec2(-1.0/0.0,1.0/0.0);

    // check corners
    for (int i = 0; i < 8; ++i) {
        vec3 p = cube_corners[i];
        if (cube_has_corner[i] && (p.z < outer_bound[0])) {
        	outer_bound[0] = p.z;
	        outer_bound_type[0] = bound_volume;
        }
    }
    // check edges
    for (int i = 0; i < 4; ++i) {
        if (cube_has_edge[i][0]
            && (cube_edge_near[i].z < outer_bound[0])) {
            outer_bound[0] = cube_edge_near[i].z;
            outer_bound_type[0] = bound_plane;
        }
    }
    // check faces
    for (int i = 0; i < 4; ++i) {
        if (cube_ray_hits[i].x >= 0.0) {
            vec3 p = frustum_ray_dirs[i] * cube_ray_hits[i].x;
            if (p.z < outer_bound[0]) {
                outer_bound[0] = p.z;
                outer_bound_type[0] = bound_ray;
            }
        }
    }
    // don't need to continue if we couldn't find a nearest depth
    if (outer_bound_type[0] == bound_none) return;

    // check corners
    for (int i = 0; i < 8; ++i) {
        vec3 p = cube_corners[i];
        if (cube_has_corner[i] && (p.z > outer_bound[1])) {
        	outer_bound[1] = p.z;
	        outer_bound_type[1] = bound_volume;
        }
    }
    // check edges
    for (int i = 0; i < 4; ++i) {
        if (cube_has_edge[i][1]
            && (cube_edge_far[i].z > outer_bound[1])) {
            outer_bound[1] = cube_edge_far[i].z;
            outer_bound_type[1] = bound_plane;
        }
    }
    // check faces
    for (int i = 0; i < 4; ++i) {
        if (cube_ray_hits[i].y >= 0.0) {
            vec3 p = frustum_ray_dirs[i] * cube_ray_hits[i].y;
            if (p.z > outer_bound[1]) {
                outer_bound[1] = p.z;
                outer_bound_type[1] = bound_ray;
            }
        }
    }

    // we have an interior interval if all four corners
    // hit the sphere

    int hits = 0;
    for (int i = 0; i < 4; ++i) {
        if (cube_ray_hits[i].x >= 0.0) {
            hits++;
        }    
    }

    if (hits == 4) {
        for (int i = 0; i < 4; ++i) {
            if (cube_ray_hits[i].x >= 0.0) {
                vec3 p0 = frustum_ray_dirs[i] * cube_ray_hits[i].x;
                vec3 p1 = frustum_ray_dirs[i] * cube_ray_hits[i].y;
                if (p0.z > inner_bound[0]) {
                    inner_bound[0] = p0.z;
                    inner_bound_type[0] = bound_ray;                
                }
                if (p1.z < inner_bound[1]) {
                    inner_bound[1] = p1.z;
                    inner_bound_type[1] = bound_ray;
                }
            }
        }
    }

    // if the interval is negative, we can't use it
    if (inner_bound[1] < inner_bound[0]) {
        inner_bound_type[0] = bound_none;
    }    
}

vec4 plane_from_triangle(vec3 p0, vec3 d0, vec3 d1) {
    vec3 n = normalize(cross(d0, d1));
    return vec4(n, -dot(n, p0));
}

void setup_globals(float t) {
    // move the frustum around
    frustum_center = lissajous(t*0.07, 2.0, 1.0)*0.1;
    // vary the frustum aperture
    frustum_half_size = mix(
        min_frustum_half_size,
		max_frustum_half_size,
        cos(t*0.13)*0.5+0.5);

    // move our cube around a bit
    vec2 cube_offset = lissajous(t*0.2, 5.0, 4.0)*0.3;
    cube_center = cube_init_center + vec3(cube_offset, 0.0);
    cube_scale = vec3(
        mix(0.2,1.0,cos(t*0.35)*0.5+0.5),
        mix(0.2,1.0,cos(t*0.292)*0.5+0.5),
        1.0);
    cube_rotation = versor(normalize(vec3(1.0)), t*0.2);

    vec3 cd = vec3(1.0,-1.0,0.0);
    cube_corners[0] = viewspace(vec3(cd.yyy));
    cube_corners[1] = viewspace(vec3(cd.yyx));
    cube_corners[2] = viewspace(vec3(cd.yxy));
    cube_corners[3] = viewspace(vec3(cd.yxx));
    cube_corners[4] = viewspace(vec3(cd.xyy));
    cube_corners[5] = viewspace(vec3(cd.xyx));
    cube_corners[6] = viewspace(vec3(cd.xxy));
    cube_corners[7] = viewspace(vec3(cd.xxx));

    vec3 frustum_extents = vec3(frustum_half_size,-frustum_half_size.x);
    frustum_corners[0] = frustum_center + frustum_extents.xy;
    frustum_corners[1] = frustum_center + frustum_extents.zy;
    frustum_corners[2] = frustum_center - frustum_extents.xy;
    frustum_corners[3] = frustum_center - frustum_extents.zy;

    ms_frustum_apex = modelspace(vec3(0.0));

    for (int i = 0; i < 4; ++i) {
        frustum_ray_dirs[i] = normalize(vec3(frustum_corners[i],1.0));
        ms_frustum_ray_dirs[i] = modelspace_dir(frustum_ray_dirs[i]);
    }

    for (int i = 0; i < 4; ++i) {
		cube_ray_hits[i] = intersect_unit_cube(ms_frustum_apex, ms_frustum_ray_dirs[i]);
    }

    frustum_planes[0] = normalize(cross(frustum_ray_dirs[1],frustum_ray_dirs[0]));
    frustum_planes[1] = normalize(cross(frustum_ray_dirs[2],frustum_ray_dirs[1]));
    frustum_planes[2] = normalize(cross(frustum_ray_dirs[3],frustum_ray_dirs[2]));
    frustum_planes[3] = normalize(cross(frustum_ray_dirs[0],frustum_ray_dirs[3]));

    ms_frustum_planes[0] = plane_from_triangle(ms_frustum_apex,ms_frustum_ray_dirs[1],ms_frustum_ray_dirs[0]);
    ms_frustum_planes[1] = plane_from_triangle(ms_frustum_apex,ms_frustum_ray_dirs[2],ms_frustum_ray_dirs[1]);
    ms_frustum_planes[2] = plane_from_triangle(ms_frustum_apex,ms_frustum_ray_dirs[3],ms_frustum_ray_dirs[2]);
    ms_frustum_planes[3] = plane_from_triangle(ms_frustum_apex,ms_frustum_ray_dirs[0],ms_frustum_ray_dirs[3]);

    for (int i = 0; i < 8; ++i) {
        cube_has_corner[i] = (dFrustum(cube_corners[i]) <= 0.0);
    }    

    for (int i = 0; i < 4; ++i) {
        vec4 pl = ms_frustum_planes[i];
        int mc = 0;
        if ((i == 1)||(i == 3))
            mc = 1;
        cube_edge_near[i] = vec3(1.0/0.0);
        cube_edge_far[i] = vec3(-1.0/0.0);
        cube_has_edge[i] = bvec2(false);
        intersect_plane_edge(mc, pl, cd.xxz, cd.zzx, cube_edge_near[i], cube_edge_far[i], cube_has_edge[i]);
        intersect_plane_edge(mc, pl, cd.yxz, cd.zzx, cube_edge_near[i], cube_edge_far[i], cube_has_edge[i]);
        intersect_plane_edge(mc, pl, cd.yyz, cd.zzx, cube_edge_near[i], cube_edge_far[i], cube_has_edge[i]);
        intersect_plane_edge(mc, pl, cd.xyz, cd.zzx, cube_edge_near[i], cube_edge_far[i], cube_has_edge[i]);

        intersect_plane_edge(mc, pl, cd.xzx, cd.zxz, cube_edge_near[i], cube_edge_far[i], cube_has_edge[i]);
        intersect_plane_edge(mc, pl, cd.yzx, cd.zxz, cube_edge_near[i], cube_edge_far[i], cube_has_edge[i]);
        intersect_plane_edge(mc, pl, cd.yzy, cd.zxz, cube_edge_near[i], cube_edge_far[i], cube_has_edge[i]);
        intersect_plane_edge(mc, pl, cd.xzy, cd.zxz, cube_edge_near[i], cube_edge_far[i], cube_has_edge[i]);

        intersect_plane_edge(mc, pl, cd.zxx, cd.xzz, cube_edge_near[i], cube_edge_far[i], cube_has_edge[i]);
        intersect_plane_edge(mc, pl, cd.zyx, cd.xzz, cube_edge_near[i], cube_edge_far[i], cube_has_edge[i]);
        intersect_plane_edge(mc, pl, cd.zyy, cd.xzz, cube_edge_near[i], cube_edge_far[i], cube_has_edge[i]);
        intersect_plane_edge(mc, pl, cd.zxy, cd.xzz, cube_edge_near[i], cube_edge_far[i], cube_has_edge[i]);        
    }

	compute_bounds();    

}

uniform float time;
uniform vec2 mouse;
uniform vec2 resolution;

// interface
