{
    return arrow_angle(texcoord, body, head, 0.25, linewidth, antialias);
}


float arrow_stealth(vec2 texcoord,
                    float body, float head,
                    float linewidth, float antialias)
{
    float w = linewidth/2.0 + antialias;
    vec2 start = -vec2(body/2.0, 0.0);
    vec2 end   = +vec2(body/2.0, 0.0);
    float height = 0.5;

    // Head : 4 lines
    float d1 = line_distance(texcoord, end-head*vec2(+1.0,-height),
                                       end);
    float d2 = line_distance(texcoord, end-head*vec2(+1.0,-height),
                                       end-vec2(3.0*head/4.0,0.0));
    float d3 = line_distance(texcoord, end-head*vec2(+1.0,+height), end);
    float d4 = line_distance(texcoord, end-head*vec2(+1.0,+0.5),
                                       end-vec2(3.0*head/4.0,0.0));

    // Body : 1 segment
    float d5 = segment_distance(texcoord, start, end - vec2(linewidth,0.0));

    return min(d5, max( max(-d1, d3), - max(-d2,d4)));
}

// Position jitter (see comments)
#define srnd(p)  (2.* fract(sin(p*mat2(12.9898, 78.233,1,-47.14))* 43758.5453) -1. )

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float M_PI = 3.14159265358979323846;
    const float SQRT_2 = 1.4142135623730951;
    const float linewidth = 3.0;
    const float antialias =  1.0;

    const float rows = 48.0;
    const float cols = 48.0;
    float body = min(iResolution.x/cols, iResolution.y/rows) / SQRT_2;
    vec2 texcoord = gl_FragCoord.xy;
    vec2 size   = iResolution.xy / vec2(cols,rows);
    vec2 center = (floor(texcoord/size) + vec2(0.5,0.5)) * size;

    // Position jitter (see comments)
    center += .2*size* srnd(center);

    texcoord -= center;

    // float theta = M_PI/3.0 + 0.1*(center.x / cols + center.y / rows);

    // vec2 mouse = iMouse.xy;
    vec2 mouse = iResolution.xy*(.5+.5*vec2(.75*cos(iTime)+.25*sin(0.33*iTime),
                                             .75*sin(iTime)+.25*cos(1.27*iTime)));

    float theta = M_PI-atan(center.y-mouse.y,  center.x-mouse.x);


    float cos_theta = cos(theta);
    float sin_theta = sin(theta);
    texcoord = vec2(cos_theta*texcoord.x - sin_theta*texcoord.y,
                    sin_theta*texcoord.x + cos_theta*texcoord.y);

    float l = 1.0-length(center-mouse.xy)/max(iResolution.x, iResolution.y);
    l = 0.25 +.75*l;


    // float d = arrow_curved(texcoord, body, 0.25*body, linewidth, antialias);
    float d = arrow_stealth(texcoord, l*body, 0.25*body, linewidth, antialias);
    // float d = arrow_triangle_90(texcoord, body, 0.15*body, linewidth, antialias);
    // float d = arrow_triangle_60(texcoord, body, 0.20*body, linewidth, antialias);
    // float d = arrow_triangle_30(texcoord, body, 0.25*body, linewidth, antialias);
    // float d = arrow_angle_90(texcoord, body, 0.15*body, linewidth, antialias);
    // float d = arrow_angle_60(texcoord, body, 0.20*body, linewidth, antialias);
    // float d = arrow_angle_30(texcoord, body, 0.25*body, linewidth, antialias);

    fragColor = filled(d, linewidth, antialias,
                          vec4(0,0,0,1),vec4(1,1,1,1));
}

/*

Stochastically visualizing the bounds of an 1D ellipsoid implicit function
over a frustum volume spanned by four rays. If the volume crosses 
the x-axis, the ellipsoid is within the frustum, and the intersection 
points are the near and far plane scalars.

Green curves visualize the function at the corner of the frustum.

In this test, the ellipsoid is rotating to nudge it in and out
of the frustum volume.

I wrote this to see what a convex function would look like that 
could e.g. be solved with newton's method.

I don't really have an intuition to how to build that function yet.

*/


// polynomial arithmetic
// describes piecewise univariate polynomial with degree 2
struct poly2 {
    // the coefficients for f(x) = a0 * x^0 + a1 * x^1 + a2 * x^2
    vec3 a;
    // distance to horizon
    float h;
};

const float infinity = 1. / 0.;

bool hasaxis(poly2 f) {
    return (f.h != infinity);
}

poly2 pa_init(float x) {
    return poly2(vec3(x,1.0,0.0),infinity);
}

float merge_axes(poly2 f, poly2 g) {
    if (hasaxis(g)) {
        if (hasaxis(f)) {
            float a0 = min(f.h, g.h);
            float a1 = max(f.h, g.h);
            return ((a0 > 0.0)?a0:a1);
        } else {
            return g.h;
		}
    }
    return f.h;
}

poly2 pa_add(poly2 f, poly2 g) {
    return poly2(f.a + g.a,merge_axes(f,g));
}
poly2 pa_add(poly2 f, float c) {
    return poly2(vec3(f.a[0] + c,f.a[1],f.a[2]),f.h);
}
poly2 pa_add(float c, poly2 f) {
    return poly2(vec3(f.a[0] + c,f.a[1],f.a[2]),f.h);
}
poly2 pa_sub(poly2 f, poly2 g) {
    return poly2(f.a - g.a,merge_axes(f,g));
}
poly2 pa_sub(poly2 f, float c) {
    return poly2(vec3(f.a[0] - c,f.a[1],f.a[2]),f.h);
}
poly2 pa_sub(float c, poly2 f) {
    return poly2(vec3(c - f.a[0],-f.a[1],-f.a[2]),f.h);
}
poly2 pa_unm(poly2 f) {
    return poly2(-f.a,f.h);
}

// {a0 a1 a2} * {b0 b1 b2}
// = {a0*b0 (a0*b1 + a1*b0) (a0*b2 + a2*b0 + a1*b1) (a1*b2 + a2*b1) (a2*b2)}
// the two new coefficients are truncated, so only linear
// functions are going to work here reliably.
poly2 pa_mul(poly2 f, poly2 g) {    
    return poly2(vec3(
    	f.a[0] * g.a[0],
	    f.a[0] * g.a[1] + f.a[1] * g.a[0],
        f.a[0] * g.a[2] + f.a[1] * g.a[1] + f.a[2] * g.a[0]
        //f.a[1] * g.a[2] + f.a[2] * g.a[1],
        //f.a[2] * g.a[2]
        ), f.h);
}
poly2 pa_mul(poly2 f, float c) {
    return poly2(f.a * c,f.h);
}
poly2 pa_mul(float c, poly2 f) {
    return poly2(f.a * c,f.h);
}

// the two new coefficients are truncated, so only linear
// functions are going to work here reliably.
poly2 pa_pow2(poly2 f) {
    return poly2(vec3(
    	f.a[0] * f.a[0],
	    2.0 * f.a[0] * f.a[1],
        2.0 * f.a[0] * f.a[2] + f.a[1] * f.a[1]
        //2.0 * f.a[1] * f.a[2],
        //f.a[2] * f.a[2]
        ), f.h);
}

// returns f(x), f'(x), f''(x)
vec3 pa_f(vec3 a, float x) {
    return vec3(
        a[0] + (a[1] +       a[2] * x) * x,
                a[1] + 2.0 * a[2] * x,
                             a[2]);
}

float solve_quadratic(vec3 fa, float x) {
    float a = fa[2];
    float b = fa[1];
    float c = fa[0];

    // the quadratic solve doesn't work for a=0
    // so we need a branch here.
    if (a == 0.0) {
        return -c / b;
    } else { 
        // (-b +- sqrt(b*b - 4.0*a*c)) / 2.0*a
        float k = -0.5*b/a;
        float q = sqrt(k*k - c/a);
        float q0 = k - q;
        float q1 = k + q;

        // pick the root right of x
		return (q0 <= x)?q1:q0;
    }
}

float solve_quadratic0(vec3 fa) {
    float a = fa[2];
    float b = fa[1];
    float c = fa[0];

    // the quadratic solve doesn't work for a=0
    // so we need a branch here.
    if (a == 0.0) {
        return -c / b;
    } else { 
        // (-b +- sqrt(b*b - 4.0*a*c)) / 2.0*a
        float k = -0.5*b/a;
        float q = sqrt(k*k - c/a);
        // pick the closest root right of 0
		return k + ((k <= q)?q:-q);
    }
}

vec2 solve_quadratic2(vec3 fa) {
    float a = fa[2];
    float b = fa[1];
    float c = fa[0];

    // the quadratic solve doesn't work for a=0
    // so we need a branch here.
    if (a == 0.0) {
        return vec2(-c / b);
    } else { 
        // (-b +- sqrt(b*b - 4.0*a*c)) / 2.0*a
        float k = 0.5*b/a;
        float q = sqrt(k*k - c/a);
		return vec2(-k, q);
    }
}

float solve_quadratic(poly2 f) {
    return solve_quadratic0(f.a);
}

// returns the x position of the next root, where f(x) = 0
float nextroot(poly2 f) {
    return solve_quadratic(f);
}

// returns the position of the next event (root or start of new segment)
float nextevent(poly2 f) {
    float s = nextroot(f);
    float h = (f.h <= 0.0)?infinity:f.h;
    s = (s <= 0.0)?h:min(s,h);
    return s;
}

float axis(poly2 f) {
    return nextevent(f);
}

float pa_sign(poly2 f) {
    return ((f.a[0] < 0.0)?-1.0:1.0);
}

// signed pow2; this is for transforming linear distance functions
// to quadratic ones so they compare correctly against spherical functions
// the position of the root is not altered
poly2 pa_spow2(poly2 f) {
    return pa_mul(pa_pow2(f), pa_sign(f));
}

poly2 pa_abs(poly2 f) {
    float s = pa_sign(f);

    f.h = axis(f);    

    return poly2(f.a * s, f.h);
}

poly2 pa_const(float c) {
    return poly2(vec3(c,0.0,0.0), infinity);
}

poly2 pa_ipol(vec2 a, vec2 b) {
    float a1 = (a.y - b.y)/(a.x - b.x);
	float a0 = a.y - a1*a.x;
    return poly2(vec3(a0, a1, 0.0), infinity);
}

poly2 pa_ipol(vec2 a, vec2 b, float k) {
    float a2 = 0.5*k;
    float aa2 = a2*a.x*a.x;
    float a1 = (a.y - b.y + a2*b.x*b.x - aa2) / (a.x - b.x);
    float a0 = a.y - a1*a.x - aa2;
    return poly2(vec3(a0, a1, a2), infinity);
}

poly2 pa_min(poly2 f, poly2 g) {
    float h = axis(pa_sub(f,g));
    float fx = f.a[0];
    float gx = g.a[0];
    return poly2((fx < gx)?f.a:g.a, h);
}

poly2 pa_max(poly2 f, poly2 g) {
    float h = axis(pa_sub(f,g));
    float fx = f.a[0];
    float gx = g.a[0];
    return poly2((fx > gx)?f.a:g.a, h);

}

// the output can only be subject to more comparisons, but must not
// be transformed further.
poly2 pa_hardmin(poly2 f, poly2 g) {
#ifdef USE_HARDMIN
    float fe = nextevent(f);
    float ge = nextevent(g);    
    float h = axis(pa_sub(f,g));
    float fx = f.a[0];
    float gx = g.a[0];
    bool fi = (fx < 0.0);
    bool gi = (gx < 0.0);
    // both outside
    if (!fi && !gi) {
        if (fe <= ge)
            return f;
        else
            return g;
    // both inside
    } else if (fi && gi) {
        if (fe <= ge)
            return f;
        else
            return g;
    } else {
        return poly2((fx < gx)?f.a:g.a, h);
    }    
#else
    float h = axis(pa_sub(f,g));
    float fx = f.a[0];
    float gx = g.a[0];
    return poly2((fx < gx)?f.a:g.a, h);
#endif
}

// the output can only be subject to more comparisons, but must not
// be transformed further.
poly2 pa_hardmax(poly2 f, poly2 g) {
    return pa_unm(pa_hardmin(pa_unm(f),pa_unm(g)));
}

// can only be used once on flat surfaces reliably
// appears to still work with more complex functions?
poly2 pa_smin(poly2 a, poly2 b, float r) {
    poly2 e = pa_min(
        pa_max(
            pa_add(
                pa_unm(
                    pa_abs(
                        pa_sub(a, b))), r),
            pa_const(0.0)),pa_const(r));
    return pa_sub(pa_min(a, b), pa_mul(pa_pow2(e), 0.25 / r));
}

poly2 pa_smax(poly2 a, poly2 b, float r) {
    return pa_unm(pa_smin(pa_unm(a),pa_unm(b),r));
}

// approximates blend with a quadratic patch, but
// very buggy. avoid.
poly2 pa_smin2(poly2 a, poly2 b, float r) {
    float h = solve_quadratic0(pa_sub(a, b).a);
    float x0 = h - r;
    float x1 = h + r;
    if (0.0 < x0) {
        a.h = x0;
        return a;
    } else if (0.0 >= x1) {
        return b;
    } else {
        vec3 ay0 = pa_f(a.a, x0);
        vec3 by0 = pa_f(b.a, x0);
        vec3 ay1 = pa_f(a.a, x1);
        vec3 by1 = pa_f(b.a, x1);
        vec3 y0 = (ay0.x < by0.x)?ay0:by0;
        vec3 y1 = (ay1.x < by1.x)?ay1:by1;
        poly2 m = pa_ipol(vec2(x0, y0.x), vec2(x1, y1.x), (y1.y - y0.y) / (x1 - x0));
        m.h = x1;
    	return m;
    }
}

vec3 ro;
vec3 rd;

poly2 sphere(poly2 x, poly2 y, poly2 z, float r) {
    return pa_sub(pa_add(pa_add(pa_pow2(x),pa_pow2(y)),pa_pow2(z)), r*r);
}

poly2 ellipsoid(poly2 x, poly2 y, poly2 z, vec3 r) {
    poly2 ex = pa_pow2(pa_mul(x, 1.0/r.x));
    poly2 ey = pa_pow2(pa_mul(y, 1.0/r.y));
    poly2 ez = pa_pow2(pa_mul(z, 1.0/r.z));
    return pa_sub(pa_add(pa_add(ex,ey),ez), 1.0);
}

poly2 cube(poly2 x, poly2 y, poly2 z, float r) {
#if 1
	return pa_sub(pa_max(pa_max(pa_abs(x),pa_abs(y)),pa_abs(z)),r);
#else
    poly2 d = pa_max(pa_max(pa_abs(x),pa_abs(y)),pa_abs(z));
    return pa_sub(pa_spow2(d),r * r);
#endif
}

poly2 plane(poly2 x, poly2 y, poly2 z, vec4 n) {
