#define HASHSCALE3 vec3(443.897, 441.423, 437.195)
///  2 out, 2 in...
vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * HASHSCALE3);
    p3 += dot(p3, p3.yzx+19.19);
    return fract((p3.xx+p3.yz)*p3.zy);

}

#define NUM_STEPS_H 16
#define NUM_STEPS_V 16
void paint() {
    setup();
    set_source_rgb(vec3(1.0));
    clear();
    translate(-0.5,0.0);

    // draw 1D graph of estimated distance to horizon
    set_line_width_px(1.0);
    set_source_rgba(0.0,0.0,0.0,1.0);

    vec2 st = 1.0/vec2(NUM_STEPS_V, NUM_STEPS_H);
    for (int k = 0; k < NUM_STEPS_V; ++k) {
        for (int i = 0; i < NUM_STEPS_H; ++i) {
	        float s = float(k) * st.x;
            float t = float(i) * st.y;
			vec2 h = hash22(get_origin() + vec2(s,t) + mod(iTime,1.0)) * st;
            prepare_ray(s + h.x,t + h.y);
            graph1D(rayf);
            stroke();        
        }
    }
#if 1
    set_source_rgb(vec3(0.0,1.0,0.0));
    prepare_ray(0.0,0.0);
    graph1D(rayf);
    prepare_ray(1.0,0.0);
    graph1D(rayf);
    prepare_ray(1.0,1.0);
    graph1D(rayf);
    prepare_ray(0.0,1.0);
    graph1D(rayf);
    stroke();    
#endif

#if 0
    set_source_rgb(vec3(1.0,0.0,0.0));
    graph1D(coeff_f);
    stroke();
#endif

    set_source_rgba(0.0,0.0,0.0,0.3);
    move_to(0.0,0.0);
    line_to(1.0,0.0);
    move_to(0.0,-1.0);
    line_to(0.0,1.0);
    move_to(1.0,-1.0);
    line_to(1.0,1.0);
    stroke();

}

