const float zoomSpeed			= -3.0;	// how fast to zoom (negative to zoom out)
const float zoomScale			= 1.0;	// how much to multiply overall zoom (closer to zero zooms in)
const int recursionCount		= 8;	// how deep to recurse
const float curvature			= 0.3;	// time warp to add curvature

//////////////////////////////////////////////////////////////////////////////////
// Precached values and math

const float e = 2.718281828459;
const float pi = 3.14159265359;
float RandFloat(int i) { return (fract(sin(float(i)) * 43758.5453)); }
int RandInt(int i) { return int(100000.0*RandFloat(i)); }

vec4 HsvToRgb(vec4 c) 
{
    float s = c.y * c.z;
    float s_n = c.z - s * .5;
    return vec4(s_n) + vec4(s) * cos(2.0 * pi * (c.x + vec4(1.0, 0.6666, .3333, 0.0)));
}

//////////////////////////////////////////////////////////////////////////////////
// Color and image manipulation

float GetRecursionFade(int r, float timePercent)
{
    if (r > recursionCount)
        return timePercent;

    // fade in and out recusion
    float rt = max(float(r) - timePercent, 0.0);
    float rc = float(recursionCount);
    return rt / rc;
}

vec4 InitPixelColor() { return vec4(0); }
vec4 CombinePixelColor(vec4 color, float timePercent, int i, int r)
{
    vec4 myColor = vec4
    (
    	mix(-0.1, 0.1, RandFloat(i + r)),
    	mix(0.0, 0.8, RandFloat(i + r + 100)),
        mix(0.0, 0.8, RandFloat(i + r + 200)),
        0.0
    );

    // combine with my color
    float f = GetRecursionFade(r, timePercent);
    myColor.y = pow(myColor.y, 4.0);
    myColor.z = pow(myColor.z, 4.0);
    color += myColor*f;
    return color;
}

vec4 FinishPixel(vec4 color, vec2 uv)
{
    // color wander
    color.x += 0.02*iTime;

    // convert to rgb
    return HsvToRgb(color);
}

vec2 InitUV(vec2 uv)
{
	// wave
	uv.x += 0.05*sin(10.0*uv.y + 0.17*iTime);
	uv.y += 0.05*sin(10.0*uv.x + 0.13*iTime);
	uv.x += 0.8*sin(2.0*uv.y + 0.31*iTime);
	uv.y += 0.8*sin(2.0*uv.x + 0.27*iTime);

    // spin
    float theta = 0.05*iTime;
    float c = cos(theta);
    float s = sin(theta);
    uv *= mat2(-s, c, c, s);

    return uv * 20.0;
}

//////////////////////////////////////////////////////////////////////////////////

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	// use square aspect ratio
	vec2 uv = fragCoord;
	uv = fragCoord / iResolution.y;
	uv -= vec2(0.5*iResolution.x / iResolution.y, 0.5);
    uv = InitUV(uv);

	// get time 
    float time = iTime + curvature*pow(abs(uv.x), 1.0);
	float timePercent = time*zoomSpeed;
	int iterations = int(floor(timePercent));
	timePercent -= float(iterations);

	// update zoom, apply pow to make rate constant
	float zoom = pow(e, -timePercent);
	zoom *= zoomScale;

	// generate recursive colors
	fragColor = InitPixelColor();
	for (int r = 0; r <= recursionCount + 1; ++r)
        fragColor = CombinePixelColor(fragColor, timePercent, iterations, r);
    fragColor = FinishPixel(fragColor, uv);
}

#define N 4


float lagrange(int j, float x, vec2 p[N])
{
	float r = 1.0;
	float xi, xj = p[j].x;

	for(int i=0; i<N; i++)
	{
		if(i != j)
		{
			xi = p[i].x;
			r *= (x - xi) / (xj - xi);
		}
	}

	return r;
}

float g(float x, vec2 p[N])
{
	float y = 0.0;
	for(int j=0; j<N; j++) y += lagrange(j, x, p) * p[j].y;

	return y;
}


vec3 drawPoint(vec3 col, vec2 pos, vec2 fragCoord)
{
	float c = distance(fragCoord, pos);
	col += vec3(1.0,0.0,0.0)*exp(-10.0*c*c);
	return col;
}

vec3 drawCurve(vec3 col, float y, vec2 fragCoord, vec2 p[N])
{
	vec3 curveColour = vec3(0.0,1.0,0.0);
	if(fragCoord.x < p[0].x || fragCoord.x > p[N-1].x)
		curveColour = vec3(0.0,0.5,1.0);
	col += (smoothstep(y - 0.2, y, fragCoord.y)
          - smoothstep(y, y + 0.2, fragCoord.y)) * curveColour;
	return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = -1.0 + 2.0*fragCoord.xy / iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;
    uv *= 15.0; // zoom

    vec2 points[N] = vec2[N](
        vec2(-8.0, 3.0+2.0*sin(iTime*2.0)),
        vec2(-1.0, 2.0+6.0*sin(iTime*3.0)),
        vec2(3.0, -1.0+4.0*sin(iTime*1.0)),
        vec2(8.0, -1.0+4.0*sin(iTime*1.0))
    );

	vec3 col = vec3(0.0);
	for(int i=0; i<N; i++)
        col = drawPoint(col, points[i], uv);
	col = drawCurve(col, g(uv.x, points), uv, points);

	fragColor = vec4(col, 1.0);
}

#define MAX_ITER 1000.

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

const vec2 offset = vec2(0.3019, 0.4859);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2. * fragCoord.xy - iResolution.xy) / iResolution.y;  // [-1; 1]

    vec2 c = uv / pow(1. + iTime / 3., 1. + iTime / 3.) + offset;

    float iter = 0.;

    vec2 z;

    while (length(z) < 4. && iter < MAX_ITER) {
        z = vec2(z.x * z.x - z.y * z.y, 2. * z.x * z.y) + c;
        iter++;
    }

    if (iter < MAX_ITER) {
        iter += 1. - log(log(length(z)) / log(2.)) / log(2.); // smoothing
        float hue = sqrt(iter / MAX_ITER);
   		fragColor = vec4(hsv2rgb(vec3(hue, 1., 1.)), 1.0);
    }
    // else black
}

// Created by Gabor Nagy (gabor.nagy@me.com)
// September 23, 2017
//
// Op Art Heart

float sqr(float x) {
    return x*x;
}

float heart(vec2 p)
{
    float n = sqr(p.x) + sqr(5.*(p.y+.25)/3.8 - sqrt(abs(p.x))) - 1.;
    return 0. - n;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;
    vec3 r = iResolution;

    vec2 uv = (2. * fragCoord.xy - r.xy) / r.y;
    vec2 uv_sin = vec2(uv.x + .33 * sin(uv.y * 5.), uv.y);

    // Rotate
    uv_sin *= mat2(cos(t), sin(t), -sin(t), cos(t));

    // Stripes
    float color = smoothstep(.5, .5 + 50./r.y, .5 + .5 * sin(uv_sin.x * 30.));

    // Scale heart
    uv *= (2. - pow(sin(t * 4.),8.) * .6);  

    // Invert color inside heart
    color = mix(color, 1.-color, smoothstep(-0.01, 0.01, heart(uv)));

    fragColor = vec4(vec3(color), 1.);
}

const float PI = 3.14159;

float sin01(float x){
	return sin(x)*0.5+0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float x = uv.x-0.5;
    float y = uv.y-0.5;

    float d = sqrt(x*x+y*y);

    vec3 color = vec3(0);

    color = vec3(sin01(d*PI*100.));
    vec3 color2 = vec3(sin01(d*PI*100. * sin01(iTime*0.1) ));
    color += color2;

	fragColor = vec4(color, 1);
}

