// Original DE from Knighty's Fragmentarium frag.
// Adapted by Syntopia

// Click in the six different zones, to display
// the six regular polytopes in 4D.
//
// Move up/down in a zone to rotate in 4D.
//
// For more info:
// http://blog.hvidtfeldts.net/index.php/2012/02/distance-estimated-polychora/
//
// Knighty's original thread on FractalForums, where the code was posted:
// http://www.fractalforums.com/general-discussion-b77/solids-many-many-solids/

/*
Type,U,V,W,T
3,0,1,0,0 - 5-cell (hypertetrahedron)
4,0,1,0,0 - 8-cell (hypercube or Tesseract)
4,0,0,1,0 - 24-cell (hyperoctahedron)
4,0,0,0,1 - 16-cell  (no 3D equvivalent)
5,0,1,0,0 - 120-cell (hyperdodecahedron)
5,0,0,0,1 - 600-cell (hypericosahedron)
*/

#define MaxSteps 40
#define MinimumDistance 0.05
#define normalDistance     0.002

#define PI 3.141592
#define Scale 3.0
#define FieldOfView 0.5
#define Jitter 0.6
#define FudgeFactor 1.0

#define Ambient 0.32184
#define Diffuse 0.5
#define LightDir vec3(1.0)
#define LightColor vec3(0.6,1.0,0.158824)
#define LightDir2 vec3(1.0,-1.0,1.0)
#define LightColor2 vec3(1.0,0.933333,1.0)
#define Offset vec3(0.92858,0.92858,0.32858)

// Return rotation matrix for rotating around vector v by angle
mat3  rotationMatrix3(vec3 v, float angle)
{
	float c = cos(radians(angle));
	float s = sin(radians(angle));

	return mat3(c + (1.0 - c) * v.x * v.x, (1.0 - c) * v.x * v.y - s * v.z, (1.0 - c) * v.x * v.z + s * v.y,
		(1.0 - c) * v.x * v.y + s * v.z, c + (1.0 - c) * v.y * v.y, (1.0 - c) * v.y * v.z - s * v.x,
		(1.0 - c) * v.x * v.z - s * v.y, (1.0 - c) * v.y * v.z + s * v.x, c + (1.0 - c) * v.z * v.z
		);
}

vec2 rotate(vec2 v, float a) {
	return vec2(cos(a)*v.x + sin(a)*v.y, -sin(a)*v.x + cos(a)*v.y);
}

// Polychora parameters
int Type = 5;


float VRadius = 0.05048;
float SRadius = 0.05476;

mat3 rot;
vec4 nc,nd,p;
float zone = 0.0;

void init(inout float U,inout float V,inout float W,inout float T){
	vec2 UV = iMouse.xy/iResolution.xy;
	float aa = 0.0;

	// Different zone presets
	if (iMouse.z>0.0) {
		if (UV.x < 1./6.) {
			Type = 3;U = 0.; V = 1.; W = 0.; T = 0.;
			zone = 1.0;
		} else if (UV.x < 2./6.) {
			Type = 4;U = 0.; V = 1.; W = 0.; T = 0.;
			zone = 2.0;
		} else if (UV.x < 3./6.) {
			Type = 4;U = 0.; V = 0.; W = 1.; T = 0.;
			zone = 3.0;
		} else if (UV.x < 4./6.) {
			Type = 4;U = 0.; V = 0.; W = 0.; T = 1.;
			zone = 4.0;
		} else if (UV.x < 5./6.) {
			Type = 5;U = 0.; V = 1.; W = 0.; T = 0.;
			zone = 5.0;
		} else  {
			Type = 5;U = 0.; V = 0.; W = 0.; T = 1.;
			zone = 6.0;
		}  
		aa = UV.y*90.;
	}
	float cospin=cos(PI/float(Type)), isinpin=1./sin(PI/float(Type));
	float scospin=sqrt(2./3.-cospin*cospin), issinpin=1./sqrt(3.-4.*cospin*cospin);

	nc=0.5*vec4(0,-1,sqrt(3.),0.);
	nd=vec4(-cospin,-0.5,-0.5/sqrt(3.),scospin);

	vec4 pabc,pbdc,pcda,pdba;
	pabc=vec4(0.,0.,0.,1.);
	pbdc=0.5*sqrt(3.)*vec4(scospin,0.,0.,cospin);
	pcda=isinpin*vec4(0.,0.5*sqrt(3.)*scospin,0.5*scospin,1./sqrt(3.));
	pdba=issinpin*vec4(0.,0.,2.*scospin,1./sqrt(3.));

	p=normalize(U*pabc+V*pbdc+W*pcda+T*pdba);

	vec3 RotVector = vec3(0.0,1.0,0.1);
	rot = rotationMatrix3(normalize(RotVector), aa);//in reality we need a 4D rotation
}

vec4 fold(vec4 pos) {
	if (Type == 3) {
		for(int i=0;i<3;i++){
			pos.xy=abs(pos.xy);
			float t=-2.*min(0.,dot(pos,nc)); pos+=t*nc;
			t=-2.*min(0.,dot(pos,nd)); pos+=t*nd;
		}
	} else if (Type == 4) {
		for(int i=0;i<8;i++){
			pos.xy=abs(pos.xy);
			float t=-2.*min(0.,dot(pos,nc)); pos+=t*nc;
			t=-2.*min(0.,dot(pos,nd)); pos+=t*nd;
		}
	} else if (Type == 5) {
		for(int i=0;i<15;i++){
			pos.xy=abs(pos.xy);
			float t=-2.*min(0.,dot(pos,nc)); pos+=t*nc;
			t=-2.*min(0.,dot(pos,nd)); pos+=t*nd;
		}
	}
	return pos;
}

float DD(float ca, float sa, float r){
	//magic formula to convert from spherical distance to planar distance.
	//involves transforming from 3-plane to 3-sphere, getting the distance
	//on the sphere then going back to the 3-plane.
	return r-(2.*r*ca-(1.-r*r)*sa)/((1.-r*r)*ca+2.*r*sa+1.+r*r);
}

float dist2Vertex(vec4 z, float r){
	float ca=dot(z,p), sa=0.5*length(p-z)*length(p+z);//sqrt(1.-ca*ca);//
	return DD(ca,sa,r)-VRadius;
}

float dist2Segment(vec4 z, vec4 n, float r){
	//pmin is the orthogonal projection of z onto the plane defined by p and n
	//then pmin is projected onto the unit sphere
	float zn=dot(z,n),zp=dot(z,p),np=dot(n,p);
	float alpha=zp-zn*np, beta=zn-zp*np;
	vec4 pmin=normalize(alpha*p+min(0.,beta)*n);
	//ca and sa are the cosine and sine of the angle between z and pmin. This is the spherical distance.
	float ca=dot(z,pmin), sa=0.5*length(pmin-z)*length(pmin+z);//sqrt(1.-ca*ca);//
	return DD(ca,sa,r)-SRadius;
}

//it is possible to compute the distance to a face just as for segments: pmin will be the orthogonal projection
// of z onto the 3-plane defined by p and two n's (na and nb, na and nc, na and and, nb and nd... and so on).
//that involves solving a system of 3 linear equations.
//it's not implemented here because it is better with transparency

float dist2Segments(vec4 z, float r){
	float da=dist2Segment(z, vec4(1.,0.,0.,0.), r);
	float db=dist2Segment(z, vec4(0.,1.,0.,0.), r);
	float dc=dist2Segment(z, nc, r);
	float dd=dist2Segment(z, nd, r);

	return min(min(da,db),min(dc,dd));
}

float DE(vec3 pos) {
	//return length(pos)-1.;
	float r=length(pos);
	vec4 z4=vec4(2.*pos,1.-r*r)*1./(1.+r*r);//Inverse stereographic projection of pos: z4 lies onto the unit 3-sphere centered at 0.
	z4.xyw=rot*z4.xyw;
	z4=fold(z4);//fold it

	return min(dist2Vertex(z4,r),dist2Segments(z4, r));
}

vec3 lightDir;
vec3 lightDir2;

// Backgorund
vec3 bg(vec3 dir) {
	if (dir.z>0.0) {
		float sun =clamp(dot(dir, normalize(vec3(0.2,0.2,0.5))),0.0,1.0);
		vec3 sky = vec3(0.6,0.4,1.0)*(1.0-dir.z*dir.z*0.4);
		sky += 1.3*pow(sun,30.0)*vec3(1.0,0.8,0.8);
		return sky;
	} else {
		vec3 ground =  vec3(0.6,0.4,0.4)*(pow(abs(dir.z),0.1)*1.0);
		return ground;
	}
}


// Lighting. Adds some environment reflections
vec3 getLight(in vec3 color, in vec3 normal, in vec3 dir) {
	float diffuse = 0.3*max(0.0,dot(-normal, lightDir)); // Lambertian
	float diffuse2 =0.3* max(0.0,dot(-normal, lightDir2)); // Lambertian
	vec3 r = reflect(normal,dir);
	return
	(diffuse*Diffuse)*(LightColor*color) +
	(diffuse2*Diffuse)*(LightColor2*color) +0.5*bg(r);
}


// Finite difference normal
vec3 getNormal(in vec3 pos) {
	vec3 e = vec3(0.0,normalDistance,0.0);

	return normalize(vec3(
			DE(pos+e.yxx)-DE(pos-e.yxx),
			DE(pos+e.xyx)-DE(pos-e.xyx),
			DE(pos+e.xxy)-DE(pos-e.xxy)
			)
		);
}

// Pseudo-random number
// From: lumina.sourceforge.net/Tutorials/Noise.html
float rand(vec2 co){
	return fract(cos(dot(co,vec2(4.898,7.23))) * 23421.631);
}

// Solid color 
vec3 getColor(vec3 normal, vec3 pos) {
	return vec3(1.0);
}

// Returns (r,theta [0..pi],phi [-pi,pi])
vec3 cartesianToSpherical(vec3 p) {
	float r = length(p);
	float theta = acos(p.z/r);
	float phi = atan(p.y,p.x);
	return vec3(r,theta,phi);
}


vec4 rayMarch(in vec3 from, in vec3 dir, in vec2 fragCoord) {
	// Add some noise to prevent banding
	float totalDistance = Jitter*rand(fragCoord.xy+vec2(iTime));
	vec3 dir2 = dir;
	float distance;
	int steps = 0;
	vec3 pos;
	for (int i=0; i <= MaxSteps; i++) {
		pos = from + totalDistance * dir;
		distance = DE(pos)*FudgeFactor;

		totalDistance += distance;
		if (distance < MinimumDistance) break;
		steps = i;
	}

	// 'AO' is based on number of steps.
	float ao = 1.0-float(steps)/float(MaxSteps);

	// Since our distance field is not signed,
	// backstep when calc'ing normal
	vec3 normal = getNormal(pos-dir*normalDistance*3.0);
	vec3 b = bg(dir);
	b = mix(b, vec3(0.0,0.0,0.0), 0.2);
	if (steps == MaxSteps) {
		return vec4(b,1.0);
	}
	vec3 color = getColor(normal, pos);
	vec3 light = getLight(color, normal, dir);
	color =(color*Ambient+light)*(ao);
	return vec4(color,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    float U = 0.0*cos(iTime)*0.5+0.1;
float V =  0.2*sin(iTime*0.1)*0.5+0.2;
float W =  1.0*cos(iTime*1.2)*0.5+0.5;
float T =  0.01;
	init(U,V,W,T);

	// Camera 
	vec3 camPos = 0.4*(12.0+2.0*sin(iTime*0.6))*vec3(cos(iTime*0.3),sin(iTime*0.3),-1.6);
	vec3 target = vec3(0.0,0.0,0.0);
	vec3 camUp  = vec3(0.0,0.0,1.0);

	// Calculate orthonormal camera reference system
	vec3 camDir   = normalize(target-camPos); // direction for center ray
	camUp = normalize(camUp-dot(camDir,camUp)*camDir); // orthogonalize
	vec3 camRight = normalize(cross(camDir,camUp));

	lightDir= -normalize(camPos+7.5*camUp);
	lightDir2=-normalize( camPos- 6.5*camRight);

	vec2 coord =-1.0+2.0*fragCoord.xy/iResolution.xy;
	float vignette = 0.2*pow(dot(coord,coord),1.0);
	coord.x *= iResolution.x/iResolution.y;

	// Get direction for this pixel
	vec3 rayDir = normalize(camDir + (coord.x*camRight + coord.y*camUp)*FieldOfView);

	vec3 col = rayMarch(camPos, rayDir, fragCoord).xyz;   
    col = clamp(col, 0.0,1.0);
    col *=(1.0-vignette);

	// Marker at the bottom to indicate zone
	float pos =6.0*fragCoord.x/iResolution.x;
	if (pos<zone && pos>zone-1.0 && coord.y<-0.8) {
		col = col.xxx;
	}
    col=col.yzx;//color swivel to distinguish from older version

	fragColor = vec4(col,1.0);
}

float _OutlineWidth = 0.02;
float _LineWidth = 0.08;
vec4 _OutlineColor = vec4(0.,0.,0.,0.);
vec4 _FrontColor = vec4(1.0,1.0,1.0,1.0);

//======================
// Line functions
//======================
float line(vec2 pos, vec2 point1, vec2 point2, float width) {
    vec2 dir0 = point2 - point1;
    vec2 dir1 = pos - point1;
    float h = clamp(dot(dir0, dir1)/dot(dir0, dir0), 0.0, 1.0);
    float d = (length(dir1 - dir0 * h) - width * 0.5);
    return d;
}

vec4 line_with_color(vec2 pos, vec2 point1, vec2 point2, float width) {   		
    float d = line(pos, point1, point2, width);
    float w = fwidth(0.5*d) * 2.0;
    vec4 layer0 = vec4(_OutlineColor.rgb, 1.-smoothstep(-w, w, d - _OutlineWidth));
    vec4 layer1 = vec4(_FrontColor.rgb, 1.-smoothstep(-w, w, d));

    return mix(layer0, layer1, layer1.a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 2.0*( fragCoord.xy / iResolution.xy) -1.0;


	float color=1.0;
	float rg=0.0;
	float an=iTime*50.0;
	float dan=iTime;
	vec2 oldp=vec2(0.0,0.0);

	for(int i=0;i<64;i++)
	{
//		vec2 p1=vec2(0.0,0.0);
		vec2 p2=vec2(rg*cos(an*3.141/180.0),rg*sin(an*3.141/180.0));
		vec4 linea=line_with_color(uv,oldp,p2,0.005);

		fragColor = mix(fragColor,linea,linea.a);

		an+=dan;
		rg+=0.02;
		oldp=p2;
	}    

}

// Author: ocb
// Title: Voxels City at Night


#define PI 3.141592653589793
#define PIdiv2 1.57079632679489
#define TwoPI 6.283185307179586
#define INFINI 1000000.

#define NO_TRANSPARENCY
#define BORDER

#define boxDim 100.
#define maxBoxRnge 170
#define maxReflRnge 70
#define emptyChance 30.
#define buildSize 5.

// object name
#define GND -1
#define SKY -1000
#define NONE 0
#define BOX 1

//Global var
vec3 color = vec3(0.);
vec3 lightRay = normalize(vec3(1.,.1,.2));

float H1 (in float v) { 						
    return fract(sin(v) * 437585.);
}
float H2 (in vec2 st,in float time) { 						
    return fract(sin(dot(st,vec2(12.9898,8.233))) * 43758.5453123+time);
}
float H3 (in vec3 st,in float time) { 						
    return fract(sin(dot(st,vec3(12.9898,8.233,17.6533107))) * 43758.5453123+time);
}

float sfcImpact(in float p, in float ray, in float h){
    float t = (h-p)/ray;
    if (t <= 0.001) t = INFINI;
    return t;
}

vec3 skyGlow(in vec3 ray){
    float a = dot(lightRay, ray);
    return vec3(smoothstep(.98,1.,a));
}

vec3 setBox(in vec3 p){
    return floor(p/boxDim + .5);
}

vec3 deBox(in vec3 box){
    return box*boxDim;		// return the center of the box
}
vec3 getNextBox(in vec3 p, in vec3 v, in vec3 box){
    vec3 d = sign(v);
	vec3 dt = ((box+d*.5)*boxDim-p)/v;
    float dmin = min(min(dt.x,dt.y),dt.z);
    d *= step(dt, vec3(dmin));
    return box+d;
}

bool checkBox(in vec3 box){
    float h = H3(floor(box),0.)*(emptyChance + 2.);
    return bool(int(floor( max(0.,h-emptyChance) )));
}

bool checkBox2(in vec3 box){
    float h = H2(floor(box.xz/buildSize),0.);
    return bool(step(min(31.,h*float(maxBoxRnge)), -box.y+20.));
}

vec4 browseBox(in vec3 box, in vec3 pos, in vec3 ray, inout int hitObj){
    float t = INFINI, tt = INFINI;
    float hitNbr = 0.;
    vec3 d;
    for(int i=0; i<maxBoxRnge;i++){
    	vec3 newBox = getNextBox(pos,ray,box);
        d = box - newBox;

        if(checkBox2(newBox)) {
            hitNbr ++;
            color += .4/hitNbr*((dot(d,lightRay)+1.)*.3+.4)*vec3(0.08,H2(floor(newBox.xz/buildSize)*2.77665,0.)/8.+.1,0.3);
            vec3 o = deBox(newBox+.5*d);

            if(abs(d.x) == 1.){
                tt = (o.x-pos.x)/ray.x;
                #ifdef BORDER
                vec3 p = pos+tt*ray;
                color.b += .1*(smoothstep(.4*boxDim, .5*boxDim ,abs(o.y-p.y)) + smoothstep(.4*boxDim, .5*boxDim ,abs(o.z-p.z)));
                #endif
            }
            else if(abs(d.y) == 1.){
                tt = (o.y-pos.y)/ray.y;
                #ifdef BORDER
                vec3 p = pos+tt*ray;
                color.b += .1*(smoothstep(.4*boxDim, .5*boxDim ,abs(o.x-p.x)) + smoothstep(.4*boxDim, .5*boxDim ,abs(o.z-p.z)));
                #endif
            }
            else{
                tt = (o.z-pos.z)/ray.z;
                #ifdef BORDER
                vec3 p = pos+tt*ray;
                color.b += .1*(smoothstep(.4*boxDim, .5*boxDim ,abs(o.x-p.x)) + smoothstep(.4*boxDim, .5*boxDim ,abs(o.y-p.y)));
                #endif
            }


            #ifdef NO_TRANSPARENCY
            if(bool(mod(newBox.y, 2.))){
                hitObj = BOX;
                t = tt;
                break;
            }
            else{
                vec3 refl = reflect(ray,d);
                color += .4/hitNbr*skyGlow(refl);
            }
            #endif
        }
        box = newBox;
    }
    color = clamp(color,0.,1.);
    return vec4(d,t);
}

bool browseBoxSimple(in vec3 box, in vec3 pos, in vec3 ray){
    bool ret = false;
    for(int i=0; i<maxReflRnge;i++){
    	box = getNextBox(pos,ray,box);
        if(checkBox2(box) && bool(mod(box.y, 2.)) ){
            ret = true;
            break;
        }
    }
    return ret;
}

vec3 getCamPos(in vec3 camTarget){
    float 	rau = 100.,//*(sin(u_time/7.)+1.) + 50.,
    		//rau = 1.,
    		alpha = iMouse.x/iResolution.x*4.*PI,
    		theta = iMouse.y/iResolution.y*PI+(PI/2.0001);
    		if (iMouse.xy == vec2(0.)){
                alpha = PIdiv2;
                theta = 0.;
            }

    return rau*vec3(-cos(theta)*sin(alpha),sin(theta),cos(theta)*cos(alpha))+camTarget;
}

vec3 getRay(in vec2 st, in vec3 pos, in vec3 camTarget){
    float 	focal = 2.;
    vec3 ww = normalize( camTarget - pos );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0)) ) ;
    vec3 vv = cross(uu,ww);
	// create view ray
	return normalize( st.x*uu + st.y*vv + focal*ww );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 st = fragCoord.xy/iResolution.xy-.5;
    st.x *= iResolution.x/iResolution.y;

    // camera def
    vec3 camTarget = vec3(3000.*sin(iTime*.02),900.*sin(iTime*.05)-100.,2500.*cos(iTime*.03));
    vec3 pos = getCamPos(camTarget);
    vec3 ray = getRay(st, pos,camTarget);

	vec3 lightRay = vec3(1.,0.,0.);	

    float t = INFINI;
    vec3 norm;
    int hitObj = SKY;
    vec3 p = pos;

    vec3 box = setBox(p);

    vec4 info = browseBox(box, p, ray, hitObj);
    if(hitObj == SKY) color += skyGlow(ray)*min(1.,(1.-.5*texture(iChannel0,ray.yz*.5).x));
    else{
        norm = info.xyz;
        t = info.w;
        p += t*.999*ray;

        box = setBox(p);
        vec3 refl = reflect(ray,norm);
        if(!browseBoxSimple(box, p, refl)){
            if(box.y == -10.) color += .2*skyGlow(refl);
            else color += .5*skyGlow(refl);
        }
    }

    fragColor = vec4(color,1.0);
}

