void rotate(poly2 x, poly2 y, out poly2 rx, out poly2 ry, float a) {
    float c = cos(a);
    float s = sin(a);
    rx = pa_sub(pa_mul(x,c),pa_mul(y,s));
    ry = pa_add(pa_mul(x,s),pa_mul(y,c));
}

