            {
                d = distance(vec2(m1,m2),vec2(floor(m1)+.5,floor(m2)+.5))*2.;
                cs = max ( cs, z * min(1.,pow(1.5-d,15.)) );
                cs = max ( cs, z * max(0.,1.-d) );
