#define MID_POINT 0.5
#define RANGE_OF_TRIG_FUNC 0.5
#define TWO_PI 6.283185307179586476925286766559
// in hertz (per sec)
#define FREQUENCY 0.1

#define BLENDING_LOOP_IN_SECS 10.0
#define PRIMARY_COLOR_LOOP_IN_SECS 6.0

#define SUBDIVISION_LOOP_IN_SECS 8.0
#define NO_OF_DIVISIONS 8

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec3 col = vec3(uv,0.5+0.5*sin(iTime));   


    // SEPARATOR FROM https://www.shadertoy.com/view/4slSWf
    float sep = iResolution.x * smoothstep( 0.5, 0.8, cos(1.57+0.25*1.2831*iTime));

    // separator
    col *= smoothstep( 1.0, 2.0, abs(fragCoord.x-sep));		

    if (uv.y < 0.4)       
        // Bottom : default blending using sin plus separator from bender demo 
        // 
		fragColor = vec4( col, 1.0 );
    else if (uv.y < 0.6)
    {
        // Middle: SINE FUNCTION 
        	// SINE starts at mid, goes up to max, down to mid then bottom and up to mid

        fragColor = vec4(MID_POINT + RANGE_OF_TRIG_FUNC * sin( FREQUENCY * TWO_PI * iTime));
    }
    else if (uv.y < 0.8)
    {           
        // 2ND from top: switching color values within array
        float timeStep = mod(iTime, PRIMARY_COLOR_LOOP_IN_SECS) / PRIMARY_COLOR_LOOP_IN_SECS;

        vec3 primaryColors[3] = vec3[](        
        	vec3(1., 0., 0),
        	vec3(0., 1., 0),
        	vec3(0., 0., 1)                
        );       

       	int colorIndex = int(floor(timeStep * 3.0));            

        fragColor = vec4(primaryColors[colorIndex], 1.);               
    }
    else
    {
        // TOP: subdividing sections by value within float array           
        float gapTimeStep = mod(iTime, SUBDIVISION_LOOP_IN_SECS) / SUBDIVISION_LOOP_IN_SECS;


        float sections[NO_OF_DIVISIONS] = float[](
            1.,
        	2.,
        	4.,
        	8.,
            16.,
            32.,
            64.,
            128.
        );                 

        float count = sections[int(gapTimeStep * float(NO_OF_DIVISIONS))]; 

        if (int(uv.x * count) % 2 == 0)
        {
            fragColor = vec4(1., 0., 1., 1.);  
        }
        else
        {
            fragColor = vec4(1., 1., 0., 1.);
        }                

    }    
}

